private container PdsBatchAttribAutoResDefaultingDependencies()
{
    return [fieldNum(SalesParameters, PdsBatchAttribAutoRes),
            [fieldNum(SalesParameters, Reservation)]
    ];
}

public void defaultField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(SalesParameters, PdsBatchAttribAutoRes):
            this.defaultPdsBatchAttribAutoRes();
            break;
    }
}

private void defaultPdsBatchAttribAutoRes()
{
    if (this.isFieldSet(fieldNum(SalesParameters, Reservation)))
    {
        if (this.Reservation != ItemReservation::Automatic && this.PdsBatchAttribAutoRes)
        {
            this.PdsBatchAttribAutoRes = NoYes::No;
        }
    }
}

