/// <summary>
/// Indicates whether a packing slip posting should be split based on site.
/// </summary>
/// <param name="_documentStatus">
/// A document type.
/// </param>
/// <param name="_returnValue">
/// A Boolean value that indicates the default return value; optional.
/// </param>
/// <returns>
/// true if the packing slips should be split based on site; otherwise, false.
/// </returns>
public boolean postSplitSiteBR(DocumentStatus _documentStatus,
                               boolean _returnValue = 0)
{
    if (_documentStatus == DocumentStatus::DeliverySlip_BR)
        return this.SplitSitePackingSlip;

    return _returnValue;
}

public SalesUpdate recommendedSpecQty(DocumentStatus _documentStatus)
{
    SalesUpdate specQty;

    switch (_documentStatus)
    {
        case DocumentStatus::PackingSlip :
            specQty = SalesUpdate::PickingList;
            break;

        case DocumentStatus::Invoice :
            specQty = SalesUpdate::PackingSlip;
            break;

        default:
            specQty = SalesUpdate::All;
            break;
    }
    return specQty;
}

public boolean reduceOnHand(DocumentStatus _documentStatus)
{
    boolean reduceOnHand;

    switch (_documentStatus)
    {
        case DocumentStatus::PickingList, DocumentStatus::ProjectPickingList :
            reduceOnHand = this.ReducePickingList;
            break;

        case DocumentStatus::PackingSlip :
            reduceOnHand = this.ReducePackingSlip;
            break;

        case DocumentStatus::Invoice :
            reduceOnHand = this.ReduceInvoice;
            break;

        default :
            reduceOnHand = false;
            break;
    }
    return reduceOnHand;
}

public boolean splitDeliveryInformation(DocumentStatus _documentStatus)
{
    boolean splitDeliveryInformation;

    switch (_documentStatus)
    {
        case DocumentStatus::Confirmation :
            splitDeliveryInformation = this.SplitDeliveryConfirmation;
            break;

        case DocumentStatus::PickingList :
            splitDeliveryInformation = this.SplitDeliveryPickingList;
            break;

        case DocumentStatus::PackingSlip :
        case DocumentStatus::ProjectPackingSlip :
            splitDeliveryInformation = this.SplitDeliveryPackingSlip;
            break;

        case DocumentStatus::Invoice :
            splitDeliveryInformation = this.SplitDeliveryInvoice;
            break;

        default:
            splitDeliveryInformation = false;
            break;
    }
    return splitDeliveryInformation;
}

public boolean splitSite(DocumentStatus _documentStatus)
{
    boolean splitSite;

    switch (_documentStatus)
    {
        case DocumentStatus::PickingList :
        case DocumentStatus::ProjectPickingList :
            splitSite = this.SplitSitePickingList;
            break;

        case DocumentStatus::PackingSlip :
        case DocumentStatus::ProjectPackingSlip :
            splitSite = this.SplitSitePackingSlip;
            break;

        case DocumentStatus::Invoice :
        case DocumentStatus::ProjectInvoice :
            splitSite = this.SplitSiteInvoice;
            break;

        default:
            splitSite = false;
            break;
    }

    if (BrazilParameters::isEnabled())
    {
        return this.postSplitSiteBR(_documentStatus, splitSite);
    }

    return splitSite;
}

public boolean splitTaxId(DocumentStatus _documentStatus)
{
    boolean splitTaxId = NoYes::No;
    if (TaxIntegrationUtils::isMultipleTaxIdAllowedWithinOneSalesOrder())
    {
        switch (_documentStatus)
        {
            case DocumentStatus::Confirmation:
                splitTaxId = this.SplitTaxIdConfirmation;
                break;

            case DocumentStatus::PickingList:
                splitTaxId = this.SplitTaxIdPickingList;
                break;

            case DocumentStatus::PackingSlip:
            case DocumentStatus::ProjectPackingSlip:
            case DocumentStatus::Invoice:
                // Must be Yes, cannot edit
                splitTaxId = NoYes::Yes;
                break;

            default:
                splitTaxId = false;
                break;
        }
    }
    return splitTaxId;
}

public void update()
{
    CustFormletterParameters custFormletterParameters;
    NoYes wasTransportationDocumentChecked;

    wasTransportationDocumentChecked = this.orig().TransportationDocumentOnPackingSlip;

    super();

    if (!this.TransportationDocumentOnPackingSlip && wasTransportationDocumentChecked)
    {
        ttsbegin;
        custFormletterParameters = CustFormletterParameters::find(true);
        custFormletterParameters.TransportationDocumentOnPackingSlip = NoYes::No;
        custFormletterParameters.update();
        ttscommit;
    }

    this.setD365SalesEnabledFeatures();

    flush SalesParameters;
}

