public boolean useShippingStat(DocumentStatus _documentStatus)
{
    boolean useShippingStat;

    switch (_documentStatus)
    {
        case DocumentStatus::PackingSlip :
        case DocumentStatus::ProjectPackingSlip:
            useShippingStat = this.useShippingStatOnPackingSlip();
            break;

        case DocumentStatus::Invoice :
            useShippingStat = this.useShippingStatOnInvoice();
            break;

        // <GBR>
        case DocumentStatus::DeliverySlip_BR :
        case DocumentStatus::DeliverySlipProject_BR :
            useShippingStat = this.useShippingStatOnPackingSlip();
            break;
        // </GBR>

        default :
            useShippingStat = false;
            break;
    }
    return useShippingStat;
}

public boolean useShippingStatOnInvoice()
