}

public boolean useShippingStatOnPackingSlip()
{
    return this.ShippingStat == SalesShipping::PackingSlip || this.ShippingStat == SalesShipping::Both;
}

public boolean validateField(FieldId _p1)
{
    boolean ret;

    ret = super(_p1);

    switch (_p1)
    {
        case fieldNum(SalesParameters, OrderType):
            if (this.OrderType == SalesType::ItemReq)
            {
                ret = checkFailed("@SYS70032");
            }
            else
            if (this.OrderType == SalesType::ReturnItem)
            {
                // The returned item order type cannot be selected manually.
                ret = checkFailed("@SYS104084");
            }
            break;

        case fieldNum(SalesParameters, QuotationNumOfDaysExpiryDate):
        case fieldNum(SalesParameters, QuotationNumOfDaysFollowUpDate):
            if (this.QuotationNumOfDaysExpiryDate < this.QuotationNumOfDaysFollowUpDate)
            {
                // Expiry days must be greater than Follow-up days.
                ret = checkFailed("@SYS91356");
            }
            break;
        case fieldNum(SalesParameters, mcrQuestionableSalesMargin):
        case fieldNum(SalesParameters, mcrAcceptableSalesMargin):
            if (isConfigurationkeyEnabled(configurationKeyNum(TradeMarginAlert))
                && this.mcrQuestionableSalesMargin > this.mcrAcceptableSalesMargin)
            {
                ret = checkFailed("@MCR36316");
            }
            break;
        case fieldNum(SalesParameters, ATPInclPlannedOrders):
        case fieldNum(SalesParameters, DeliveryDateControlType):
            ReqParameters reqParameters = ReqParameters::find();

            if (!this.isAtpInclPlannedOrdersFieldValidWithPlanningParameters(reqParameters)
             || !this.isDeliveryDateControlTypeFieldValidWithPlanningParameters(reqParameters))
            {
                ret = checkFailed("@SCM:ReqParametersFormWarning");
            }
            break;
        case fieldNum(SalesParameters, MarkupPeriodChargeRuleQtyThresholdMatch):
            if (this.orig().MarkupPeriodChargeRuleQtyThresholdMatch != this.MarkupPeriodChargeRuleQtyThresholdMatch)
            {
                ret = MarkupPeriodChargeInvoiceLineBase::checkMarkupPeriodChargeInvoiceLineBase(fieldId2PName(tableNum(SalesParameters), fieldNum(SalesParameters, MarkupPeriodChargeRuleQtyThresholdMatch)));
            }
            break;
        case fieldNum(SalesParameters, MarkupPeriodChargeRuleSiteLocationMatch):
            if (this.orig().MarkupPeriodChargeRuleSiteLocationMatch != this.MarkupPeriodChargeRuleSiteLocationMatch)
            {
                ret = MarkupPeriodChargeInvoiceLineBase::checkMarkupPeriodChargeInvoiceLineBase(fieldId2PName(tableNum(SalesParameters), fieldNum(SalesParameters, MarkupPeriodChargeRuleSiteLocationMatch)));
            }
            break;
    }
    return ret;
}

/// <summary>
///     Validates the value of the AtpInclPlannedOrders field.
/// </summary>
/// <param name = "_reqParameters">
///     The planning parameters.
/// </param>
/// <returns>
///     true if the value in the field is valid; otherwise, false.
/// </returns>
public boolean isAtpInclPlannedOrdersFieldValidWithPlanningParameters(ReqParameters _reqParameters)
{
    return !(_reqParameters.ReqInactive        &&
             this.ATPInclPlannedOrders == true &&
                 (this.DeliveryDateControlType == SalesDeliveryDateControlType::ATP ||
                  this.DeliveryDateControlType == SalesDeliveryDateControlType::ATPPlusIssueMargin));
}

/// <summary>
///     Validates the value of the DeliveryDateControlType field.
/// </summary>
/// <param name = "_reqParameters">
///     The planning parameters.
/// </param>
/// <returns>
///     true if the value in the field is valid; otherwise, false.
/// </returns>
public boolean isDeliveryDateControlTypeFieldValidWithPlanningParameters(ReqParameters _reqParameters)
{
    return !(_reqParameters.ReqInactive
        &&  (this.DeliveryDateControlType == SalesDeliveryDateControlType::CTP
        ||  (this.DeliveryDateControlType == SalesDeliveryDateControlType::FullRunCTP
        &&   MpsReqInactiveCTPValidationFlight::instance().isEnabled())));
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret
     &&((this.GenericCurrency && !this.ExchangeRateType)
     || (!this.GenericCurrency && this.ExchangeRateType)))
    {
        ret = checkFailed(strFmt("@SYS326645", fieldStr(SalesParameters, GenericCurrency), fieldStr(SalesParameters, ExchangeRateType), "@SYS12983"));
    }
    return ret;
}

public static container  allowedNumSeqGroup()
{
    container res;
    // <GEERU>
    #ISOCountryRegionCodes
    boolean countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>
    //<GTH>
    boolean countryRegion_TH = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoTH]);
    //</GTH>

    res =      [SalesParameters::numRefPackingSlipId                    ().NumberSequenceDatatype,
                SalesParameters::numRefInvoiceId                        ().NumberSequenceDatatype,
                SalesParameters::numRefSalesCreditNoteId                ().NumberSequenceDatatype,
                SalesParameters::numRefSalesPackingSlipVoucher          ().NumberSequenceDatatype,
                SalesParameters::numRefSalesInvoiceVoucher              ().NumberSequenceDatatype,
                SalesParameters::numRefSalesCreditNoteVoucher           ().NumberSequenceDatatype];

    // <GEERU>
    if (countryRegion_RU)
    {
        res += SalesParameters::numRefCustInvoiceId_M15_RU             ().NumberSequenceDatatype;
        res += SalesParameters::numRefPrintoutJournalNum_CustInvM15_RU ().NumberSequenceDatatype;
        res += SalesParameters::numRefSalesCorrectInvoiceId_RU         ().NumberSequenceDatatype;
        res += SalesParameters::numRefSalesCorrectCreditNoteId_RU      ().NumberSequenceDatatype;
        res += SalesParameters::numRefSalesCorrectInvoiceVoucher_RU    ().NumberSequenceDatatype;
        res += SalesParameters::numRefSalesCorrectCreditNoteVoucher_RU ().NumberSequenceDatatype;
        res += SalesParameters::numRefCustCorrectInvoiceId_M15_RU      ().NumberSequenceDatatype;
        res += SalesParameters::numRefPrintoutJourNum_CustCorrInvM15_RU().NumberSequenceDatatype;
    }
    // </GEERU>

    //<GTH>
    if (countryRegion_TH)
    {
        res += SalesParameters::numRefSalesDebitNoteId      ().NumberSequenceDatatype;
        res += SalesParameters::numRefSalesDebitNoteVoucher ().NumberSequenceDatatype;
        res += SalesParameters::numRefSalesTaxInvoiceId     ().NumberSequenceDatatype;
        res += SalesParameters::numRefSalesTaxInvoiceVoucher().NumberSequenceDatatype;
    }
    //</GTH>
    // <GMY>
    else if (TaxGSTFeatureChecker_MY::isCountryRegionMY())
    {
        res += [SalesParameters::numRefSalesDebitNoteId_MY().NumberSequenceDatatype,
                SalesParameters::numRefSalesDebitNoteVoucher_MY().NumberSequenceDatatype];
    }
    // </GMY>

    return res;
}

