public static ItemFreeTxt nameAndOrDescription(InventTable _inventTable, LanguageId _languageId, InventDimId _inventDimId, EcoResCategory _ecoResCategory = null)
{
    ItemFreeTxt name;
    ItemFreeTxt description;

    if (_inventTable)
    {
        name         = _inventTable.productName(_languageId, _inventDimId);
        description  = _inventTable.productDescription(_languageId, _inventDimId);
    }
    else
    {
        EcoResCategoryTranslation categoryTranslation = EcoResCategoryTranslation::find(_ecoResCategory.RecId, _languageId);
        name        = categoryTranslation ? categoryTranslation.FriendlyName : _ecoResCategory.Name;
        description = categoryTranslation ? categoryTranslation.Description : EcoResCategoryTranslation::find(_ecoResCategory.RecId, LanguageTable::defaultLanguage()).Description;
    }

    if (description)
    {
        if (CustFormletterParameters::find().ItemNameAndTxt2Description)
        {
            return name + '\n' + description;
        }

        return Description;
    }


    return name;
}

