/// <summary>
/// Determines the access level for invoiced sales orders.
/// </summary>
/// <param name = "_treatInvoicedOrderUpdateWarningAsError">
/// A boolean value that indicates whether the "Warning" access level to invoiced orders must be treated as "Locked".
/// </param>
/// <returns>
/// The access level for invoiced sales orders.
/// </returns>
public SalesPurchAccesLevel accesLevelInvoiced(boolean _treatInvoicedOrderUpdateWarningAsError)
{
    if (_treatInvoicedOrderUpdateWarningAsError && this.AccesLevelInvoiced == SalesPurchAccesLevel::Warning)
    {
        return SalesPurchAccesLevel::Locked;
    }

    return this.AccesLevelInvoiced;
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return super() || SalesEnforceConcurrentDeletesFlight::instance().isEnabled();
}

private void setD365SalesEnabledFeatures()
{
    TradeD365SalesFeatureState::updateFeatureStates(this);
}

internal boolean isD365SalesQuotationLifecycleAndProspectIntegrationEnabled()
{
    return this.IsD365SalesQuotationLifecycleEnabled
        && this.IsD365SalesProspectIntegrationEnabled;
}
