AmountCur lineAmountExclTax(TransDate    _transDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), Tax _tax = null)
{
    // <GIN>
    #ISOCountryRegionCodes

    SalesQuotationLine  salesQuotationLine = this.salesQuotationLine();
    boolean             includesFormulaDesignerTax;
    boolean             countryRegion_IN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);

    if (countryRegion_IN)
    {
        includesFormulaDesignerTax = FormulaDesigner_IN::isPriceInclTaxOnLineAmount(salesQuotationLine.TaxItemGroup);
    }
    // </GIN>

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxModelTaxable taxModelTaxable = TaxModelDocLineFactory::newTaxModelDocLine(this.salesQuotationLine());
        if (taxModelTaxable && taxModelTaxable.isPriceInclTax())
        {
            return this.amountExcludingTaxGTE();
        }
    }
    // </GTE>

    if (this.salesQuotationTable().InclTax == NoYes::Yes
        // <GIN>
        || includesFormulaDesignerTax)
        // </GIN>
    {
        return this.salesQuotationLine().amountExclTax(this.LineAmount, this.DeliverNow, _transDate, _tax);
    }

    return this.LineAmount;
}

    void  modifiedClosed()
{
    if (this.Closed)
    {
        this.RemainAfter = 0;
        this.setRemainAfterInvent();
    }
    else
    {
        this.setQty(this.salesQuotationParmTable().Ordering, this.salesQuotationParmUpdate().CreditRemaining, false);
        this.setLineAmount();
    }
}

    void  modifiedDeliverNow()
{
    this.setQty(this.salesQuotationParmTable().Ordering, this.salesQuotationParmUpdate().CreditRemaining);
    this.setInventDeliverNow();
    this.setLineAmount();

    if (this.Closed)
    {
        this.RemainAfter = 0;
        this.setRemainAfterInvent();
    }

    this.calcPackingUnitQty();
}

    public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(SalesQuotationParmLine, DeliverNow):
            this.modifiedDeliverNow();
            break;

        case fieldNum(SalesQuotationParmLine, RemainAfter):
            this.setRemainAfterInvent();
            break;

        case fieldNum(SalesQuotationParmLine, InventNow):
            this.calcPackingUnitQty();
            break;

        case fieldNum(SalesQuotationParmLine, Closed):
            this.modifiedClosed();
            break;

        case fieldNum(SalesQuotationParmLine, PackingUnit):
            this.calcPackingUnitQty();
            break;

        default: break;
    }
}

