    display SalesQty qtyToPickInSalesUnit()
{
    SalesQuotationLine      salesQuotationLine;
    InventOnHandQty         inventOnHandQty;
    InventMovement          movement;

    salesQuotationLine = this.salesQuotationLine();
    movement  = InventMovement::construct(salesQuotationLine);

    inventOnHandQty = InventOnHandQty::newPhysicalUpdate(movement,movement.inventdim());

    return EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(salesQuotationLine.ItemId,
																				 salesQuotationLine.InventDimId,
																				 inventOnHandQty.availPhysical() - InventTransIdSum::newTransOriginId(movement.inventTransOriginId()).reservPhysical(),
																				 salesQuotationLine.inventTable().inventUnitId(),
																				 salesQuotationLine.SalesUnit,
																				 NoYes::Yes);
}

