    void  setInventDeliverNow()
{
    SalesQuotationLine salesQuotationLine;

    salesQuotationLine = this.salesQuotationLine();
    this.InventNow = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(this.ItemId,
																						   this.InventDimId,
																						   this.DeliverNow,
																						   salesQuotationLine.SalesUnit,
																						   salesQuotationLine.inventTable().inventUnitId(),
																						   NoYes::Yes);
}

    void setLineAmount(SalesQuotationLine     _salesQuotationLine = null)
{
    SalesQuotationLine localSalesQuotationLine;

    if (_salesQuotationLine)
    {
        localSalesQuotationLine = _salesQuotationLine;
    }
    else
    {
        localSalesQuotationLine = this.salesQuotationLine();
    }

    this.LineAmount = localSalesQuotationLine.calcLineAmount(this.DeliverNow);
}

    void  setQty(DocumentStatus         _documentStatus,
             boolean                _creditRemaining,
             boolean                _setInventNow       = true)
{
    SalesQuotationQuantity      salesQuotationQuantity;
    SalesQuotationLine          salesQuotationLine;
    InventQty                   inventNow;

    salesQuotationQuantity = SalesQuotationQuantity::construct(_documentStatus);

    salesQuotationLine = this.salesQuotationLine();

    salesQuotationLine.SalesDeliverNow = this.DeliverNow;
    salesQuotationLine.setInventDeliverNow();

    [this.DeliverNow,   this.RemainBefore,       this.RemainAfter]          = salesQuotationQuantity.qtySales(salesQuotationLine,
                                                                                                              SalesUpdate::All,
                                                                                                              salesQuotationLine.SalesDeliverNow,
                                                                                                              _creditRemaining);

    [inventNow,         this.RemainBeforeInvent, this.RemainAfterInvent]    = salesQuotationQuantity.qtyInvent(salesQuotationLine,
                                                                                                               SalesUpdate::All,
                                                                                                               salesQuotationLine.InventDeliverNow,
                                                                                                               _creditRemaining);

    if (_setInventNow)
    {
        this.InventNow = inventNow;
    }
}

    void  setRemainAfterInvent()
{
    SalesQuotationLine  salesQuotationLine;

    salesQuotationLine = this.salesQuotationLine();
    this.RemainAfterInvent = EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(this.ItemId,
																								   this.InventDimId,
																								   this.RemainAfter,
																								   salesQuotationLine.SalesUnit,
																								   salesQuotationLine.inventTable().inventUnitId(),
																								   NoYes::Yes);
}

    static boolean  exist(Num               _parmId,
                             QuotationIdBase   _quotationId)
{
    if (_parmId && _quotationId)
    {
        return (select firstonly RecId from salesQuotationParmLine
                    index hint ParmIdQuotationIdIdx
                    where salesQuotationParmLine.ParmId      == _parmId &&
                          salesQuotationParmLine.QuotationId == _quotationId
               ).RecId != 0;
    }

    return false;
}

    static boolean existTableRefId(ParmId           _parmId,
                                      TradeLineRefId   _tableRef)
{
    if (_parmId  &&  _tableRef)
    {
        return (select firstonly RecId from salesQuotationParmLine
                    index hint ParmIdTableRefIdIdx
                    where salesQuotationParmLine.ParmId      == _parmId
                       && salesQuotationParmLine.TableRefId  == _tableRef).RecId != 0;
    }

    return false;
}

    static void  findAndDelete(ParmId               _parmId,
                                  QuotationIdBase      _origQuotationId,
                                  TradeLineRefId       _tableRef)
{
    SalesQuotationParmLine  salesQuotationParmLine;

    delete_from salesQuotationParmLine
                index hint ParmIdTableRefIdIdx
                where salesQuotationParmLine.ParmId          == _parmId
                   && salesQuotationParmLine.TableRefId      == _tableRef
                   && salesQuotationParmLine.OrigQuotationId == _origQuotationId;
}

/// <summary>
/// Amount excluding tax for GTE.
/// </summary>
/// <returns>
/// The amount excluding tax.
/// </returns>
public amountCur amountExcludingTaxGTE()
{
    TaxBaseCur          taxOrigin;
    ITaxDocument        taxDocumentObject;
    ITaxDocumentLine    taxDocumentLineObject;

    taxOrigin = this.LineAmount;
    taxDocumentObject = TaxBusinessService::calculateTax(
            TaxableDocumentObject::construct(
                TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(this.salesQuotationParmTable())));

    if (taxDocumentObject)
    {
        taxDocumentLineObject = taxDocumentObject.findLineBySource(this.TableId, this.RecId);
        if (taxDocumentLineObject && taxDocumentLineObject.priceInclTax())
        {
            taxOrigin = taxOrigin - taxDocumentLineObject.getInclTax().amountTransactionCurrency();
        }
    }
    return taxOrigin;
}

/// <summary>
/// Mark current tax status of <c> SalesQuotationParmTable </c> to dirty
/// </summary>
// <GTE>
private void markCurrentTaxDocumentTaxStatusDirty()
{
    TaxableDocumentDescriptor descriptor = TaxableDocumentDescriptorFactory::getTaxableDocumentDescriptor(this.salesQuotationParmTable());
    ITaxableDocument taxableDocument = TaxableDocumentObject::construct(descriptor);
    TaxBusinessService::markTaxDocumentTaxStatus(taxableDocument, TaxDocumentCalculationLevel::Dirty);
}

// </GTE>

/// <summary>
/// Determines whether a change to the <c>SalesQuotationParmLine</c> table will update the Tax
/// </summary>
/// <param name="_origTransactionTable">
/// The original <c>SalesQuotationParmLine</c> record.
/// </param>
/// <returns>True if current line tax should be recalculated, otherwise false </returns>
// <GTE>
private boolean taxRecalculationNeeded(Common _origTransactionTable = null)
{
    SalesQuotationParmLine      salesQuotationParmLine_Orig;
    boolean                     updateNeeded;

    if (_origTransactionTable)
    {
        salesQuotationParmLine_Orig = _origTransactionTable as SalesQuotationParmLine;
    }
    else
    {
        salesQuotationParmLine_Orig = this.orig();
    }

    updateNeeded =
        ((this.ItemId                != salesQuotationParmLine_Orig.ItemId) ||
        (this.DeliveryPostalAddress != salesQuotationParmLine_Orig.DeliveryPostalAddress) ||
        (this.DeliverNow            != salesQuotationParmLine_Orig.DeliverNow) ||
        (this.LineAmount            != salesQuotationParmLine_Orig.LineAmount) ||
        (this.SalesCategory         != salesQuotationParmLine_Orig.SalesCategory));

    return updateneeded;
}

// </GTE>

/// <summary>
/// Update record.
/// </summary>
public void update()
{
    super();

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled() && this.SalesPurchJournalLine::taxRecalculationNeeded())
    {
        this.markCurrentTaxDocumentTaxStatusDirty();
    }
    // </GTE>
}

public void delete()
{
    super();

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        this.markCurrentTaxDocumentTaxStatusDirty();
    }
    // </GTE>
}

