/// <summary>
/// Checks if all the lines are matched or reconciled.
/// </summary>
/// <returns>
/// true if all the lines of the invoice is matched or reconciled; otherwise, false.
/// </returns>
boolean allMatchedOrReconciled()
{
    return this.totalNotReconciled() == 0;
}

/// <summary>
/// This method displays the workflow status.
/// </summary>
/// <returns>
/// The status of the work flow.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public display String30 displayWorkflowStatus()
{
    TMSInvoiceLine invoiceLine;

    for (TMSWorkflowStatus i = TMSWorkflowStatus::None; i <= TMSWorkflowStatus::Approved; i++)
    {
        while select invoiceLine
            where invoiceLine.InternalInvNumber == this.InternalInvNumber
        {
            if (i == TMSWorkflowStatus::None)
            {
                if (invoiceLine.displayWorkflowStatus(invoiceLine) ==
                    enum2Symbol(enumNum(TMSWorkflowStatus), TMSWorkflowStatus::None))
                {
                    return enum2Symbol(enumNum(TMSWorkflowStatus), TMSWorkflowStatus::None);
                }
                else
                {
                    continue;
                }
            }

            if (invoiceLine.findWorkflowStatus(i))
            {
                if (i == TMSWorkflowStatus::Approved && this.totalRemaining())
                {
                    return '';
                }
                return invoiceLine.displayWorkflowStatus(invoiceLine);
            }
        }
    }

    return '';
}

