/// <summary>
/// Populates default values in a field.
/// </summary>
/// <param name = "_fieldId">The Id of the field that gets defaulted.</param>
public void defaultField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(TMSInvoiceTable, DueUTCDateTime):
            this.setDueUTCDateTime();
            break;
    }
}

/// <summary>
/// Sets the <c>DueUTCDateTime</c>.
/// </summary>
public void setDueUTCDateTime()
{
    PaymTerm        paymTerm;
    VendTable       vendTable;

    vendTable = VendTable::find(this.VendAccount);

    if (vendTable.RecId != 0 && vendTable.PaymTermId != '')
    {
        paymTerm  = PaymTerm::find(vendTable.PaymTermId);

        if (paymTerm.RecId != 0)
        {
            this.DueUTCDateTime = DateTimeUtil::newDateTime((paymTerm.due(this.InvoiceDate ? this.InvoiceDate : this.DocumentDate, vendTable.PaymDayId)),
                                                    0,
                                                    DateTimeUtil::getCompanyTimeZone());
        }
    }
}

/// <summary>
/// Sets an internal invoice number using number sequence.
/// </summary>
void setInternalInvNum()
{
    this.InternalInvNumber = NumberSeq::newGetNum(TMSParameters::numRefTMSInternalInvNumber()).num();
}

/// <summary>
/// Sets vendor account information.
/// </summary>
void setVendAccounts()
{
    ttsbegin;

    this.VendAccount        = TMSCarrier::find(this.CarrierCode).VendorCode;
    this.VendInvoiceAccount = TMSCarrier::find(this.CarrierCode).VendorCode;

    ttscommit;
}

/// <summary>
/// Calculates total matched amount of the invoice.
/// </summary>
/// <returns>
/// The total matched amount of the invoice.
/// </returns>
Amount totalMatched()
{
    TmsfbInvMatch   invoiceMatch;
    TMSInvoiceLine  invoiceLine;

    select sum(RateCur) from invoiceMatch
        exists join invoiceLine
        where invoiceLine.RecId == invoiceMatch.InvoiceLineRecId &&
              invoiceLine.InternalInvNumber == this.InternalInvNumber;

    return invoiceMatch.RateCur;
}

/// <summary>
/// Calculates total unreconciled amount of the invoice.
/// </summary>
/// <returns>
/// The total unreconciled amount of the invoice.
/// </returns>
Amount totalNotReconciled()
{
    return this.totalRemaining();
}

/// <summary>
/// Calculates total reconciled amount of the invoice.
/// </summary>
/// <returns>
/// The total reconciled amount of the invoice.
/// </returns>
Amount totalReconciled()
{
    TmsInvoiceLineReconcile invoiceLineReconcile;
    TMSInvoiceLine          invoiceLine;

    select sum(Amount) from invoiceLineReconcile
        exists join invoiceLine
        where invoiceLine.RecId == invoiceLineReconcile.InvoiceLineRecId &&
              invoiceLine.InternalInvNumber == this.InternalInvNumber;

    return invoiceLineReconcile.Amount;
}

/// <summary>
/// Calculates the total remaining amount of the invoice.
/// </summary>
/// <returns>
/// The total remaining amount of the invoice.
/// </returns>
Amount totalRemaining()
{
    TMSInvoiceLine  invoiceLine;
    Amount          ret;

    while select invoiceLine
        where invoiceLine.InternalInvNumber == this.InternalInvNumber
    {
        ret += invoiceLine.amountRemaining();
    }

    return ret;
}

/// <summary>
/// Updates invoice header using the total amount of the lines.
/// </summary>
void updateInvoiceAmount()
{
    TMSInvoiceLine invoiceLine;

    ttsbegin;

    select sum(NetAmount), sum(Weight), sum(Qty) from invoiceLine
        where invoiceLine.InternalInvNumber == this.InternalInvNumber;

    this.InvoiceAmount   = invoiceLine.NetAmount;
    this.Weight             = invoiceLine.Weight;
    this.Qty                = invoiceLine.Qty;
    this.update();

    ttscommit;
}

/// <summary>
/// Updates a field of the invoice lines.
/// </summary>
/// <param name="_fieldId">
/// Id of the field to be updated.
/// </param>
/// <param name="_value">
/// Value to be set to field.
/// </param>
void updateInvoiceLines(FieldId _fieldId, anytype _value)
{
    TMSInvoiceLine  invoiceLine;
    SysDictField    field;
    Types           type;

    ttsbegin;

    field   = new SysDictField(tableNum(TMSInvoiceLine), _fieldId);
    type    = extendedTypeId2Type(field.typeId());

    while select forupdate invoiceLine
        where invoiceLine.InternalInvNumber == this.InternalInvNumber
    {
        switch (type)
        {
            case Types::String:
                invoiceLine.(_fieldId) = _value;
                break;

            case Types::Date:
                invoiceLine.(_fieldId) = str2Date(_value, 213);
                break;

            case Types::Real:
                invoiceLine.(_fieldId) = str2num(_value);
                break;

            case Types::Integer:
                invoiceLine.(_fieldId) = str2int(_value);
                break;

            default:
                invoiceLine.(_fieldId) = _value;
                break;
        }

        if (!invoiceLine.validateWrite())
        {
            throw error(strFmt("@TRX:TMSCannotUpdateRecordInTable", tablePName(TMSInvoiceLine)));
        }
        else
        {
            invoiceLine.update();
        }
    }

    ttscommit;
}

/// <summary>
/// Checks if a specific <c>tmsInvoiceTable</c> record exists.
/// </summary>
/// <param name="_tmsInternalInvNumber">
/// Internal invoice number of the record.
/// </param>
/// <returns>
/// True if the record exists.
/// </returns>
public static boolean exist(TMSInternalInvNumber _tmsInternalInvNumber)
{
    return (_tmsInternalInvNumber) &&
           (select firstonly tmsInvoiceTable
                where tmsInvoiceTable.InternalInvNumber == _tmsInternalInvNumber).RecId != 0;
}

/// <summary>
/// Filters invoice records by freight bill type.
/// </summary>
/// <param name="_filter">
/// Id of the freight bill type of the invoice records.
/// </param>
/// <returns>
/// String representation of the set of found record ids.
/// </returns>
public static str filterInvoices(TMSFreightBillTypeId _filter)
{
    TMSInvoiceTable         invoiceTable;
    TMSInvoiceLine          invoiceLine;
    Set                     invoices = new Set(Types::Int64);
    Query                   q;
    QueryRun                qr;
    QueryBuildDataSource    qbds, qbds2;
    str                     ret;

    if (_filter)
    {
        q = new Query();

        qbds = q.addDataSource(tableNum(TMSInvoiceTable));
        qbds.addGroupByField(fieldNum(TMSInvoiceTable, InternalInvNumber));
        qbds.addGroupByField(fieldNum(TMSInvoiceTable, RecId));

        qbds2 = qbds.addDataSource(tableNum(TMSInvoiceLine));
        qbds2.addRange(fieldNum(TMSInvoiceLine, FreightBillTypeId)).value(_filter);
        qbds2.joinMode(JoinMode::InnerJoin);
        qbds2.relations(true);

        qr = new QueryRun(q);

        while (qr.next())
        {
            invoiceTable = qr.get(tableNum(TMSInvoiceTable));
            invoiceLine  = qr.get(tableNum(TMSInvoiceLine));
            invoices.add(invoiceTable.RecId);
        }
    }

    // Return arbitrary string to ensure grid becomes empty
    if (invoices.empty() && _filter)
    {
        ret = '1';
    }

    else
    {
        ret =  strRem(invoices.toString(), '{}');
    }

    return ret;
}

/// <summary>
/// Finds a specific <c>TMSInvoiceTable</c> record with given parameters.
/// </summary>
/// <param name="_tmsInternalInvNumber">
/// Internal invoice number of the record.
/// </param>
/// <param name="_forupdate">
/// Determines whether the record is selected for update.
/// </param>
/// <returns>
/// The record that is searched for.
/// </returns>
public static TMSInvoiceTable find(TMSInternalInvNumber _tmsInternalInvNumber,
                                   boolean              _forupdate = false)
{
    TMSInvoiceTable    tmsInvoiceTable;

    if (_tmsInternalInvNumber)
    {
        tmsInvoiceTable.selectForUpdate(_forupdate);

        select firstonly tmsInvoiceTable
            where tmsInvoiceTable.InternalInvNumber == _tmsInternalInvNumber;
    }

    return tmsInvoiceTable;
}

