}

/// <summary>
/// Performs form lookup on <c>TMSCarrierService</c> for carrier service code.
/// </summary>
/// <param name="_ctrl">
/// Form control that lookup belongs to.
/// </param>
/// <param name="_carrierCode">
/// Carrier code to be set to filter records.
/// </param>
static void lookupCarrierServiceCode(FormControl _ctrl, TMSCarrierCode _carrierCode)
{
    SysTableLookup          sysTableLookup;
    Query                   query;
    QueryBuildDataSource    queryBuildDataSource;

    if (_carrierCode)
    {
        sysTableLookup          = SysTableLookup::newParameters(tableNum(TMSCarrierService), _ctrl);
        query                   = new Query();
        queryBuildDataSource    = query.addDataSource(tableNum(TMSCarrierService));

        sysTableLookup.addLookupfield(fieldNum(TMSCarrierService, CarrierServiceCode));
        sysTableLookup.addLookupfield(fieldNum(TMSCarrierService, Name));
        sysTableLookup.addLookupfield(fieldNum(TMSCarrierService, CarrierCode));
        queryBuildDataSource.addRange(fieldNum(TMSCarrierService, CarrierCode)).value(queryValue(_carrierCode));

        sysTableLookup.parmQuery(query);
        sysTableLookup.performFormLookup();
    }
}

/// <summary>
///     Finds a unique invoice record by vendor account and vendor invoice number.
/// </summary>
/// <param name="_vendAccount">
///     The associated vendor account.
/// </param>
/// <param name="_vendInvoiceId">
///     The associated vendor invoice number.
/// </param>
/// <param name="_forUpdate">
///     True if the records needs to be updated; false otherwise.
/// </param>
/// <returns>
///     A unique invoice record.
/// </returns>
public static TMSInvoiceTable findByVendAccountVendInvoice(
    VendAccount     _vendAccount,
    VendInvoiceId   _vendInvoiceId,
    boolean         _forUpdate      = false)
{
    TMSInvoiceTable tmsInvoiceTable;

    if (_vendAccount && _vendInvoiceId)
    {
        tmsInvoiceTable.selectForUpdate(_forupdate);

        select firstonly tmsInvoiceTable
            where tmsInvoiceTable.VendInvoiceId == _vendInvoiceId &&
                    tmsInvoiceTable.VendAccount   == _vendAccount;
    }

    return tmsInvoiceTable;
}

public void update()
{
    if (appl.ttsLevel() == 0)
    {
        throw error(strFmt("@SCM:TransactionExpected", funcName()));
    }

    if (this.AutoMatchReady != this.orig().AutoMatchReady)
    {
        this.updateInvoiceLines(fieldNum(TMSInvoiceLine, AutoMatchReady), this.AutoMatchReady);
    }

    if (this.VendInvoiceId != this.orig().VendInvoiceId)
    {
        this.updateInvoiceLines(fieldNum(TMSInvoiceLine, VendInvoiceId), this.VendInvoiceId);
    }

    super();
}

