/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
[Hookable(false)]
public boolean shouldThrowExceptionOnZeroDelete()
{
    return true;
}

public static void clearReferenceToLedgerJournal(LedgerJournalId _ledgerJournalId)
{
    TMSInvoiceTable tmsInvoiceTable;
    update_recordset tmsInvoiceTable 
        setting RefJournalNum = '' 
        where tmsInvoiceTable.RefJournalNum == _ledgerJournalId;
}
