/// <summary>
/// Displays the total number of loads on the segment.
/// </summary>
/// <returns>
/// The total number of loads on the segment.
/// </returns>
display TMSCurrentId displayNumberOfLoads()
{
    return (select count(RecId) from tmsRouteSegmentLoad
                where tmsRouteSegmentLoad.RouteCode      == this.RouteCode &&
                      tmsRouteSegmentLoad.Sequence       == this.Sequence).RecId;
}

