/// <summary>
///     Evaluate if the calling record has route rate details.
/// </summary>
/// <param name="_id">
///     The ID of the calling record.
/// </param>
/// <returns>
///     Ture if route details exists; otherwise false.
/// </returns>
public boolean enableRouteRateDetails(TMSId _id)
{
    TMSRouteRateDetail  detail;

    if (_id)
    {
        select firstonly RecId
            from detail
            where detail.RouteCode  == this.RouteCode
                && detail.Sequence  == this.Sequence
                && detail.Id        == _id;
    }

    return (detail.RecId != 0);
}

public void initValue()
{
    if (this.CurrencyCode == '')
    {
        this.CurrencyCode = CompanyInfoHelper::standardCurrency();
    }

    super();
}

public void insert()
{
    if (this.OriginHubCode)
    {
        this.OriginLocation = TMSHubMaster::find(this.OriginHubCode).Location;
    }

    if (this.DestinationHubCode)
    {
        this.DestinationLocation = TMSHubMaster::find(this.DestinationHubCode).Location;
    }

    super();
}

public void modifiedField(FieldId _fieldId)
{
    TMSRouteSegmentLoad     segmentLoad;
    TMSRoute                route;

    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(TMSRouteSegment, BookingNum):
            update_recordset segmentLoad
                setting BookingNum = this.BookingNum
                where segmentLoad.RouteCode == this.RouteCode &&
                      segmentLoad.Sequence == this.Sequence;
            break;

        case fieldNum(TMSRouteSegment, VesselName):
            update_recordset segmentLoad
                setting VesselName = this.VesselName
                where segmentLoad.RouteCode == this.RouteCode &&
                      segmentLoad.Sequence == this.Sequence;
            break;

        case fieldNum(TMSRouteSegment, VoyageNum):
            update_recordset segmentLoad
                setting VoyageNum = this.VoyageNum
                where segmentLoad.RouteCode == this.RouteCode &&
                      segmentLoad.Sequence == this.Sequence;
            break;

        case fieldNum(TMSRouteSegment, CurrencyCode):
            route = TMSRoute::find(this.RouteCode, true);
            if (route.Manual)
            {
                ttsbegin;

                route.CurrencyCode = this.CurrencyCode;
                route.update();

                ttscommit;
            }
            break;
    }
}

