/// <summary>
/// Ties route segment and load together, also ties route segment and shipment together if it is not connected before.
/// </summary>
/// <param name="_loadLine">
/// Load line record containing load and shipment info.
/// </param>
void setLoadAndShipmentXRef(WHSLoadLine _loadLine)
{
    TMSRouteSegmentLoad     segmentLoad;
    TMSRouteSegmentShipment segmentShipment;
    WHSLoadTable            load                = WHSLoadTable::find(_loadLine.LoadId, true);
    WHSShipmentTable        shipment            = WHSShipmentTable::find(_loadLine.ShipmentId, true);

    ttsbegin;

    if (!TMSRouteSegmentLoad::exist(this.RouteCode, this.Sequence, _loadLine.LoadId))
    {
        segmentLoad.initFromSegment(this);
        segmentLoad.Id = _loadLine.LoadId;
        if (!segmentLoad.validateWrite())
        {
            throw error(strFmt("@TRX:TMSCannotInsertRecordInTable", tablePName(TMSRouteSegmentLoad)));
        }
        else
        {
            segmentLoad.insert();
        }
    }

    if (!TMSRouteSegmentShipment::exist(this.RouteCode, this.Sequence, _loadLine.ShipmentId))
    {
        segmentShipment.initFromSegment(this);
        segmentShipment.Id = _loadLine.ShipmentId;
        if (!segmentLoad.validateWrite())
        {
            throw error(strFmt("@TRX:TMSCannotInsertRecordInTable", tablePName(TMSRouteSegmentShipment)));
        }
       else
        {
            segmentShipment.insert();
        }
    }

    if (shipment.RouteCode == '')
    {
        shipment.RouteCode = this.RouteCode;
        if (!shipment.validateWrite())
        {
            throw error(strFmt("@TRX:TMSCannotWriteRecordInTable", tablePName(WHSShipmentTable)));
        }
        else
        {
            shipment.write();
        }
    }

    ttscommit;
}

public void update()
{
    if (this.orig().OriginHubCode != this.OriginHubCode)
    {
        this.OriginLocation = TMSHubMaster::find(this.OriginHubCode).Location;
    }

    if (this.orig().DestinationHubCode != this.DestinationHubCode)
    {
        this.DestinationLocation = TMSHubMaster::find(this.DestinationHubCode).Location;
    }

    super();
}

/// <summary>
/// Checks if a specific <c>TMSRouteSegment</c> record exists.
/// </summary>
/// <param name="_tmsRouteCode">
/// Route code of the record.
/// </param>
/// <param name="_tmsSequence">
/// Sequence of the record.
/// </param>
/// <returns>
/// True if the record exists.
/// </returns>
public static boolean exist(TMSRouteCode _tmsRouteCode,
                            TMSSequence  _tmsSequence)
{
    return (_tmsRouteCode && _tmsSequence) &&
           (select firstonly tmsRouteSegment
                where tmsRouteSegment.RouteCode == _tmsRouteCode &&
                  tmsRouteSegment.Sequence ==  _tmsSequence).RecId != 0;
}

/// <summary>
/// Finds a specific <c>TMSRouteSegment</c> record with given parameters.
/// </summary>
/// <param name="_tmsRouteCode">
/// Route code of the record.
/// </param>
/// <param name="_tmsSequence">
/// Sequence of the record.
/// </param>
/// <param name="_forupdate">
/// Determines whether the record is selected for update.
/// </param>
/// <returns>
/// The record that is searched for.
/// </returns>
public static TMSRouteSegment find(TMSRouteCode _tmsRouteCode,
                                   TMSSequence  _tmsSequence,
                                   boolean      _forupdate = false)
{
    TMSRouteSegment    tmsRouteSegment;

    if (_tmsRouteCode &&
        _tmsSequence)
    {
        tmsRouteSegment.selectForUpdate(_forupdate);

        select firstonly tmsRouteSegment
            where tmsRouteSegment.RouteCode == _tmsRouteCode &&
                  tmsRouteSegment.Sequence ==  _tmsSequence;
    }

    return tmsRouteSegment;
}

/// <summary>
/// Creates the route segment based on the route and load.
/// </summary>
/// <param name = "_route">The parent route of the segment.</param>
/// <param name = "_load">The load that the route segment is being created for.</param>
/// <returns>The created segment.</returns>
public static TMSRouteSegment createFromRouteAndLoad(
    TMSRoute        _route,
    WHSLoadTable    _load)
{
    ttsbegin;

    TMSRouteSegment segment;

    segment.RouteCode                   = _route.RouteCode;
    segment.Sequence                    = 1;
    segment.CarrierCode                 = _load.CarrierCode;
    segment.CarrierServiceCode          = _load.CarrierServiceCode;
    segment.ModeCode                    = _load.ModeCode;
    segment.ScheduledShipUTCDateTime    = _route.ShipUTCDateTime;

    segment.setOriginDestinationFromLoad(_load);

    segment.insert();

    ttscommit;

    return segment;
}

private void setOriginDestinationFromLoad(WHSLoadTable _load)
{
    TMSRouteSegmentAddressPhoneParameters routeSegmentAddressPhoneParameters = TMSRouteSegmentAddressPhoneParameters::construct();

    if (_load.LoadDirection == WHSLoadDirection::Outbound)
    {
        this.initSegmentAddressPhoneParametersFromOutboundLoad(routeSegmentAddressPhoneParameters, _load);
    }
    else if (_load.LoadDirection == WHSLoadDirection::Inbound)
    {
        this.initSegmentAddressPhoneParametersFromInboundLoad(routeSegmentAddressPhoneParameters, _load);
    }

    if (!routeSegmentAddressPhoneParameters.FromAddress)
    {
        routeSegmentAddressPhoneParameters.FromAddress = _load.originAddress();
    }

    if (!routeSegmentAddressPhoneParameters.ToAddress)
    {
        routeSegmentAddressPhoneParameters.ToAddress = _load.destinationAddress();
    }

    this.initOrigin(routeSegmentAddressPhoneParameters.FromAddress);
    this.initDestination(routeSegmentAddressPhoneParameters.ToAddress);
}

private void initSegmentAddressPhoneParametersFromTransfer(TMSRouteSegmentAddressPhoneParameters _routeSegmentAddressPhoneParameters, WHSLoadTable _load)
{
    InventTransferTable transferTable = InventTransferTable::find(_load.OrderNum);

    if (transferTable)
    {
        InventLocation warehouse = InventLocation::find(_load.InventLocationId);

        _routeSegmentAddressPhoneParameters.FromAddress = TMSAddressPhone::initFromInventLocation(warehouse);
        _routeSegmentAddressPhoneParameters.ToAddress   = TMSAddressPhone::initFromInventLocation(InventLocation::find(transferTable.InventLocationIdTo), false, transferTable.ToPostalAddress);

        this.DlvTermId = transferTable.DlvTermId;
    }
}

private void initSegmentAddressPhoneParametersFromSales(TMSRouteSegmentAddressPhoneParameters _routeSegmentAddressPhoneParameters, WHSLoadTable _load)
{

    SalesTable salesTable = SalesTable::find(_load.OrderNum);

    if (salesTable.RecId)
    {
        InventLocation warehouse = InventLocation::find(_load.InventLocationId);

        _routeSegmentAddressPhoneParameters.FromAddress = TMSAddressPhone::initFromInventLocation(warehouse);
        _routeSegmentAddressPhoneParameters.ToAddress   = TMSAddressPhone::initFromSalesTable(salesTable);

        this.DlvTermId = salesTable.DlvTerm;
    }
}

private void initSegmentAddressPhoneParametersFromOutboundLoad(TMSRouteSegmentAddressPhoneParameters _routeSegmentAddressPhoneParameters, WHSLoadTable _load)
{
    if (_load.OrderNum)
    {
        if (WHSLoadLine::findFirstForOrderNum(_load.OrderNum, InventTransType::Sales))
        {
            this.initSegmentAddressPhoneParametersFromSales(_routeSegmentAddressPhoneParameters, _load);
        }
        else if (WHSLoadLine::findFirstForOrderNum(_load.OrderNum, InventTransType::TransferOrderShip))
        {
            this.initSegmentAddressPhoneParametersFromTransfer(_routeSegmentAddressPhoneParameters, _load);
        }
    }
}

private void initSegmentAddressPhoneParametersFromInboundLoad(TMSRouteSegmentAddressPhoneParameters _routeSegmentAddressPhoneParameters, WHSLoadTable _load)
{
    VendTable vendTable = VendTable::find(_load.AccountNum);

    if (vendTable.RecId)
    {
        InventLocation warehouse = InventLocation::find(_load.InventLocationId);

        _routeSegmentAddressPhoneParameters.FromAddress = TMSAddressPhone::initFromVendAccount(vendTable.AccountNum);
        _routeSegmentAddressPhoneParameters.ToAddress   = TMSAddressPhone::initFromInventLocation(warehouse);
    }
}

private void initOrigin(TMSAddressPhone _fromAddress)
{
    this.OriginName     = _fromAddress.parmName();
    this.OriginLocation = _fromAddress.parmLocation();
}

private void initDestination(TMSAddressPhone _toAddress)
{
    this.DestinationName     = _toAddress.parmName();
    this.DestinationLocation = _toAddress.parmLocation();
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
[Hookable(false)]
public boolean shouldThrowExceptionOnZeroDelete()
{
    return true;
}
