/// <summary>
/// Determines if main account is suitable for posting including Hungary Subscription/Continuous Tax
/// </summary>
/// <param name="_taxLedgerAccountGroup">
/// The taxLedgerAccountGroup instance which stores the Deferred Incoming Tax value
/// </param>
/// <param name="_accountId">
/// The Main account to be checked
/// </param>
/// <returns>
/// true if account suitable; otherwise false
/// </returns>
public boolean isAccountNumValidForDeferredTax(TaxLedgerAccountGroup _taxLedgerAccountGroup, MainAccountNum _accountId)
{
    boolean isGoodAccountNum;
    #isoCountryRegionCodes

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
    {
        if (_accountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(_taxLedgerAccountGroup.TaxIncomingDeferredLedgerDimension_HU).MainAccountId
            || _accountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(_taxLedgerAccountGroup.TaxOutgoingDeferredLedgerDimension_HU).MainAccountId)
        {
            isGoodAccountNum = true;
        }
    }

    return isGoodAccountNum;
}

/// <summary>
/// Determines if main account suitable for posting including RU fields
/// </summary>
/// <param name="_accountId">
/// Main account to be checked
/// </param>
/// <returns>
/// true if account suitable; otherwise false
/// </returns>
public boolean isGoodAccountNum_RU(MainAccountNum _accountId)
{
    #isoCountryRegionCodes
    boolean isGood()
    {
        RTax25LedgerAccountGroup    rTaxGroup = RTax25LedgerAccountGroup::find(this.TaxAccountGroup);
        int                         i;
        container                   ledgerDimensions = [
            this.TaxOutgoingOffsetLedgerDimension_RU,
            this.TaxDeferredLedgerDimension_RU,
            this.TaxIncomingLedgerDimension,
            rTaxGroup.AssessedTaxLedgerDimension,
            rTaxGroup.ctaLedgerDimension,
            rTaxGroup.ctlLedgerDimension,
            rTaxGroup.dtaLedgerDimension,
            rTaxGroup.dtlLedgerDimension
        ];

        for (i=1; i<=conLen(ledgerDimensions); i++)
        {
            if (_accountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(conPeek(ledgerDimensions, i)).MainAccountId)
            {
                return true;
            }
        }

        return false;
    }
    return SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) && isGood();
}

public LedgerDimensionDefaultAccount taxLedgerDimension(TaxDirection    _taxDirection)
{
    LedgerDimensionDefaultAccount   ledgerDimensionId;

    switch (_taxDirection)
    {
        case TaxDirection::IncomingTax:
            ledgerDimensionId = this.TaxIncomingLedgerDimension;
            break;
        case TaxDirection::OutgoingTax:
            ledgerDimensionId = this.TaxOutgoingLedgerDimension;
            break;
        case TaxDirection::UseTax:
            ledgerDimensionId = this.TaxUseTaxLedgerDimension;
            break;
        case TaxDirection::IncomingTax_W:
            ledgerDimensionId = this.TaxIncomingDifferenceLedgerDimension;
            break;
        case TaxDirection::OutgoingTax_W:
            ledgerDimensionId = this.TaxOutgoingDifferenceLedgerDimension;
            break;
    }

    return ledgerDimensionId;
}

/// <summary>
/// Gets the ledger dimension value for hungarian continuous taxes
/// </summary>
/// <param name="_taxDirection">
/// Tax Direction of transaction
/// </param>
/// <returns>
/// The appropriate account information depending on whether
/// the tax direction is incoming or outgoing
/// </returns>
public LedgerDimensionDefaultAccount taxLedgerDimension_HU(TaxDirection     _taxDirection)
{
    LedgerDimensionDefaultAccount   ledgerDimensionId;

    switch (_taxDirection)
    {
        case TaxDirection::IncomingTax:
        case TaxDirection::UseTax:
             ledgerDimensionId = this.TaxIncomingDeferredLedgerDimension_HU;
             break;

        case TaxDirection::OutgoingTax:
             ledgerDimensionId = this.TaxOutgoingDeferredLedgerDimension_HU;
             break;
        default:
            throw error("@SYS22316");
    }

    return ledgerDimensionId;
}

/// <summary>
/// Gets <c>LedgerDimensionDefaultAccount</c> for posting of journal of overdue debt VAT journal.
/// </summary>
/// <param name="_taxDirection">
/// Direction of tax.
/// </param>
/// <returns>
/// <c>LedgerDimensionDefaultAccount</c> value.
/// </returns>
/// <exception cref="Exception::Error">
///  If incorrect <c>TaxDirection</c> has been specified.
/// </exception>
public LedgerDimensionDefaultAccount taxReverseOffsetLedgerDimension_W(TaxDirection _taxDirection)
{
    LedgerDimensionDefaultAccount ret;
    switch (_taxDirection)
    {
        case TaxDirection::IncomingTax:
             ret = this.TaxReverseOffsetIncLedgerDimension_W;
             TaxLedgerAccountGroup::assertFieldRequired_W(true, ret, fieldnum(TaxLedgerAccountGroup, TaxReverseOffsetIncLedgerDimension_W));
             break;
        case TaxDirection::OutgoingTax:
             ret = this.TaxReverseOffsetOutLedgerDimension_W;
             TaxLedgerAccountGroup::assertFieldRequired_W(true, ret, fieldnum(TaxLedgerAccountGroup, TaxReverseOffsetOutLedgerDimension_W));
             break;
        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }
    return ret;
}

public void update()
{
    TaxLedgerAccountGroup this_Orig = this.orig();

    super();

    if (this.TaxUnrealizedReceivablesLedgerDimension != this_Orig.TaxUnrealizedReceivablesLedgerDimension)
    {
        LedgerCache::clearScope(LedgerCacheScope::TaxMainAccDimensionListProviderRegion);
    }

    if (this.TaxIncomingLedgerDimension != this_Orig.TaxIncomingLedgerDimension ||
        this.TaxOutgoingLedgerDimension != this_Orig.TaxOutgoingLedgerDimension ||
        this.TaxUseTaxLedgerDimension != this_Orig.TaxUseTaxLedgerDimension)
    {
        LedgerCache::clearScope(LedgerCacheScope::TaxMainAccDimensionListProvider);
    }
}

/// <summary>
///   Validates that the specified account is provided for the specified tax direction.
/// </summary>
/// <param name="_taxDirection">
///   The tax direction to validate.
/// </param>
/// <param name="_validatingUseTaxPayable">
///   true if we are to validate the use tax payable account; otherwise, false; optional.
/// </param>
/// <param name="_validatingExchangeRateTaxDifference">
///   true if we are to validate the tax exchange rate difference account; otherwise, false; optional.
/// </param>
/// <param name="_validatingExchangeRateTaxDifferenceOffset">
///   true if we are to validate the tax exchange rate difference offset account; otherwise, false; optional.
/// </param>
public void validateAccountForTaxDirection(TaxDirection _taxDirection, boolean _validatingUseTaxPayable = false
    ,
    boolean _validatingExchangeRateTaxDifference        = false,
    boolean _validatingExchangeRateTaxDifferenceOffset  = false
    )
{
    boolean displayError = false;

    // <GEEU>
    void validateDifferenceDimension(
        LedgerDimensionDefaultAccount   _dimension,
        LedgerDimensionDefaultAccount   _offset)
    {
        if (!_dimension)
        {
            if (_validatingExchangeRateTaxDifference)
            {
                displayError = true;
            }
        }
        if (!_offset)
        {
            if (_validatingExchangeRateTaxDifferenceOffset)
            {
                displayError = true;
            }
        }
    }
    // </GEEU>

    switch (_taxDirection)
    {
        case TaxDirection::IncomingTax :
            if (!this.TaxIncomingLedgerDimension)
            {
                displayError = true;
            }
            break;
        case TaxDirection::OutgoingTax :
            if (!this.TaxOutgoingLedgerDimension)
            {
                displayError = true;
            }
            break;
        case TaxDirection::UseTax :
            // <GEEU>
            if (_validatingExchangeRateTaxDifference && !this.TaxIncomingDifferenceLedgerDimension)
            {
                displayError = true;
            }
            else if (_validatingExchangeRateTaxDifferenceOffset && !this.TaxOutgoingDifferenceLedgerDimension)
            {
                displayError = true;
            }
            else
            {
            // </GEEU>
                if (!this.TaxOffsetUseTaxLedgerDimension & _validatingUseTaxPayable)
                {
                    displayError = true;
                }

                if (!TaxParameters::find().PurchTaxOnOperations && !this.TaxUseTaxLedgerDimension && !_validatingUseTaxPayable)
                {
                    displayError = true;
                }
            // <GEEU>
            }
            // </GEEU>
            break;
        // <GEEU>
        case TaxDirection::IncomingTax_W:
            validateDifferenceDimension(
                this.TaxIncomingDifferenceLedgerDimension,
                this.TaxIncomingDiffOffsetLedgerDimension);
            break;
        case TaxDirection::OutgoingTax_W:
            validateDifferenceDimension(
                this.TaxOutgoingDifferenceLedgerDimension,
                this.TaxOutgoingDiffOffsetLedgerDimension);
            break;
        // </GEEU>
    }

    if (displayError)
    {
        // <GEEU>
        if (_validatingExchangeRateTaxDifference || _validatingExchangeRateTaxDifferenceOffset)
        {
            throw error(strFmt("@GLS221223", this.Name));
        }
        else
        {
        // </GEEU>
            throw error(strFmt("@Tax:NotHaveAccountErrorMsg", this.TaxAccountGroup, _taxDirection));
        // <GEEU>
        }
        // </GEEU>
    }
}

    boolean validateWrite()
    {
        // <GEEHU>
        #isoCountryRegionCodes
        // </GEEHU>
        boolean ok = true;
        // <GEEU>
        boolean checkLedgerDimension(LedgerDimensionAccount _dimension)
        {
            return MainAccount::checkAllowPosting(
                LedgerDimensionFacade::getMainAccountFromLedgerDimension(_dimension).MainAccountId,
                LedgerPostingType::Tax,
                true
            );
        }
        // </GEEU>

        ok = super();
        if (ok)
        {
            ok =  MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxReportLedgerDimension).MainAccountId, LedgerPostingType::Tax, true) &&
                  MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxIncomingLedgerDimension).MainAccountId, LedgerPostingType::Tax, true) &&
                  MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxOutgoingLedgerDimension).MainAccountId, LedgerPostingType::Tax, true) &&
                  MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxUseTaxLedgerDimension).MainAccountId, LedgerPostingType::Tax, true) &&
                  // <GEERU>
                  checkLedgerDimension(this.TaxDeferredLedgerDimension_RU)          &&
                  checkLedgerDimension(this.TaxIncomingPaymentLedgerDimension_RU)   &&
                  checkLedgerDimension(this.TaxOutgoingOffsetLedgerDimension_RU)    &&
                  checkLedgerDimension(this.TaxGoodsInRouteLedgerDimension_RU)      &&
                  // </GEERU>
                  MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxOffsetUseTaxLedgerDimension).MainAccountId, LedgerPostingType::Tax, true)
            // <GBR>
                  && (!BrazilParameters::isEnabled()
                    || (MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxSalesOffsetLedgerDimension_BR).MainAccountId, LedgerPostingType::TaxExpense_BR, true) &&
                        MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxIncomingLongTermLedgerDimension_BR).MainAccountId, LedgerPostingType::Tax, true) &&
                        MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxIncomingShortTermLedgerDimension_BR).MainAccountId, LedgerPostingType::Tax, true)))
            // </GBR>
					&& MainAccount::checkAllowPosting(LedgerDimensionFacade::getMainAccountFromLedgerDimension(this.TaxVatInAdvanceLedgerDimension_IT).MainAccountId)
            ;
            // <GEEU>
            if (ok && (TaxParameters::isBankExchRateEnabled_W() || TaxSpecificExchangeRateHelper::isTaxSpecificExchangeRateEnabled()))
            {
                ok = checkLedgerDimension(this.TaxIncomingDifferenceLedgerDimension)
                    && checkLedgerDimension(this.TaxIncomingDiffOffsetLedgerDimension)
                    && checkLedgerDimension(this.TaxOutgoingDifferenceLedgerDimension)
                    && checkLedgerDimension(this.TaxOutgoingDiffOffsetLedgerDimension);
            }
            // </GEEU>

            // <GEEHU>
            if (ok && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
            {
                ok = checkLedgerDimension(this.TaxIncomingDeferredLedgerDimension_HU) &&
                    checkLedgerDimension(this.TaxOutgoingDeferredLedgerDimension_HU);
            }
            // </GEEHU>

            // <GJP>
            if (ok && TaxConsInvoice_JP::isTaxDifferencePostingOnConsInvoiceEnabled(true))
            {
                ok = checkLedgerDimension(this.TaxNondeductibleTaxLedgerDimension);
            }
            // </GJP>

            if (ok)
            {
                if (! TaxParameters::find().PurchTaxOnOperations &&
                    this.TaxUseTaxLedgerDimension == this.TaxOffsetUseTaxLedgerDimension &&
                    (this.TaxUseTaxLedgerDimension && this.TaxOffsetUseTaxLedgerDimension))
                    ok = checkFailed("@SYS24548");
            }
        }
        return ok;
    }

public static LedgerDimensionDefaultAccount accountLedgerDimension(
    TaxCode         _taxCode,
    TaxDirection    _taxDirection)
{
    TaxLedgerAccountGroup taxLedgerAccountGroup;
    TaxAccountGroup     taxAccountGroup;

    taxAccountGroup = TaxTable::findAccountGroup(_taxCode);

    taxLedgerAccountGroup= TaxLedgerAccountGroup::find(taxAccountGroup);

    switch (_taxDirection)
    {
        case TaxDirection::IncomingTax  :
            return taxLedgerAccountGroup.TaxIncomingLedgerDimension;
        case TaxDirection::OutgoingTax  :
            return taxLedgerAccountGroup.TaxOutgoingLedgerDimension;
        case TaxDirection::UseTax       :
            return taxLedgerAccountGroup.TaxUseTaxLedgerDimension;
    }
    throw error("@SYS22316");
}

