static void assertFieldRequired_W(boolean _required, anytype _value, FieldId _field)
{
    if (_required && ! _value)
    {
        throw error(strFmt("@SYS26332", fieldId2pname(tableNum(TaxLedgerAccountGroup), _field)));
    }
}

public static boolean checkExist(TaxAccountGroup _taxAccountGroup )
{
    boolean ret = true;
    ;

    if (!TaxLedgerAccountGroup::exist(_taxAccountGroup))
    {
        ret = checkFailed(strFmt(TaxLedgerAccountGroup::txtNotExist(), _taxAccountGroup));
    }

    return ret;
}

