}

public static boolean exist(TaxAccountGroup _taxAccountGroup )
{
    boolean found;
    ;

    found = (select firstonly
                 RecId
            from
                 taxLedgerAccountGroup
             where
                 taxLedgerAccountGroup.TaxAccountGroup == _taxAccountGroup).RecId != 0;

    return found;
}

public static TaxLedgerAccountGroup find(
    TaxAccountGroup     _taxAccountGroup,
    boolean             _forupdate = false,
    ConcurrencyModel    _concurrencyModel = ConcurrencyModel::Auto)
{
    TaxLedgerAccountGroup taxLedgerAccountGroup;

    taxLedgerAccountGroup.selectForUpdate(_forupdate);
    if (_forupdate  && _concurrencyModel != ConcurrencyModel::Auto)
    {
        taxLedgerAccountGroup.concurrencyModel(_concurrencyModel);
    }

    select firstonly
        taxLedgerAccountGroup
        where
        taxLedgerAccountGroup.TaxAccountGroup == _taxAccountGroup;

    return taxLedgerAccountGroup;
}

/// <summary>
/// Determines whether the specified record occurs in the <c>TaxLedgerAccountGroup</c> table.
/// </summary>
/// <param name="_taxCode">
/// The <c>TaxCode</c> value to use to get the <c>TaxLedgerAccountGroup</c> table buffer.
/// </param>
/// <param name="_isCustomer">
/// A Boolean value that indicates whether to get the <c>TaxOutgoingLedgerDimension</c> or
/// <c>TaxIncomingLedgerDimension</c> value.
/// </param>
/// <returns>
/// A <c>LedgerDimensionDefaultAccount</c> value, depending on the specified <c>TaxCode</c> value.
/// </returns>
public static LedgerDimensionDefaultAccount findTaxRealizedLedgerDimesnion(
    TaxCode _taxCode,
    boolean _isCustomer = false)
{
    TaxLedgerAccountGroup   taxLedgerAccountGroup;

    taxLedgerAccountGroup = TaxLedgerAccountGroup::find(TaxTable::findAccountGroup(_taxCode));

    if (_isCustomer)
    {
        return taxLedgerAccountGroup.TaxOutgoingLedgerDimension;
    }
    else
    {
        return taxLedgerAccountGroup.TaxIncomingLedgerDimension;
    }
}

