/// <summary>
/// Determines whether the specified record occurs in the <c>TaxLedgerAccountGroup</c> table.
/// </summary>
/// <param name="_taxAccountGroup">
/// A <c>TaxAccountGroup</c> value to use to find the <c>TaxLedgerAccountGroup</c> table buffer.
/// </param>
/// <param name="_ledgerDimension">
/// The <c>LedgerDimensionAccount</c> value to use to find the <c>MainAccountId</c> value.
/// </param>
/// <returns>
/// true if the account number is used as one of the tax accounts in the account group; otherwise,
/// false.
/// </returns>
static boolean ledgerDimensionExistInGroup(
    TaxAccountGroup         _taxAccountGroup,
    LedgerDimensionAccount  _ledgerDimension)
{
    MainAccountNum        ledgerMainAccountId;
    TaxLedgerAccountGroup taxLedgerAccountGroup = TaxLedgerAccountGroup::find(_taxAccountGroup);

    if (taxLedgerAccountGroup)
    {
        ledgerMainAccountId = LedgerDimensionFacade::getMainAccountFromLedgerDimension(_ledgerDimension).MainAccountId;
        if (LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxIncomingLedgerDimension).MainAccountId     == ledgerMainAccountId ||
            LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxOutgoingLedgerDimension).MainAccountId     == ledgerMainAccountId ||
            LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxReportLedgerDimension).MainAccountId       == ledgerMainAccountId ||
            LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxUseTaxLedgerDimension).MainAccountId       == ledgerMainAccountId ||
            LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxOffsetUseTaxLedgerDimension).MainAccountId == ledgerMainAccountId)
            return true;
    }
    return false;
}

