/// <summary>
///    If the account type or the offset account type of the journal line is ledger, this method checks if the tax account exists according to the ledger account posting group for tax.
/// </summary>
/// <param name="_ledgerDimension">
///    The ledger dimension to verify.
/// </param>
/// <param name="_taxCode">
///    The tax code to verify.
/// </param>
/// <returns>
///    true if one of the accounts is a ledger account, and that ledger account is a tax account; otherwise, false.
/// </returns>
public static boolean verifyVATAccountNum(LedgerDimensionAccount _ledgerDimension, TaxCode _taxCode)
{
    TaxLedgerAccountGroup   taxLedgerAccountGroup;
    MainAccountNum          ledgerMainAccountId;
    boolean                 isValid;

    taxLedgerAccountGroup = TaxLedgerAccountGroup::find(TaxTable::find(_taxCode).TaxAccountGroup);
    ledgerMainAccountId = LedgerDimensionFacade::getMainAccountFromLedgerDimension(_ledgerDimension).MainAccountId;

    // <GIN>
    if (TaxParameters::checkTaxParameters_IN()
        && TaxTable::find(_taxCode).TaxType_IN != TaxType_IN::None)
    {
        // Skip the standard posting profile validation for India. India posting profile validation is handled differently.
        isValid = true;
    }
    else
    // </GIN>

    if (taxLedgerAccountGroup.RecId != 0)
    {
        if (ledgerMainAccountId &&
            (ledgerMainAccountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxIncomingLedgerDimension).MainAccountId ||
            ledgerMainAccountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxOutgoingLedgerDimension).MainAccountId ||
            ledgerMainAccountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxReportLedgerDimension).MainAccountId   ||
            ledgerMainAccountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxUseTaxLedgerDimension).MainAccountId   ||
            ledgerMainAccountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxOffsetUseTaxLedgerDimension).MainAccountId ||
            (TaxConsInvoice_JP::isTaxDifferencePostingOnConsInvoiceEnabled(true) &&
                ledgerMainAccountId == LedgerDimensionFacade::getMainAccountFromLedgerDimension(taxLedgerAccountGroup.TaxNondeductibleTaxLedgerDimension).MainAccountId)
            // <GEERU>
            || taxLedgerAccountGroup.isGoodAccountNum_RU(ledgerMainAccountId)
            // </GEERU>
            // <GEEHU>
            || taxLedgerAccountGroup.isAccountNumValidForDeferredTax(taxLedgerAccountGroup, ledgerMainAccountId)
            // </GEEHU>
            ))
            isValid = true;
        else
            isValid = checkFailed(strFmt("@SYS66086", ledgerMainAccountId, taxLedgerAccountGroup.TaxAccountGroup, _taxCode));
    }

    return isValid;
}
