Method.Source:
/// <summary>
///  Gets the specified record in the <c>TaxTransHistory_W</c> table.
/// </summary>
/// <returns>
///  A record in the <c>TaxTransHistory_W</c> table; otherwise, an empty record.
/// </returns>
public TaxTransHistory_W taxTransHistory_W()
{
    TaxTransHistory_W taxTransHistory_W;

    if (this.RecId)
    {
        select firstonly taxTransHistory_W
            where taxTransHistory_W.TaxTrans == this.RecId;
    }

    return taxTransHistory_W;
}

