Method.Source:
/// <summary>
/// Gets the company registration number for Excise tax from <c>TaxRegistrationNumbers_IN</c> table using CompanyRegistrationNumber field.
/// </summary>
/// <returns>
/// returns company registration number for Excise tax.
/// </returns>
display CompanyEccNumber_IN companyECCNumber()
{
    CompanyEccNumber_IN     companyEccNumber;
    SecurityRights          sr;
    SecurityTableRights     stRights;
    AccessRight             ar;

    sr = SecurityRights::construct();
    stRights = sr.tableFieldAccessRights(tableStr(TaxTrans_IN));
    ar = stRights.fieldAccessRight(fieldStr(TaxTrans_IN,CompanyRegistrationNumber));

    if (ar != AccessRight::NoAccess)
    {
        if (this.TaxType == TaxType_IN::Excise)
        {
            companyEccNumber = TaxRegistrationNumbers_IN::find(this.CompanyRegistrationNumber).RegistrationNumber;
        }
    }
    return companyEccNumber;
}

