/// <summary>
/// Copys tax transaction.
/// </summary>
/// <param name="_refTaxTrans">
/// The tax transaction reference.
/// </param>
/// <param name="_taxTrans">
/// The tax transaction.
/// </param>
/// <param name="_factor">
/// The factor.
/// </param>
public void copyTaxTrans(
    TaxTrans    _refTaxTrans,
    TaxTrans    _taxTrans,
    Integer     _factor)
{
    TaxTrans_IN taxTrans;
    TaxTrans_W  taxTransW = TaxTrans_W::findByTaxTrans(_taxTrans.RecId);

    if (TaxTable::find(_taxTrans.TaxCode).TaxType_IN != TaxType_IN::None)
    {
        select RecId, SourceRecId, SourceTableId, TaxComponentTable, TaxLedgerAccountGroup, LoadOnInventoryPercent,
            LoadOnInventoryAmount, PostedTaxAmout, CustVendRegistrationNumber, RecoverableAmount, PayableAmount,
            DeferredAmount, AbatementPercent, AbatementAmount from taxTrans
            where taxTrans.RefRecId == _refTaxTrans.RecId;

        this.RefRecId                   = _taxTrans.RecId;
        this.SourceRecId                = taxTrans.SourceRecId;
        this.SourceTableId              = taxTrans.SourceTableId;
        this.TaxComponentTable          = taxTrans.TaxComponentTable;
        this.TaxLedgerAccountGroup      = taxTrans.TaxLedgerAccountGroup;
        this.LoadOnInventoryPercent     = taxTrans.LoadOnInventoryPercent;
        this.LoadOnInventoryAmount      = taxTrans.LoadOnInventoryAmount * _factor;
        this.PostedTaxAmout             = taxTrans.PostedTaxAmout * _factor;
        this.CustVendRegistrationNumber = taxTrans.CustVendRegistrationNumber;
        this.NonRecoverableAmt          = taxTrans.NonRecoverableAmt * _factor;
        this.vatScheduleName            = taxTrans.vatScheduleName;
        this.CustVendRegistrationNumber = taxTrans.CustVendRegistrationNumber;
        this.RecoverableAmount          = taxTrans.RecoverableAmount * _factor;
        this.PayableAmount              = taxTrans.PayableAmount * _factor;
        this.DeferredAmount             = taxTrans.DeferredAmount * _factor;
        this.AbatementPercent           = taxTrans.AbatementPercent;
        this.AbatementAmount            = taxTrans.AbatementAmount * _factor;
        this.ReverseChargePercentage_IN = taxTransW.ServiceTaxReverseChargePercentage_IN;
    }
}

/// <summary>
/// Gets the customer registration number for Excise tax from <c>TaxRegistrationNumbers_IN</c> table using CustVendRegistrationNumber field.
/// </summary>
/// <returns>
/// returns customer registration number for Excise tax.
/// </returns>
display CustomerEccNumber_IN customerECCNumber()
{
    CustomerEccNumber_IN    customerEccNumber;
    SecurityRights          sr;
    SecurityTableRights     stRights;
    AccessRight             ar;

    sr = SecurityRights::construct();
    stRights = sr.tableFieldAccessRights(tableStr(TaxTrans_IN));
    ar = stRights.fieldAccessRight(fieldStr(TaxTrans_IN,CustVendRegistrationNumber));

    if (ar != AccessRight::NoAccess)
    {
        if (this.TaxType == TaxType_IN::Excise && this.Customer)
        {
            customerEccNumber = TaxRegistrationNumbers_IN::find(this.CustVendRegistrationNumber).RegistrationNumber;
        }
    }
    return customerEccNumber;
}

