/// <summary>
/// Exports the tariff code.
/// </summary>
/// <returns>
/// The tariff code for export.
/// </returns>
display ExportTariffCode_IN exportTariffCode()
{
    ExportTariffCode_IN exportTariffCode;

    if (this.CustomsDirection == Direction_IN::Export)
    {
        exportTariffCode = CustomsTariffCodeTable_IN::find(this.CustomsTariffCodeTable).TariffCode;
    }

    return exportTariffCode;
}

/// <summary>
/// Gets excise ledger accounts.
/// </summary>
/// <param name="_exciseRecordType">
/// The excise record type.
/// </param>
/// <param name="_taxTrans">
/// The tax transaction.
/// </param>
/// <param name="_taxAccountGroup">
/// The tax account group.
/// </param>
/// <param name="_directSettlement">
/// The direct settlement.
/// </param>
/// <param name="_exciseDeferredAccount">
/// The excise deferred account.
/// </param>
/// <param name="_tmpTaxAccountNum">
/// The temporary account number.
/// </param>
/// <param name="_inventTransferUpdateType">
/// The invent transfer update type.
/// </param>
public void getExciseLedgerAccounts(
    ExciseRecordType_IN         _exciseRecordType,
    TaxTrans                    _taxTrans,
    RefRecId                    _taxAccountGroup,
    DirectSettlement_IN         _directSettlement,
    RefRecId                    _exciseDeferredAccount,
    RefRecId                    _tmpTaxAccountNum,
    InventTransferUpdateType    _inventTransferUpdateType = InventTransferUpdateType::PickingList)
{
    TaxLedgerAccounts_IN     taxLedgerAccounts;
    TaxLedgerAccountSetup_IN taxLedgerAccountSetup;
    TransDateTime            transDateTime;
    LedgerDimensionDefaultAccount defaultAccount;
    TaxTransGeneralJournalAccountEntry  taxTransGeneralJournalAccountEntry;

    select firstOnly LedgerDimension from taxTransGeneralJournalAccountEntry
        where taxTransGeneralJournalAccountEntry.TaxTrans == _taxTrans.RecId
            && taxTransGeneralJournalAccountEntry.TaxTransRelationship == TaxTransRelationshipType::Tax;

    defaultAccount = LedgerDefaultAccountHelper::getDefaultAccountFromLedgerDimension(taxTransGeneralJournalAccountEntry.LedgerDimension);

    if (_taxTrans.SourceTableId != tablenum(InventQualityOrderTable))
    {
        transDateTime = DateTimeUtil::newDateTime(_taxTrans.TransDate, 0, DateTimeUtil::getUserPreferredTimeZone());
        switch (_exciseRecordType)
        {
            case ExciseRecordType_IN::RG23A:
                this.ClaimPercentage   = ClaimPercentage_IN::findClaimPercentage(_taxTrans.taxTrans_W().TaxComponentTable_IN, transDateTime, transDateTime, RGRegister_IN::RG23A).Percentage;
                break;

            case ExciseRecordType_IN::RG23C:
                this.ClaimPercentage    = ClaimPercentage_IN::findClaimPercentage(_taxTrans.taxTrans_W().TaxComponentTable_IN, transDateTime, transDateTime, RGRegister_IN::RG23C).Percentage;
                break;
            default :
                this.ClaimPercentage = 0;
                break;
        }
    }

    if (this.ClaimPercentage && _taxTrans.TaxDirection == TaxDirection::IncomingTax &&
       _taxTrans.TaxItemGroup && _taxTrans.TaxGroup)
    {
        this.DeferredAmount  = (_taxTrans.TaxAmount - _taxTrans.TaxInCostPriceMST) * (1 - this.ClaimPercentage /100);
    }

    select AccountType from taxLedgerAccounts
        join RecId from taxLedgerAccountSetup
            where taxLedgerAccountSetup.TaxLedgerAccountGroup == _taxAccountGroup
               && taxLedgerAccounts.TaxLedgerAccountSetup     == taxLedgerAccountSetup.RecId
               && taxLedgerAccounts.LedgerDimension           == defaultAccount;

    if (taxLedgerAccounts.AccountType == TaxAccountType_IN::ExcisePayableAccount)
    {
        this.PayableLedgerDimension = taxTransGeneralJournalAccountEntry.LedgerDimension;
    }
    if (_taxTrans.TaxDirection == TaxDirection::OutgoingTax && _exciseRecordType == ExciseRecordType_IN::RG23D)// &&
        //_taxTrans.AccountNum)
    {
        this.rg23dcenvatCreditTransLedgerDimension = taxTransGeneralJournalAccountEntry.LedgerDimension;
    }
    if (_taxTrans.TaxDirection == TaxDirection::IncomingTax && _exciseRecordType == ExciseRecordType_IN::RG23D)// &&
        //_taxTrans.AccountNum)
    {
        this.rg23dcenvatCreditLedgerDimension =taxTransGeneralJournalAccountEntry.LedgerDimension;
    }

    if (_taxTrans.Source !=  TaxModuleType::Voucher && _taxTrans.Source !=  TaxModuleType::FreeTxtInvoice &&
        _inventTransferUpdateType != InventTransferUpdateType::Receive)
    {
        if (_directSettlement == DirectSettlement_IN::PLA && taxTransGeneralJournalAccountEntry.LedgerDimension)
        {
            this.plaLedgerDimension = taxTransGeneralJournalAccountEntry.LedgerDimension;
        }
    }

    if (_taxTrans.Source ==  TaxModuleType::Purch || _taxTrans.Source ==  TaxModuleType::StockTransfer_IN &&
        _inventTransferUpdateType != InventTransferUpdateType::Shipment)
    {
        if (_exciseRecordType == ExciseRecordType_IN::RG23A &&
            _tmpTaxAccountNum)
        {
            this.rg23aDeferredLedgerDimension = _tmpTaxAccountNum;
        }
        if (_exciseRecordType == ExciseRecordType_IN::RG23C &&
            _tmpTaxAccountNum)
        {
            this.rg23cDeferredLedgerDimension = _tmpTaxAccountNum;
        }
    }

    if (_taxTrans.Source ==  TaxModuleType::Sales || _taxTrans.Source ==  TaxModuleType::StockTransfer_IN &&
        _inventTransferUpdateType != InventTransferUpdateType::Receive)
    {
        if ( _taxTrans.TaxDirection == TaxDirection::IncomingTax && _directSettlement == DirectSettlement_IN::RG23A &&
            taxLedgerAccounts.AccountType  == TaxAccountType_IN::ExciseRecoverableAccount && taxTransGeneralJournalAccountEntry.LedgerDimension)
        {
            this.RecoverableLedgerDimension =   taxTransGeneralJournalAccountEntry.LedgerDimension;
        }
        if (_taxTrans.TaxDirection == TaxDirection::IncomingTax && _directSettlement == DirectSettlement_IN::RG23C &&
            taxLedgerAccounts.AccountType  == TaxAccountType_IN::ExciseRecoverableAccount && taxTransGeneralJournalAccountEntry.LedgerDimension)
        {
            this.RecoverableLedgerDimension =   taxTransGeneralJournalAccountEntry.LedgerDimension;
        }
    }

    if (_taxTrans.Source !=  TaxModuleType::Sales && _taxTrans.Source !=  TaxModuleType::FreeTxtInvoice &&
        _inventTransferUpdateType != InventTransferUpdateType::Shipment)
    {
        if (taxLedgerAccounts.AccountType == TaxAccountType_IN::ExciseRecoverableAccount)
        {
            this.RecoverableLedgerDimension = taxTransGeneralJournalAccountEntry.LedgerDimension;
        }
    }

    if (_taxTrans.Source ==  TaxModuleType::Voucher)
    {
        if (_exciseRecordType == ExciseRecordType_IN::RG23A && _exciseDeferredAccount)
        {
            this.rg23aDeferredLedgerDimension = _exciseDeferredAccount;
        }
        if (_exciseRecordType == ExciseRecordType_IN::RG23C && _exciseDeferredAccount)
        {
            this.rg23cDeferredLedgerDimension = _exciseDeferredAccount;
        }
    }
}

/// <summary>
/// Imports the tariff code.
/// </summary>
/// <returns>
/// The tariff code for import.
/// </returns>
display ImportTariffCode_IN importTariffCode()
{
    ImportTariffCode_IN importTariffCode;

    if (this.CustomsDirection == Direction_IN::Import)
    {
        importTariffCode = CustomsTariffCodeTable_IN::find(this.CustomsTariffCodeTable).TariffCode;
    }

    return importTariffCode;
}

/// <summary>
/// Proportionates the tax amounts and populates the bufer.
/// </summary>
/// <param name="_taxTrans">
/// Taxtrans buffer.
/// </param>
/// <param name="_factor">
/// The factor used or proportionating.
/// </param>

public void initFromTaxTrans_IN(
    TaxTrans_IN _taxTrans,
    Factor _factor =  1)
{
    TaxTrans_W taxTransW = TaxTrans_W::findByTaxTrans(_taxTrans.RecId);

    if (_taxTrans)
    {
        this.data(_taxTrans);
        this.LoadOnInventoryAmount              = _taxTrans.LoadOnInventoryAmount * _factor;
        this.PostedTaxAmout                     = _taxTrans.PostedTaxAmout * _factor;
        this.NonRecoverableAmt                  = _taxTrans.NonRecoverableAmt * _factor;
        this.RecoverableAmount                  = _taxTrans.RecoverableAmount * _factor;
        this.PayableAmount                      = _taxTrans.PayableAmount * _factor;
        this.DeferredAmount                     = _taxTrans.DeferredAmount * _factor;
        this.AbatementAmount                    = _taxTrans.AbatementAmount * _factor;
        this.MiscChargeAmount                   = _taxTrans.MiscChargeAmount * _factor;
        this.RetentionAmount                    = _taxTrans.RetentionAmount * _factor;
        this.SourceBaseAmountCurRegulated       = _taxTrans.SourceBaseAmountCurRegulated * _factor;
        this.AssessableValue                    = _taxTrans.AssessableValue * _factor;
        this.CustomsCIFAmount                   = _taxTrans.CustomsCIFAmount * _factor;
        this.SourceRegulateAmountCur            = _taxTrans.SourceRegulateAmountCur * _factor;
        this.CustomsTaxRegulationAmountCur      = _taxTrans.CustomsTaxRegulationAmountCur * _factor;
        this.CustomsFOBAmount                   = _taxTrans.CustomsFOBAmount * _factor;
        this.CustomsLandingChargesAmount        = _taxTrans.CustomsLandingChargesAmount * _factor;
        this.CustomsPreferentialValue           = _taxTrans.CustomsPreferentialValue * _factor;
        this.SourceTaxAmountCur                 = _taxTrans.SourceTaxAmountCur * _factor;
        this.SourceBaseAmountCur                = _taxTrans.SourceBaseAmountCur * _factor;
        this.InvoiceAmount                      = _taxTrans.InvoiceAmount * _factor;
        this.TransactionAmount                  = _taxTrans.TransactionAmount * _factor;
        this.TaxAmount                          = _taxTrans.TaxAmount * _factor;
        this.TaxAmountSecondary                 = _taxTrans.TaxAmountSecondary * _factor;
        this.ExpenseAmount                      = _taxTrans.ExpenseAmount * _factor;
        this.rg23dcenvatCreditTransferredAmount = _taxTrans.rg23dcenvatCreditTransferredAmount * _factor;
        this.rg23dcenvatCreditAmount            = _taxTrans.rg23dcenvatCreditAmount * _factor;
        this.rg23cDeferredAmount                = _taxTrans.rg23cDeferredAmount * _factor;
        this.rg23aDeferredAmount                = _taxTrans.rg23aDeferredAmount * _factor;
        this.plaAmount                          = _taxTrans.plaAmount * _factor;
        this.CreditAmount                       = _taxTrans.CreditAmount * _factor;
        this.DebitAmount                        = _taxTrans.DebitAmount * _factor;
        this.JournalInvoiceAmount               = _taxTrans.JournalInvoiceAmount * _factor;
        this.TaxOrigin                          = _taxTrans.TaxOrigin * _factor;
        this.ReverseChargePercentage_IN         = taxTransW.ServiceTaxReverseChargePercentage_IN;
    }
}

/// <summary>
/// Initialize from tax work transaction.
/// </summary>
/// <param name="_tmpTaxWorkTrans">
/// The temporary work transaction.
/// </param>
/// <param name="_taxTrans">
/// The tax transaction.
/// </param>
/// <param name="_factor">
/// The factor.
/// </param>
public void initFromTaxWorkTrans(
    TmpTaxWorkTrans   _tmpTaxWorkTrans,
    TaxTrans          _taxTrans,
    Factor            _factor =  1)
{
    TaxTrans_IN taxTransLoc;
    TaxTrans    taxTrans;

    if (TaxTable::find(_tmpTaxWorkTrans.TaxCode).TaxType_IN != TaxType_IN::None)
    {
        select RecId from taxTrans
            order by RecId
                where taxTrans.Voucher       == _tmpTaxWorkTrans.Voucher
                    && taxTrans.TransDate    == _tmpTaxWorkTrans.TransDate
                    && taxTrans.TaxCode      == _tmpTaxWorkTrans.TaxCode
                    && taxTrans.TaxGroup     == _tmpTaxWorkTrans.TaxGroup
                    && taxTrans.TaxItemGroup == _tmpTaxWorkTrans.TaxItemGroup
                join * from taxTransLoc
                   where taxTransLoc.RefRecId == taxTrans.RecId;
        if (taxTransLoc.RecId)
        {
            this.data(taxTransLoc);
            this.RefRecId                           = _taxTrans.RecId;
            this.LoadOnInventoryAmount              = taxTransLoc.LoadOnInventoryAmount * _factor;
            this.PostedTaxAmout                     = taxTransLoc.PostedTaxAmout * _factor;
            this.NonRecoverableAmt                  = taxTransLoc.NonRecoverableAmt * _factor;
            this.RecoverableAmount                  = taxTransLoc.RecoverableAmount * _factor;
            this.PayableAmount                      = taxTransLoc.PayableAmount * _factor;
            this.DeferredAmount                     = taxTransLoc.DeferredAmount * _factor;
            this.AbatementAmount                    = taxTransLoc.AbatementAmount * _factor;
            this.MiscChargeAmount                   = taxTransLoc.MiscChargeAmount * _factor;
            this.RetentionAmount                    = taxTransLoc.RetentionAmount * _factor;
            this.SourceBaseAmountCurRegulated       = taxTransLoc.SourceBaseAmountCurRegulated * _factor;
            this.AssessableValue                    = taxTransLoc.AssessableValue * _factor;
            this.CustomsCIFAmount                   = taxTransLoc.CustomsCIFAmount * _factor;
            this.SourceRegulateAmountCur            = taxTransLoc.SourceRegulateAmountCur * _factor;
            this.CustomsTaxRegulationAmountCur      = taxTransLoc.CustomsTaxRegulationAmountCur * _factor;
            this.CustomsFOBAmount                   = taxTransLoc.CustomsFOBAmount * _factor;
            this.CustomsLandingChargesAmount        = taxTransLoc.CustomsLandingChargesAmount * _factor;
            this.CustomsPreferentialValue           = taxTransLoc.CustomsPreferentialValue * _factor;
            this.SourceTaxAmountCur                 = taxTransLoc.SourceTaxAmountCur * _factor;
            this.SourceBaseAmountCur                = taxTransLoc.SourceBaseAmountCur * _factor;
            this.InvoiceAmount                      = taxTransLoc.InvoiceAmount * _factor;
            this.TransactionAmount                  = taxTransLoc.TransactionAmount * _factor;
            this.TaxAmount                          = taxTransLoc.TaxAmount * _factor;
            this.TaxAmountSecondary                 = taxTransLoc.TaxAmountSecondary * _factor;
            this.ExpenseAmount                      = taxTransLoc.ExpenseAmount * _factor;
            this.rg23dcenvatCreditTransferredAmount = taxTransLoc.rg23dcenvatCreditTransferredAmount * _factor;
            this.rg23dcenvatCreditAmount            = taxTransLoc.rg23dcenvatCreditAmount * _factor;
            this.rg23cDeferredAmount                = taxTransLoc.rg23cDeferredAmount * _factor;
            this.rg23aDeferredAmount                = taxTransLoc.rg23aDeferredAmount * _factor;
            this.plaAmount                          = taxTransLoc.plaAmount * _factor;
            this.CreditAmount                       = taxTransLoc.CreditAmount * _factor;
            this.DebitAmount                        = taxTransLoc.DebitAmount * _factor;
            this.JournalInvoiceAmount               = taxTransLoc.JournalInvoiceAmount * _factor;
            this.TaxOrigin                          = taxTransLoc.TaxOrigin * _factor;
            this.ReverseChargePercentage_IN         = taxTransLoc.ReverseChargePercentage_IN;
        }
    }
}

