{
    //Init the taxReportCodeNode reference field
    TaxReportSetoffHrchySetupHelper_IN::updateTaxReportHierarchyNodeId(this);

    super();
}

/// <summary>
/// This method is used to insert info into the <c>TaxTrans_IN</c> table from <c>PurchLine</c> table.
/// </summary>
/// <param name="_purchLine">
/// A buffer of <c>PurchLine</c> table.
/// </param>
/// <param name="_inventTransId">
/// The inventory transaction id.
/// </param>
public void insertFromPurchLine(
    PurchLine       _purchLine,
    InventTransId   _inventTransId)
{
    TransTaxInformation  transTaxInformation;

    transTaxInformation = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(_purchLine);

    this.ExciseTariffCodes      = transTaxInformation.ExciseTariffCodes;
    this.ExciseRecordType       = transTaxInformation.ExciseRecordType;
    this.TaxItemGroup           = _purchLine.TaxItemGroup;
    this.TaxGroup               = _purchLine.TaxGroup;
    this.VoucherCurrency        = _purchLine.CurrencyCode;
    this.MiscChargeAmount       = MarkupTrans::findPurchLine_IN(_purchLine.TableId, _purchLine.RecId).Value;
    this.RegistrationNumberType = transTaxInformation.ExciseType;
    if (this.RegistrationNumberType != ExciseType_IN::None)
    {
        this.CompanyRegistrationNumber = transTaxInformation.ExciseECCRegistrationNumber;
    }
}

/// <summary>
/// This method is used to insert info into the <c>TaxTrans_IN</c> table from <c>SalesLine</c> table.
/// </summary>
/// <param name="_salesLine">
/// A buffer of <c>SalesLine</c> table.
/// </param>
/// <param name="_inventTransId">
/// The inventory transaction id.
/// </param>
public void insertFromSalesLine(
    SalesLine       _salesLine,
    InventTransId   _inventTransId)
{
    TransTaxInformation  transTaxInformation;

    transTaxInformation = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(_salesLine);

    this.ExciseTariffCodes      = transTaxInformation.ExciseTariffCodes;
    this.ExciseRecordType       = transTaxInformation.ExciseRecordType;
    this.TaxItemGroup           = _salesLine.TaxItemGroup;
    this.TaxGroup               = _salesLine.TaxGroup;
    this.VoucherCurrency        = _salesLine.CurrencyCode;
    this.SalesOrderDate         = DateTimeUtil::date(_salesLine.CreatedDateTime);
    this.SalesOrderQty          = _salesLine.SalesQty;
    this.RegistrationNumberType = transTaxInformation.ExciseType;

    if (this.RegistrationNumberType != ExciseType_IN::None)
    {
        this.CompanyRegistrationNumber = transTaxInformation.ExciseECCRegistrationNumber;
    }
}

/// <summary>
/// This method is used to insert info into the <c>TaxTrans_IN</c> table from <c>InventDim</c> table.
/// </summary>
/// <param name="_inventDim">
/// A buffer of <c>InventDim</c> table.
/// </param>
public void insertInventDetails(InventDim   _inventDim)
{
    #InventDimDevelop
    InventProductDimension::initTableFromInventDim(this, _inventDim);
    this.BatchNumber       =  _inventDim.InventBatchId;
    this.Location          =  _inventDim.InventLocationId;
    this.SerialNumber      =  _inventDim.InventSerialId;
    this.Warehouse         =  _inventDim.InventLocationId;
}

/// <summary>
/// This method is used to insert the stock transfer details into the <c>TaxTrans_IN</c> table.
/// </summary>
/// <param name="_inventTransLine">
/// A buffer of <c>InventTransferLine</c> table.
/// </param>
/// <param name="_shipmentDate">
/// A shipment date.
/// </param>
/// <param name="_itemUnitPrice">
/// A item unit price.
/// </param>
/// <param name="_qty">
/// A quantity of the item to be shipped.
/// </param>
/// <param name="_retentionAcc">
/// The ledger dimension account.
/// </param>
/// <param name="_unitPrice">
/// A unit price.
/// </param>
/// <param name="_retentionAmount">
/// A retention amount.
/// </param>
/// <param name="_voucherId">
/// A voucher ID.
/// </param>
/// <param name ="_taxTrans">
/// The record ID of the <c>TaxTrans</c> record.
/// </param>
public void insertStockTransferDetails( InventTransferLine              _inventTransLine,
                                        TransDate                       _shipmentDate,
                                        real                            _itemUnitPrice,
                                        InventTransferQtyShipped        _qty,
                                        LedgerDimensionDefaultAccount   _retentionAcc,
                                        UnitPrice_IN                    _unitPrice,
                                        NetAmount_IN                    _retentionAmount,
                                        InventTransferVoucherId         _voucherId,
                                        RefRecId                        _taxTrans)
{
    TransTaxInformation transTaxInformation = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(_inventTransLine);
    InventTransferLine_IN inventTransferLineIN = _inventTransLine.inventTransferLine_IN();
    TaxTable    taxTable = TaxTable::find(inventTransferLineIN.vatRetentionCode);

    this.SourceRecId                = _inventTransLine.RecId;
    this.SourceTableId              = tablenum(InventTransferLine);
    this.InventTransId              = _inventTransLine.InventTransId;
    this.RetentionPct               = inventTransferLineIN.Retention;
    this.RetentionAmount            = _retentionAmount;
    this.TaxPeriod                  = taxTable.TaxPeriod;
    this.Voucher                    = _voucherId;
    this.TaxType                    = taxTable.TaxType_IN;
    this.CompanyRegistrationNumber  = transTaxInformation.VATTIN;
    this.TransDate                  = _shipmentDate;
    this.TaxCode                    = inventTransferLineIN.vatRetentionCode;
    this.VoucherCurrency            = inventTransferLineIN.CurrencyCode;
    this.SourceBaseAmountCur        = -(_unitPrice * _qty);
    this.ItemId                     = _inventTransLine.ItemId;
    this.Unit                       = _inventTransLine.UnitId;
    this.TransactionAmount          = _qty * _itemUnitPrice;
    this.Quantity                   = _qty;
    this.TransferOrderNumber        = _inventTransLine.TransferId;
    this.PriceType                  = inventTransferLineIN.vatPriceType;
    this.Source                     = TaxModuleType::StockTransfer_IN;
    this.TaxComponentTable          = taxTable.TaxComponentTable_IN;
    this.SourceCurrencyCode         = Ledger::accountingCurrency();
    this.LineNum                    = _inventTransLine.LineNum;
    this.RecoverableLedgerDimension = _retentionAcc;
    this.RefRecId                   = _taxTrans;
    this.insert();
}

/// <summary>
/// Gets the next not selected field name from available field list.
/// </summary>
/// <param name="_availableNumber">
/// The available number is used to find record.
/// </param>
/// <param name="_availableFieldIdList">
/// The available filed id list is used to find reord in it.
/// </param>
/// <returns>
/// The filed name if the name exist.
/// </returns>
public fieldName nextNotSelected(
    int         _availableNumber,
    container   _availableFieldIdList)
{
    fieldName fieldName;

    _availableNumber ++;

    if (_availableNumber <= conlen(_availableFieldIdList))
    {
        fieldName = conpeek(_availableFieldIdList, _availableNumber);
    }

    return fieldName;
}

/// <summary>
/// Gets the next selected field name from selected field list.
/// </summary>
/// <param name="_selectedNumber">
/// The selected number is used to find record.
/// </param>
/// <param name="_selectedFieldIdList">
/// The selected filed id list is used to find reord in it.
/// </param>
/// <returns>
/// The filed name if the name exist.
/// </returns>
public fieldName nextSelected(
    int         _selectedNumber,
    container   _selectedFieldIdList)
{
    fieldName fieldName;

    _selectedNumber ++;

    if (_selectedNumber <= conlen(_selectedFieldIdList))
    {
        fieldName = conpeek(_selectedFieldIdList, _selectedNumber);
    }

    return fieldName;
}

/// <summary>
/// Gets the vendor registration number for Excise tax from <c>TaxRegistrationNumbers_IN</c> table using CustVendRegistrationNumber field.
/// </summary>
/// <returns>
/// returns vendor registration number for Excise tax.
/// </returns>
display VendorEccNumber_IN vendorECCNumber()
{
    VendorEccNumber_IN      vendorEccNumber;
    SecurityRights          sr;
    SecurityTableRights     stRights;
    AccessRight             ar;

    sr = SecurityRights::construct();
    stRights = sr.tableFieldAccessRights(tableStr(TaxTrans_IN));
    ar = stRights.fieldAccessRight(fieldStr(TaxTrans_IN,CustVendRegistrationNumber));

    if (ar != AccessRight::NoAccess)
    {
        if (this.TaxType == TaxType_IN::Excise && this.Vendor)
        {
            vendorEccNumber = TaxRegistrationNumbers_IN::find(this.CustVendRegistrationNumber).RegistrationNumber;
        }
    }
    return vendorEccNumber;
}

/// <summary>
/// Gets the vendor name of the tax trans.
/// </summary>
/// <returns>
/// The vendor name information.
/// </returns>
display VendorName_IN vendorName()
{
    return DirPartyTable::findRec(VendTable::find(this.Vendor).Party).Name;
}

