/// <summary>
/// Checks whether <c>TaxTrans_IN</c> record exists or not for specified parameter.
/// </summary>
/// <param name="_refRecId">
/// The reference record id is used to find record.
/// </param>
/// <returns>
/// Returns true if record exists otherwise false.
/// </returns>
public static boolean exist(RefRecId   _refRecId)
{
    return _refRecId &&
           (select firstonly RecId from taxTrans_IN
                where taxTrans_IN.RefRecId  == _refRecId).RecId != 0;
}

/// <summary>
/// Checks if series number exist.
/// </summary>
/// <param name="_sourceTableId">
/// The source table id.
/// </param>
/// <param name="_sourceRecId">
/// The source RecId.
/// </param>
/// <param name="_voucher">
/// The voucher.
/// </param>
/// <returns>
/// Return 'True' if series number exist.
/// </returns>
public static boolean existSeriesNumber(
    tableId    _sourceTableId,
    recId      _sourceRecId,
    Voucher    _voucher)
{
    return _sourceTableId &&
           _sourceRecId &&
           (select firstonly RecId from taxTrans_IN
                where taxTrans_IN.SourceTableId         == _sourceTableId       &&
                      taxTrans_IN.SourceRecId           == _sourceRecId         &&
                      taxTrans_IN.Voucher               == _voucher             &&
                      (taxTrans_IN.CompanyFormNumber    != ''                   ||
                      taxTrans_IN.CompanyFormSeries     != ''                   ||
                      taxTrans_IN.CustVendFormNumber    != ''                   ||
                      taxTrans_IN.CustVendFormSeries    != '')).RecId != 0;
}

/// <summary>
/// Checks whether the given filed name is enabled.
/// </summary>
/// <param name="_fieldName">
/// The field name is used to be checked.
/// </param>
/// <returns>
/// Returns true if this field is enabled.
/// </returns>
public static boolean fieldEnabled(fieldName _fieldName)
{
    boolean                 enabled                 = true;
    DictField               dictField               = new DictField(tablenum(TaxTrans_IN), fieldname2id(tablenum(TaxTrans_IN),_fieldName));
    DictConfigurationKey    dictConfigurationKey    = new DictConfigurationKey(dictField.configurationKeyId());

    if (dictConfigurationKey)
    {
        enabled = dictConfigurationKey.enabled();
    }

    return enabled;
}

/// <summary>
/// Gets the field name for given parameter.
/// </summary>
/// <param name="_fieldName">
/// The field name is used to return name for it.
/// </param>
/// <returns>
/// The actual field name.
/// </returns>
public static fieldLabel fieldName(fieldName _fieldName)
{
    DictField  dictField;
    DictMethod dictMethod;
    DictType   dictType;
    fieldLabel name;

    dictField = new DictField(tablenum(TaxTrans_IN), fieldname2id(tablenum(TaxTrans_IN), _fieldName));
    if (dictField)
    {
        name = dictField.label();
    }
    else
    {
        dictMethod = new DictMethod(UtilElementType::TableInstanceMethod, tablenum(TaxTrans_IN), _fieldName);
        if (dictMethod.returnType() == Types::UserType)
        {
            dictType = new DictType(dictMethod.returnId());
            name = dictType.label();
        }
    }

    return name;
}

/// <summary>
/// Finds the specified record in the <c>TaxTrans_IN</c> table.
/// </summary>
/// <param name="_refRecId">
/// The reference record id is used to find record.
/// </param>
/// <returns>
/// A record in the <c>TaxTrans_IN</c> table.
/// </returns>
public static TaxTrans_IN  find(RefRecId   _refRecId)
{
    TaxTrans_IN  taxTrans;

    if (_refRecId)
    {
        select firstonly taxTrans
            where taxTrans.RefRecId     == _refRecId;
    }

    return taxTrans;
}

/// <summary>
/// Finds the specified record in the <c>TaxTrans_IN</c> table.
/// </summary>
/// <param name="_recId">
/// The record id is used to find record.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that specifies whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>TaxTrans_IN</c> table.
/// </returns>
public static TaxTrans_IN findRecId(
    recId       _recId,
    boolean     _forUpdate = false)
{
    TaxTrans_IN taxTrans;

    if (_recId)
    {
        taxTrans.selectForUpdate(_forUpdate);

        select firstonly taxTrans
            where taxTrans.RecId  == _recId;
    }
    return taxTrans;
}

/// <summary>
/// Finds the specified record in the <c>TaxTrans_IN</c> table.
/// </summary>
/// <param name="_recId">
/// The reference record id is used to find record.
/// </param>
/// <param name="_forUpdate">
/// A Boolean value that specifies whether to read the record for update; optional.
/// </param>
/// <returns>
/// A record in the <c>TaxTrans_IN</c> table.
/// </returns>
static TaxTrans_IN findRefRecId(
    RefRecId    _recId,
    boolean     _forUpdate = false)
{
    TaxTrans_IN taxTrans;

    if (_recId)
    {
        taxTrans.selectForUpdate(_forUpdate);

        select firstonly taxTrans
            where taxTrans.RefRecId  == _recId;
    }

    return taxTrans;
}

/// <summary>
/// Gets the field values of the tax transaction.
/// </summary>
/// <param name="_taxTrans">
/// The tax transaction.
/// </param>
/// <param name="_fieldName">
/// The field name information.
/// </param>
/// <returns>
/// The field value.
/// </returns>
public static CCStatSort_IN getFieldValues(
    TaxTrans_IN _taxTrans,
    fieldName   _fieldName)
{
    DictField     dictField;
    DictTable     dictTable;
    CCStatSort_IN fieldValue;
    ExecutePermission executePermission;
    SysDictType   dictType;

    dictField = new DictField(tablenum(TaxTrans_IN), fieldname2id(tablenum(TaxTrans_IN), _fieldName));

    dictType = new SysDictType(extendedtypenum(TaxValue));

    if (dictField && dictField.baseType() == Types::Int64)
    {
        fieldValue = TaxTrans_IN::getReferencesFieldValues(fieldname2id(tablenum(TaxTrans_IN), _fieldName), _taxTrans);
    }
    else
    {
        if (dictField)
        {
            if (_fieldName == fieldstr(TaxTrans_IN, CustomsPreferentialValue)
                || _fieldName == fieldstr(TaxTrans_IN, TaxValue))
            {
                // using strfmt decimal places are not displayed properly.
                fieldValue = num2Str0(_taxTrans.(fieldname2id(tablenum(TaxTrans_IN), _fieldName)), 1, dictType.noOfDecimals());
            }
            else
            {
                fieldValue = strfmt('%1', _taxTrans.(fieldname2id(tablenum(TaxTrans_IN), _fieldName)));
            }
        }
        else
        {
            dictTable = new DictTable(_taxTrans.TableId);

            if (dictTable && _fieldName && _fieldName!= '0')
            {
                executePermission = new ExecutePermission();
                executePermission.assert();
                fieldValue = strfmt('%1', dictTable.callObject(_fieldName, _taxTrans));
            }
        }
    }

    return fieldValue;
}

/// <summary>
/// This method is used to fetch the corresponding string value of Recid fields for tax transactions related data.
/// </summary>
/// <param name="fieldId">
/// The fieldid of corresponding field of TaxTrans_IN
/// </param>
/// <param name="_taxTrans">
/// The _taxTrans buffer that contains the SalesTaxInquiryView_IN data
/// </param>
/// <returns>
/// String name value of the field.
/// </returns>
public static CCStatSort_IN getReferencesFieldValues(fieldId fieldId, TaxTrans_IN _taxTrans)
{
    CCStatSort_IN  name;

    switch (fieldId)
    {
        case fieldnum(TaxTrans_IN, CustomsBillOfEntryNumberTable) :
            name = CustomsBillOfEntryNumberTable_IN::find(_taxTrans.CustomsBillOfEntryNumberTable).BillOfEntryNumber;
            break;

        case fieldnum(TaxTrans_IN, ExportDutyExpenseLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.ExportDutyExpenseLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, ExportDutyPayLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.ExportDutyPayLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, CustomsImportInvoiceNumberTable) :
            name = CustomsImportInvoiceNumberTable_IN::find(_taxTrans.CustomsImportInvoiceNumberTable).ImporterInvoiceNumber;
            break;

        case fieldnum(TaxTrans_IN, ImportDutyPayLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.ImportDutyPayLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, ImportDutyExpenseLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.ImportDutyExpenseLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, CustomsShippingBillNumberTable) :
            name = CustomsShippingBillNumberTable_IN::find(_taxTrans.CustomsShippingBillNumberTable).ShippingBillNumber;
            break;

        case fieldnum(TaxTrans_IN, TaxLedgerAccountGroup) :
            name = TaxLedgerAccountGroup_IN::find(_taxTrans.TaxLedgerAccountGroup).TaxAccountGroup;
            break;

        case fieldnum(TaxTrans_IN, TaxComponentTable) :
            name = TaxComponentTable_IN::find(_taxTrans.TaxComponentTable).Component;
            break;

        case fieldnum(TaxTrans_IN, ExpenseLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.ExpenseLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, InterimRecoverableLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.InterimRecoverableLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, InterimPayableLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.InterimPayableLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, ServicePayableLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.ServicePayableLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, ServiceRecoverableLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.ServiceRecoverableLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, ServiceCodeTable) :
            name = ServiceCodeTable_IN::find(_taxTrans.ServiceCodeTable).ServiceCode;
            break;

        case fieldnum(TaxTrans_IN, LedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.LedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, OffsetLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.OffsetLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, plaLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.plaLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, InterimPLALedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.InterimPLALedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, PayableLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.PayableLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, RecoverableLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.RecoverableLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, rg23aDeferredLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.rg23aDeferredLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, rg23cDeferredLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.rg23cDeferredLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, rg23dcenvatCreditLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.rg23dcenvatCreditLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, rg23dcenvatCreditTransLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.rg23dcenvatCreditTransLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, SalesTaxFormTypes) :
            name = SalesTaxFormTypes_IN::find(_taxTrans.SalesTaxFormTypes).FormType;
            break;

        case fieldnum(TaxTrans_IN, DeferredLedgerDimension) :
            name = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_taxTrans.DeferredLedgerDimension);
            break;

        case fieldnum(TaxTrans_IN, CustVendRegistrationNumber) :
            name = TaxRegistrationNumbers_IN::find(_taxTrans.CustVendRegistrationNumber).RegistrationNumber;
            break;

        case fieldnum(TaxTrans_IN, ExciseTariffCodes) :
            name = ExciseTariffCodes_IN::find(_taxTrans.ExciseTariffCodes).TariffCode;
            break;

        case fieldnum(TaxTrans_IN, CustomsTariffCodeTable) :
            name = CustomsTariffCodeTable_IN::find(_taxTrans.CustomsTariffCodeTable).TariffCode;
            break;
        case fieldnum(TaxTrans_IN, vatScheduleName) :
            name = VATSchedule_IN::find(_taxTrans.vatScheduleName).Schedule;
            break;
        case fieldnum(TaxTrans_IN, CompanyRegistrationNumber) :
            name = TaxRegistrationNumbers_IN::find(_taxTrans.CompanyRegistrationNumber).RegistrationNumber;
            break;
        case fieldnum(TaxTrans_IN, CustVendRegistrationNumber) :
            name = TaxRegistrationNumbers_IN::find(_taxTrans.CustVendRegistrationNumber).RegistrationNumber;
            break;
        case fieldNum(TaxTrans_IN, CustomerLocation):
            name = LogisticsLocation::find(_taxTrans.CustomerLocation).LocationId;
            break;
        case fieldNum(TaxTrans_IN, CustomerTaxInformation):
            name = TaxInformation_IN::find(_taxTrans.CustomerTaxInformation).Name;
            break;
        case fieldNum(TaxTrans_IN, HSNCodeTable):
            name = HSNCodeTable_IN::find(_taxTrans.HSNCodeTable).Code;
            break;
        case fieldNum(TaxTrans_IN, ServiceAccountingCodeTable):
            name = ServiceAccountingCodeTable_IN::find(_taxTrans.ServiceAccountingCodeTable).SAC;
            break;
        case fieldNum(TaxTrans_IN, vendorLocation):
            name = LogisticsLocation::find(_taxTrans.VendorLocation).LocationId;
            break;
        case fieldNum(TaxTrans_IN, vendorTaxInformation):
            name = TaxInformation_IN::find(_taxTrans.VendorTaxInformation).Name;
            break;
    }

    return name;
}

/// <summary>
/// This method is used to insert info into the <c>TaxTrans_IN</c> table from given parameters.
/// </summary>
/// <param name="_exciseDSARegister">
/// A buffer of <c>ExciseDSARegister_IN</c> table;
/// </param>
/// <param name="_common">
/// A buffer of table which is given from user.
/// </param>
/// <param name="_taxPeriod">
/// The tax period; optional.
/// </param>
public static void insertIntoTaxTransDSA(
    ExciseDSARegister_IN       _exciseDSARegister,
    Common                     _common,
    TaxPeriod                  _taxPeriod = '')
{
    CompanyInfo                         companyInfoLoc;
    TaxTrans_IN                         taxTrans;
    TaxTrans                            taxTransLoc;
    InventTable                         inventTable;
    InventTrans                         inventTrans;
    InventTransOrigin                   inventTransOrigin;
    ProdTable                           prodTable;
    CustInvoiceJour                     custInvoiceJour;
    CustInvoiceTrans                    custInvoiceTrans;
    CustInvoiceTrans_IN                 custInvoiceTrans_IN;
    InventJournalTrans                  inventJournalTrans;
    InventTransferLine                  inventTransferLine;
    CustTable                           custTable;
    SalesLine                           salesLine;
    MarkupTrans                         markupTrans;
    WMSOrderTrans                       wmsOrderTrans;
    CustQuotationJour                   custQuotationJour;
    SalesQuotationLine                  salesQuotationLine;
    SalesQuotationLine                  salesQuotationLineLoc;
    CustQuotationTrans                  custQuotationTrans;
    CustPackingSlipJour                 custPackingSlipJour;
    CustPackingSlipTrans                custPackingSlipTrans;
    SalesLine                           salesLineLoc;
    TaxInformation_IN                   taxInformation;
    TransTaxInformation                 transTaxInformation;
    TransTaxInformation                 transTaxInformationLoc;

    ttsbegin;
    companyInfoLoc                                      = CompanyInfo::find();
    taxTrans.dsaIdDate                                  = _exciseDSARegister.dsaDate;
    taxTrans.CompanyRegistrationNumber                  = _exciseDSARegister.TaxRegistrationNumberTable;
    taxTrans.dsaIdNumberSequenceCode                    = _exciseDSARegister.NumberSequence;
    taxTrans.InventTransId                              = _exciseDSARegister.InventTransId;
    taxTrans.ItemId                                     = _exciseDSARegister.Item;
    taxTrans.ExciseRecordType                           = _exciseDSARegister.ExciseRecordType;
    taxTrans.Quantity                                   = _exciseDSARegister.IssueQty ? _exciseDSARegister.IssueQty : _exciseDSARegister.ManufacturedQty;
    taxTrans.dsaQuantity                                = _exciseDSARegister.IssueQty ? _exciseDSARegister.IssueQty : _exciseDSARegister.ManufacturedQty;
    taxTrans.SourceTaxAmountCur                         = _exciseDSARegister.TaxAmount;
    switch (_common.TableId)
    {
        case tablenum(InventTrans):
            inventTrans                                 = _common;
            inventTable                                 = InventTable::find(inventTrans.ItemId);
            inventTransOrigin                           = InventTransOrigin::find(inventTrans.InventTransOrigin);
            switch (inventTransOrigin.ReferenceCategory)
            {
                case InventTransType::Production:
                    prodTable                                   = ProdTable::find(inventTransOrigin.ReferenceId);
                    taxTrans.ExciseTariffCodes                  = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(prodTable).ExciseTariffCodes;
                    taxTrans.Source                             = TaxModuleType::ProducationOrder_IN;
                    break;
                case InventTransType::PmfProdCoBy:
                    taxTrans.ExciseTariffCodes                  = inventTable.ExciseTariffCodes_IN;
                    taxTrans.Source                             = TaxModuleType::ProducationOrder_IN;
                    break;
                case InventTransType::BOMMain:
                    inventJournalTrans                          = InventJournalTrans::findInventTransId(inventTransOrigin.InventTransId);
                    transTaxInformation                         = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(inventJournalTrans);
                    taxTrans.ExciseTariffCodes                  = transTaxInformation.ExciseTariffCodes;
                    taxTrans.Source                             = TaxModuleType::Inventory_IN;
                    break;
                default:
                    taxTrans.ExciseTariffCodes                  = inventTable.ExciseTariffCodes_IN;
                    break;
            }
            taxTrans.Voucher                            = inventTrans.VoucherPhysical;
            taxTrans.TransDate                          = taxTrans.dsaIdDate;
            taxTrans.CommissionarateCompanyInformation  = TaxInformation_IN::findDefaultByLocation(DirParty::primaryPostalAddressLocationRecId(companyInfoLoc.RecId)).Commissionarate;
            taxTrans.ProductionOrderDate                = inventTrans.DatePhysical;
            taxTrans.ProductionOrderNumber              = InventTransOrigin::find(inventTrans.InventTransOrigin).ReferenceId;
            taxTrans.ProductionOrderQty                 = taxTrans.Quantity;
            taxTrans.ManufacturedQuantity               = taxTrans.Quantity;
            taxTrans.insertInventDetails(inventTrans.inventDim());
            break;
        case tablenum(CustInvoiceTrans):
            custInvoiceTrans                            = _common;
            custInvoiceJour                             = CustInvoiceJour::findFromCustInvoiceTrans(custInvoiceTrans.SalesId,
                                                                                                    custInvoiceTrans.InvoiceId,
                                                                                                    custInvoiceTrans.InvoiceDate,
                                                                                                    custInvoiceTrans.NumberSequenceGroup);
            custTable                                   = CustTable::find(custInvoiceJour.OrderAccount);
            taxInformation = TaxInformation_IN::findDefaultByLocation(DirParty::primaryPostalAddressLocationRecId(custTable.Party));
            salesLine                                   = SalesLine::findInventTransId(custInvoiceTrans.InventTransId);
            transTaxInformation                         = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(salesLine);
            custInvoiceTrans_IN                         = custInvoiceTrans.custInvoiceTrans_IN();
            taxTrans.ExciseTariffCodes                  = custInvoiceTrans_IN.ExciseTariffCodes_IN;
            taxTrans.SalesInvoiceDate                   = custInvoiceJour.InvoiceDate;
            taxTrans.InvoiceId                          = custInvoiceJour.InvoiceId;
            taxTrans.InvoiceAmount                      = custInvoiceJour.InvoiceAmount;
            taxTrans.InvoiceAccount                     = custInvoiceJour.InvoiceAccount;
            taxTrans.AssessableValue                    = custInvoiceTrans_IN.AssessableValue_IN;
            taxTrans.CommissionarateCustomerMaster      = taxInformation.Commissionarate;
            taxTrans.DivisionVendorMaster               = taxInformation.Division;
            taxTrans.Customer                           = custTable.AccountNum;
            taxTrans.CustomerAddress                    = LogisticsPostalAddress::findByLocation(taxInformation.RegistrationLocation).Address;
            if (transTaxInformation.ExciseType !=  ExciseType_IN::None)
            {
                taxTrans.CustVendRegistrationNumber = (transTaxInformation.ExciseType == ExciseType_IN::Manufacturer)
                                                        ? taxInformation.ManECCRegistrationNumberTable
                                                        : taxInformation.TraderECCRegistrationNumberTable;
            }
            taxTrans.RegistrationNumberType             = transTaxInformation.ExciseType;
            taxTrans.RangeCustomerMaster                = taxInformation.Range;
            taxTrans.Voucher                            = custInvoiceJour.LedgerVoucher;
            taxTrans.TransDate                          = custInvoiceJour.InvoiceDate;
            taxTrans.Source                             = TaxModuleType::SalesInvoice;

            select count(RecId) from wmsOrderTrans
                where wmsOrderTrans.InventTransId == salesLine.InventTransId;
            if (wmsOrderTrans.RecId == 1)
            {
                taxTrans.PickingListDate            = wmsOrderTrans.DlvDate;
                taxTrans.PickingListNumber          = wmsOrderTrans.RouteId;
                taxTrans.PickingListQuantity        = wmsOrderTrans.Qty;
            }

            taxTrans.TaxItemGroup                       = custInvoiceJour.TaxItemGroup;
            taxTrans.Unit                               = custInvoiceTrans.SalesUnit;
            select sum(Value) from markupTrans
                where markupTrans.TransTableId  == custInvoiceTrans.TableId
                   && markupTrans.TransRecId    == custInvoiceTrans.RecId;

            taxTrans.MiscChargeAmount                   = markupTrans.Value;
            taxTrans.insertFromSalesLine(salesLine, taxTrans.InventTransId);
            taxTrans.insertInventDetails(InventDim::find(salesLine.InventDimId));
            break;
        case tablenum(CustQuotationTrans):
            custQuotationTrans                          = _common;
            custQuotationJour                           = CustQuotationJour::findFromCustQuotationTrans(custQuotationTrans.SalesId,
                                                                                                        custQuotationTrans.QuotationId,
                                                                                                        custQuotationTrans.QuotationDate);
            custTable                                   = CustTable::find(custQuotationJour.OrderAccount);
            taxInformation = TaxInformation_IN::findDefaultByLocation(DirParty::primaryPostalAddressLocationRecId(custTable.Party));
            salesQuotationLine                          = SalesQuotationLine::findInventTransId(custQuotationTrans.InventTransId);
            transTaxInformation                         = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(salesQuotationLine);
            taxTrans.SalesInvoiceDate                   = custQuotationJour.QuotationDate;
            taxTrans.SalesOrderDate                     = DateTimeUtil::date(salesQuotationLine.CreatedDateTime);
            taxTrans.InvoiceId                          = custQuotationJour.QuotationId;
            taxTrans.InvoiceAccount                     = custQuotationJour.InvoiceAccount;
            taxTrans.InvoiceAmount                      = custQuotationJour.QuotationAmount;
            taxTrans.AssessableValue                    = SalesQuotationLineTaxExtensionIN::findBysalesQuotationLine(salesQuotationLine.RecId, true).AssessableValueTransactionCurrency;
            taxTrans.CommissionarateCustomerMaster      = taxInformation.Commissionarate;
            taxTrans.DivisionVendorMaster               = taxInformation.Division;
            taxTrans.Customer                           = custTable.AccountNum;
            taxTrans.CustomerAddress                    = LogisticsPostalAddress::findByLocation(taxInformation.RegistrationLocation).Address;
            if (transTaxInformation.ExciseType !=  ExciseType_IN::None)
            {
                taxTrans.CustVendRegistrationNumber = (transTaxInformation.ExciseType ==  ExciseType_IN::Manufacturer)
                                                        ? taxInformation.ManECCRegistrationNumberTable
                                                        : taxInformation.TraderECCRegistrationNumberTable;
            }
            taxTrans.RegistrationNumberType             = transTaxInformation.ExciseType;
            taxTrans.RangeCustomerMaster                = taxInformation.Range;
            taxTrans.Voucher                            = custQuotationJour.QuotationDocNum;
            taxTrans.TransDate                          = custQuotationJour.QuotationDate;
            taxTrans.Source                             = TaxModuleType::SalesInvoice;
            taxTrans.VoucherCurrency                    = salesQuotationLine.CurrencyCode;
            taxTrans.TaxItemGroup                       = salesQuotationLine.TaxItemGroup;
            taxTrans.TaxGroup                           = salesQuotationLine.TaxGroup;
            taxTrans.insertInventDetails(InventDim::find(salesQuotationLine.InventDimId));

            select sum (SalesQty) from salesQuotationLineLoc
                where salesQuotationLineLoc.QuotationId == salesQuotationLine.QuotationId;

            taxTrans.SalesOrderQty                      = salesQuotationLineLoc.SalesQty;

            select firstonly LineAmount, SalesUnit, TableId, RecId
                from custQuotationTrans
                where custQuotationTrans.SalesId        == custQuotationJour.SalesId
                   && custQuotationTrans.QuotationId    == custQuotationJour.QuotationId
                   && custQuotationTrans.QuotationDate  == custQuotationJour.QuotationDate;

            taxTrans.TransactionAmount                  = custQuotationTrans.LineAmount;
            taxTrans.Unit                               = custQuotationTrans.SalesUnit;
            select sum (Value) from markupTrans
                where markupTrans.TransTableId  == custQuotationTrans.TableId
                   && markupTrans.TransRecId    == custQuotationTrans.RecId;
            taxTrans.MiscChargeAmount                   = markupTrans.Value;
            break;
        case tablenum(InventJournalTrans):
            inventJournalTrans                          = _common;
            transTaxInformation                         = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(inventJournalTrans);
            taxTrans.BillOfMaterialJournalNumber        = inventJournalTrans.JournalId;
            taxTrans.ExciseTariffCodes                  = transTaxInformation.ExciseTariffCodes;
            taxTrans.Unit                               = inventJournalTrans.unitId();
            taxTrans.Voucher                            = inventJournalTrans.JournalId;
            taxTrans.TransDate                          = inventJournalTrans.TransDate;
            taxTrans.Source                             = TaxModuleType::Inventory_IN;
            taxTrans.insertInventDetails(inventJournalTrans.inventDim());
            break;
        case tablenum(InventTransferLine):
            inventTransferLine                          = _common;
            transTaxInformation                         = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(inventTransferLine);
            taxTrans.ExciseTariffCodes                  = transTaxInformation.ExciseTariffCodes;
            select firstonly Voucher
                from  taxTransLoc
                where taxTransLoc.RecId == _exciseDSARegister.TransRecId;

            taxTrans.Voucher                            = taxTransLoc.Voucher;
            taxTrans.TransDate                          = taxTrans.dsaIdDate;
            taxTrans.Source                             = TaxModuleType::StockTransfer_IN;
            break;
        case tablenum(CustPackingSlipJour):
            custPackingSlipJour                     = _common;
            custTable                               = CustTable::find(custPackingSlipJour.OrderAccount);
            salesLine                               = SalesLine::findInventTransId(_exciseDSARegister.InventTransId);
            transTaxInformation                     = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(salesLine);
            taxInformation                          = TaxInformation_IN::findDefaultByLocation(DirParty::primaryPostalAddressLocationRecId(custTable.Party));
            taxTrans.SalesPackingSlipDate           = custPackingSlipJour.DeliveryDate;
            taxTrans.SalesPackingSlipNumber         = custPackingSlipJour.PackingSlipId;
            taxTrans.SalesPackingSlipQuantity       = custPackingSlipJour.Qty;
            taxTrans.SalesOrder                     = salesLine.SalesId;
            taxTrans.SalesOrderDate                 = DateTimeUtil::date(salesLine.CreatedDateTime);
            taxTrans.InvoiceAccount                 = custPackingSlipJour.InvoiceAccount;
            taxTrans.CommissionarateCustomerMaster  = taxInformation.Commissionarate;
            taxTrans.DivisionVendorMaster           = taxInformation.Division;
            taxTrans.Customer                       = custTable.AccountNum;
            taxTrans.CustomerAddress                = LogisticsPostalAddress::findByLocation(taxInformation.RegistrationLocation).Address;
            transTaxInformationLoc                  = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(salesQuotationLine);
            if (transTaxInformationLoc.ExciseType !=  ExciseType_IN::None)
            {
                taxTrans.CustVendRegistrationNumber = (transTaxInformationLoc.ExciseType ==  ExciseType_IN::Manufacturer)
                                                        ? taxInformation.ManECCRegistrationNumberTable
                                                        : taxInformation.TraderECCRegistrationNumberTable;
            }
            taxTrans.RegistrationNumberType         = transTaxInformationLoc.ExciseType;
            taxTrans.RangeCustomerMaster            = taxInformation.Range;
            taxTrans.Voucher                        = custPackingSlipJour.LedgerVoucher;
            taxTrans.TransDate                      = custPackingSlipJour.DeliveryDate;
            taxTrans.Source                         = TaxModuleType::Sales;
            taxTrans.VoucherCurrency                = salesLine.CurrencyCode;
            taxTrans.TaxItemGroup                   = salesLine.TaxItemGroup;
            taxTrans.TaxGroup                       = salesLine.TaxGroup;
            taxTrans.ExciseTariffCodes              = transTaxInformation.ExciseTariffCodes;

            select count(RecId)
                from wmsOrderTrans
                where wmsOrderTrans.InventTransId == salesLine.InventTransId;
            if (wmsOrderTrans.RecId == 1)
            {
                taxTrans.PickingListDate        = wmsOrderTrans.DlvDate;
                taxTrans.PickingListNumber      = wmsOrderTrans.RouteId;
                taxTrans.PickingListQuantity    = wmsOrderTrans.Qty;
            }
            select firstonly Value
                from markupTrans
                where markupTrans.TransRecId    == salesLine.RecId
                   && markupTrans.TransTableId  == salesLine.TableId;
            if (markupTrans)
            {
                taxTrans.MiscChargeAmount = markupTrans.Value;
            }
            select firstonly SalesUnit
                from custPackingSlipTrans
                where custPackingSlipTrans.SalesId          == custPackingSlipJour.SalesId
                   && custPackingSlipTrans.PackingSlipId    == custPackingSlipJour.PackingSlipId
                   && custPackingSlipTrans.DeliveryDate     == custPackingSlipJour.DeliveryDate;
            if (custPackingSlipTrans)
            {
                taxTrans.Unit = custPackingSlipTrans.SalesUnit;
            }

            taxTrans.insertInventDetails(InventDim::find(salesLine.InventDimId));

            select sum(SalesQty)
                from salesLineLoc
                where salesLineLoc.SalesId == salesLine.SalesId;

            taxTrans.SalesOrderQty = salesLineLoc.SalesQty;
            break;
        case tablenum(SalesLine):
            salesLine                                   = _common;
            transTaxInformation                         = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(salesLine);
            taxTrans.ExciseTariffCodes                  = transTaxInformation.ExciseTariffCodes;
            taxTrans.SalesInvoiceDate                   = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

            select firstonly Voucher, TransDate
                from  taxTransLoc
                where taxTransLoc.RecId == _exciseDSARegister.TransRecId;

            taxTrans.Voucher                            = taxTransLoc.Voucher;
            taxTrans.TransDate                          = taxTransLoc.TransDate;
            taxTrans.Source                             = TaxModuleType::SalesInvoice;
            taxTrans.Unit                               = salesLine.SalesUnit;
            taxTrans.insertFromSalesLine(salesLine, taxTrans.InventTransId);
            taxTrans.insertInventDetails(InventDim::find(salesLine.InventDimId));
            break;

        // For non-transaction related register update
        case tableNum(Common):
            taxTrans.TransDate                          = _exciseDSARegister.DSADate;
            break;

        default:
            break;
    }

    taxInformation              = TaxInformation_IN::findDefaultByLocation(DirParty::primaryPostalAddressLocationRecId(companyInfoLoc.RecId));
    taxTrans.CompanyAddress     = LogisticsPostalAddress::findByLocation(taxInformation.RegistrationLocation).Address;
    taxTrans.CompanyName                                = DirPartyTable::findRec(companyInfoLoc.RecId).Name;
    taxTrans.CompanyBankAddress                         = LogisticsPostalAddress::findByLocation(BankAccountTable::find(companyInfoLoc.Bank).Location).Address;
    taxTrans.CompanyBankName                            = companyInfoLoc.Bank;
    taxTrans.RangeCompanyInformation                    = taxInformation.Range;
    taxTrans.DivisionCompanyInformation                 = taxInformation.Division;
    taxTrans.TaxType                                    = TaxType_IN::Excise;
    taxTrans.TaxPeriod                                  = _taxPeriod;
    taxTrans.ExciseRegister                             = ExciseRegister_IN::DSA;
    taxTrans.insert();

    if (_exciseDSARegister)
    {
        _exciseDSARegister.TaxTrans_IN                  = taxTrans.RecId;
        _exciseDSARegister.update();
    }
    ttscommit;
}

/// <summary>
/// This method is used to insert info into the <c>TaxTrans_IN</c> table from given parameters.
/// </summary>
/// <param name="_excisePLARegister">
/// A buffer of <c>ExcisePLARegister_IN</c> table;
/// </param>
/// <param name="_ledgerJournalTrans">
/// A buffer of <c>LedgerJournalTrans</c> table;
/// </param>
/// <param name="_taxPeriod">
/// The tax period; optional.
/// </param>
/// <param name="_plaAccount">
/// The PLA account; optional.
/// </param>
/// <param name="_interimPLAAccount">
/// The interim PLA account; optional.
/// </param>
/// <param name="_transactionDate">
/// The transaction date; optional.
/// </param>
/// <param name="_eccNumber">
/// The customer/vendor registration number; optional.
/// </param>
/// <param name="_voucher">
/// The voucher number; optional.
/// </param>
/// <param name="_taxComponent">
/// The tax component for the tax code; optional.
/// </param>
/// <param name="_taxCode">
/// The tax code; optional.
/// </param>
public static void insertIntoTaxTransPLA(
    ExcisePLARegister_IN     _excisePLARegister,
    LedgerJournalTrans       _ledgerJournalTrans  = null,
    TaxPeriod                _taxPeriod           = '',
    RefRecId                 _plaAccount          = 0,
    RefRecId                 _interimPLAAccount   = 0,
    TransDate                _transactionDate     = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
    RefRecId                 _eccNumber           = 0,
    Voucher                  _voucher             = '',
    RefRecId                 _taxComponent        = 0,
    TaxCode                  _taxCode             ='')

{
    AssetTable                       assetTable;
    AssetBook                        assetBook;
    CompanyInfo                      companyInfoLoc;
    VendTable                        vendTable;
    TaxTrans_IN                      taxTrans;
    ChallanInformation_IN            challanInformation;
    TaxInformation_IN                taxInformation;
    TransTaxInformation              transTaxInformation;
    LedgerJournalTransTaxExtensionIN ledgerJournalTransTaxExtensionIN;

    companyInfoLoc = CompanyInfo::find();

    ttsbegin;

    transTaxInformation = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(_ledgerJournalTrans);

    taxTrans.CompanyRegistrationNumber  = _excisePLARegister.TaxRegistrationNumberTable;
    taxTrans.plaEntryNumber             = _excisePLARegister.NumberSequence;
    taxTrans.plaId                      = _excisePLARegister.NumberSequence;
    taxTrans.plaIdDate                  = _excisePLARegister.rgDate;
    taxTrans.plaAmount                  = _excisePLARegister.CreditBalance ?
                                          _excisePLARegister.CreditBalance :
                                          _excisePLARegister.DebitBalance;
    taxTrans.CustVendRegistrationNumber = _eccNumber;
    taxTrans.TaxComponentTable          = _excisePLARegister.TaxComponentTable;
    taxTrans.ExciseRegister             = ExciseRegister_IN::PLA;
    taxTrans.SourceTaxAmountCur         = _excisePLARegister.CreditBalance ?
                                          _excisePLARegister.CreditBalance :
                                          _excisePLARegister.DebitBalance;
    taxTrans.PostedTaxAmout             = taxTrans.SourceTaxAmountCur;

    taxTrans.plaLedgerDimension         = _plaAccount;
    taxTrans.InterimPLALedgerDimension  = _interimPLAAccount;
    taxTrans.TransDate                  = _transactionDate;
    taxTrans.Voucher                    = _voucher;
    taxTrans.TaxComponentTable          = _taxComponent;
    taxTrans.TaxCode                    = _taxCode;
    taxTrans.SourceCurrencyCode         = Ledger::accountingCurrency(companyInfoLoc.RecId);

    if (_ledgerJournalTrans.RecId)
    {
        assetTable                      = _ledgerJournalTrans.findAssetTable();
        ledgerJournalTransTaxExtensionIN = _ledgerJournalTrans.ledgerJournalTransTaxExtensionIN();
        assetBook                       = assetTable.book();
        taxTrans.Make                   = assetTable.Make;
        taxTrans.Model                  = assetTable.Model;
        taxTrans.PlacedInService        = assetBook.UsedFromDate;
        taxTrans.AssetLocation          = assetTable.Location;
        taxTrans.FixedAssetSerialNumber = assetTable.SerialNum;
        //this code just for doing payment for PLA. So we just need set taxTrans.DebitAmount
        taxTrans.DebitAmount            = _excisePLARegister.DebitBalance;
        taxTrans.ExciseTariffCodes       = transTaxInformation.ExciseTariffCodes;
        taxTrans.ExciseRecordType       = transTaxInformation.ExciseRecordType;

        if (transTaxInformation.ExciseType !=  ExciseType_IN::None)
        {
            taxTrans.CustVendRegistrationNumber = transTaxInformation.ExciseECCRegistrationNumber;
        }
        taxTrans.JournalInvoice         = _ledgerJournalTrans.Invoice;
        taxTrans.JournalInvoiceAmount   = _ledgerJournalTrans.amount();
        taxTrans.JournalInvoiceDate     = _ledgerJournalTrans.TransDate;
        taxTrans.JournalNum             = _ledgerJournalTrans.JournalNum;
        taxTrans.TaxItemGroup           = _ledgerJournalTrans.TaxItemGroup;
        taxTrans.TaxGroup               = _ledgerJournalTrans.TaxGroup;
        taxTrans.TransDate              = _ledgerJournalTrans.TransDate;
        taxTrans.TransactionAmount      = (_ledgerJournalTrans.AmountCurCredit ?
                                           _ledgerJournalTrans.AmountCurCredit :
                                           _ledgerJournalTrans.AmountCurDebit);
        taxTrans.AssessableValue        = _ledgerJournalTrans.ledgerJournalTransTaxExtensionIN().AssessableValueTransactionCurrency;
        taxTrans.VoucherCurrency        = _ledgerJournalTrans.CurrencyCode;
        taxTrans.SourceCurrencyCode     = _ledgerJournalTrans.CurrencyCode;
        taxTrans.Voucher                = _ledgerJournalTrans.Voucher;
        if (_ledgerJournalTrans.AccountType == LedgerJournalACType::Bank)
        {
            taxTrans.CompanyBankName        = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_ledgerJournalTrans.LedgerDimension);
        }
        else if (_ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Bank)
        {
            taxTrans.CompanyBankName = LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_ledgerJournalTrans.OffsetLedgerDimension);
        }
        if (_ledgerJournalTrans.AccountType == LedgerJournalACType::Vend)
        {
            vendTable = VendTable::find(LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_ledgerJournalTrans.LedgerDimension));
        }
        else if (_ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Vend)
        {
            vendTable = VendTable::find(LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(_ledgerJournalTrans.OffsetLedgerDimension));
        }
        taxInformation.clear();
        taxInformation = TaxInformation_IN::findDefaultByLocation(DirParty::primaryPostalAddressLocationRecId(vendTable.Party));
        taxTrans.RegistrationNumberType         = transTaxInformation.ExciseType;
        taxTrans.CommissionarateVendorMaster    = taxInformation.Commissionarate;
        taxTrans.DivisionVendorMaster           = taxInformation.Division;
        taxTrans.Vendor                         = vendTable.AccountNum;
        taxTrans.VendorAddress                  = LogisticsPostalAddress::findByLocation(taxInformation.RegistrationLocation).Address;
        if (transTaxInformation.ExciseType !=  ExciseType_IN::None)
        {
            taxTrans.CustVendRegistrationNumber = (transTaxInformation.ExciseType ==  ExciseType_IN::Manufacturer)
                                                    ? taxInformation.ManECCRegistrationNumberTable
                                                    : taxInformation.TraderECCRegistrationNumberTable;
        }
        taxTrans.RangeVendorMaster              = taxInformation.Range;
        taxTrans.Journal                        = LedgerJournalTable::find(_ledgerJournalTrans.JournalNum).JournalName;
        taxTrans.TransTableId                   = _ledgerJournalTrans.TableId;
        taxTrans.TransRecId                     = _ledgerJournalTrans.RecId;
        select firstonly @Date, ChallanNumber from challanInformation
            where challanInformation.SourceRecID == _ledgerJournalTrans.RecId;
        if (challanInformation.RecId)
        {
            taxTrans.ChallanDate    = challanInformation.Date;
            taxTrans.ChallanNumber  = challanInformation.ChallanNumber;
        }
    }

    taxInformation.clear();
    taxInformation = TaxInformation_IN::findDefaultByLocation(DirParty::primaryPostalAddressLocationRecId(companyInfoLoc.RecId));

    taxTrans.CommissionarateCompanyInformation  = taxInformation.Commissionarate;
    taxTrans.CompanyAddress                     = LogisticsPostalAddress::findByLocation(taxInformation.RegistrationLocation).Address;
    taxTrans.CompanyName                        = DirPartyTable::findRec(companyInfoLoc.RecId).Name;
    taxTrans.CompanyBankAddress                 = LogisticsPostalAddress::findByLocation(BankAccountTable::find(companyInfoLoc.Bank).Location).Address;
    if (!_ledgerJournalTrans.RecId)
    {
        taxTrans.CompanyBankName                    = companyInfoLoc.Bank;
    }
    taxTrans.RangeCompanyInformation            = taxInformation.Range;
    taxTrans.DivisionCompanyInformation         = taxInformation.Division;
    taxTrans.TaxPeriod                          = _taxPeriod;
    taxTrans.TaxType                            = TaxType_IN::Excise;
    taxTrans.insert();
    ttscommit;
}

/// <summary>
/// This method is used to insert info into the <c>TaxTrans_IN</c> table from given parameters.
/// </summary>
/// <param name="_exciseRG23AIRegister">
/// A buffer of <c>ExciseRG23AIRegister_IN</c> table;
/// </param>
/// <param name="_inventTrans">
/// A buffer of <c>InventTrans</c> table;
/// </param>
public static void insertRG23AIIntoTaxTrans(
    ExciseRG23AIRegister_IN    _exciseRG23AIRegister,
    InventTrans                _inventTrans)
{
    CompanyInfo         companyInfoLoc;
    TaxTrans_IN         taxTrans;
    InventTable         inventTable;
    TaxInformation_IN   taxInformation;

    companyInfoLoc = CompanyInfo::find();
    inventTable    = InventTable::find(_inventTrans.ItemId);
    taxInformation = TaxInformation_IN::findDefaultByLocation(DirParty::primaryPostalAddressLocationRecId(companyInfoLoc.RecId));
    ttsbegin;
    taxTrans.rg23aiIdDate                       = _exciseRG23AIRegister.rgDate;
    taxTrans.ExciseTariffCodes                  = inventTable.ExciseTariffCodes_IN;
    taxTrans.CompanyRegistrationNumber          = _exciseRG23AIRegister.TaxRegistrationNumberTable;
    taxTrans.RegistrationNumberType             = ExciseType_IN::Manufacturer;
    taxTrans.rg23aiNumberSequenceCode           = _exciseRG23AIRegister.NumberSequence;
    taxTrans.InventTransId                      = _exciseRG23AIRegister.InventTransId;
    taxTrans.ItemId                             = _exciseRG23AIRegister.Item;
    taxTrans.ExciseRecordType                   = _exciseRG23AIRegister.ExciseRecordType;
    taxTrans.Quantity                           = _exciseRG23AIRegister.dsaQty;
    taxTrans.Voucher                            = _inventTrans.VoucherPhysical;
    taxTrans.TransDate                          = taxTrans.rg23aiIdDate;
    taxTrans.dsaQuantity                        = _exciseRG23AIRegister.dsaQty;
    taxTrans.TaxType                            = TaxType_IN::Excise;
    taxTrans.CommissionarateCompanyInformation  = taxInformation.Commissionarate;
    taxTrans.CompanyAddress                     = LogisticsPostalAddress::findByLocation(taxInformation.RegistrationLocation).Address;

    taxTrans.CompanyName                        = DirPartyTable::findRec(companyInfoLoc.RecId).Name;
    taxTrans.CompanyBankAddress                 = LogisticsPostalAddress::findByLocation(BankAccountTable::find(companyInfoLoc.Bank).Location).Address;
    taxTrans.CompanyBankName                    = companyInfoLoc.Bank;
    taxTrans.RangeCompanyInformation            = taxInformation.Range;
    taxTrans.DivisionCompanyInformation         = taxInformation.Division;
    taxTrans.ProductionOrderDate                = _inventTrans.DatePhysical;
    taxTrans.ProductionOrderNumber              = InventTransOrigin::find(_inventTrans.InventTransOrigin).ReferenceId;
    taxTrans.ProductionOrderQty                 = taxTrans.Quantity;
    taxTrans.ManufacturedQuantity               = taxTrans.Quantity;
    taxTrans.ExciseRegister                     = ExciseRegister_IN::RG23APartI;
    taxTrans.insertInventDetails(_inventTrans.inventDim());
    taxTrans.ExciseRegisterType                 = NoYes::Yes;
    taxTrans.insert();
    ttscommit;
}

/// <summary>
/// Checks if customs field is mandatory.
/// </summary>
/// <param name="_fieldName">
/// The field name.
/// </param>
/// <returns>
/// Returns 'True' if customs field is mandatory.
/// </returns>
public static boolean isCustomsFieldMandatory(fieldName _fieldName)
{
    return confind(TaxTrans_IN::customsSummaryFieldIds(), _fieldName) != 0;
}

