/// <summary>
/// Marks transaction for settlement.
/// </summary>
/// <param name="_taxRegistrationNumber">
/// The tax registration number.
/// </param>
/// <param name="_fromDate">
/// The from date.
/// </param>
/// <param name="_toDate">
/// The to date.
/// </param>
/// <param name="_isCorrection">
/// The isCorrection flag.
/// </param>
public static void markForSettlement(
    RefRecId  _taxRegistrationNumber,
    TransDate _fromDate,
    TransDate _toDate,
    boolean   _isCorrection = false)
{
    TaxTrans_IN              taxTransCopy;
    ComponentTransactions_IN componentTransactions;

    ttsbegin;
    if (_isCorrection
        && _taxRegistrationNumber
        && _fromDate)
    {
        // For 'IncludeCorrection' scenario(GL Parameter) transactions prior to user selected 'From date' also flows.
        update_recordset taxTransCopy
            setting MarkedSettle = NoYes::Yes
            join componentTransactions
            where taxTransCopy.RefRecId                            == componentTransactions.TransRecid
                && componentTransactions.Mark                       == NoYes::Yes
                && componentTransactions.Posted                     == NoYes::No
                && componentTransactions.TransDate                  <= _fromDate
                && componentTransactions.TaxRegistrationNumberTable == _taxRegistrationNumber;
    }
    else if (_taxRegistrationNumber
        && _fromDate
        && _toDate)
    {
        // Transactions > user selected 'From date' also flows
        update_recordset taxTransCopy
            setting MarkedSettle = NoYes::Yes
            join componentTransactions
            where taxTransCopy.RefRecId                            == componentTransactions.TransRecid
                && componentTransactions.Mark                       == NoYes::Yes
                && componentTransactions.Posted                     == NoYes::No
                && componentTransactions.TransDate                  >= _fromDate
                && componentTransactions.TransDate                  <= _toDate
                && componentTransactions.TaxRegistrationNumberTable == _taxRegistrationNumber;
    }
    ttscommit;
}

/// <summary>
/// Gets sales tax selected field id list.
/// </summary>
/// <returns>
/// The list of sales tax selected field id.
/// </returns>
public static container salesTaxSelectedFieldIdList()
{
    Counter         i;
    Counter         h;
    Counter         j;
    container       fList;
    SysDictField    sysDictField;
    DictFieldGroup  dictFieldGroup;
    DictTable       dictTable = new DictTable(tablenum(TaxTrans_IN));

    for (i=1;i<=dictTable.fieldGroupCnt();i++)
    {
        if (dictTable.fieldGroup(i) == tablefieldgroupstr(TaxTrans_IN, SalesTaxSelectedFields))
        {
            dictFieldGroup = new DictFieldGroup(tablenum(TaxTrans_IN),dictTable.fieldGroup(i));
            for (h=1;h<=dictFieldGroup.numberOfFields();h++)
            {
                sysDictField = new SysDictField(tablenum(TaxTrans_IN), dictFieldGroup.field(h));
                if (sysDictField)
                {
                    for (j=1; j <= sysDictField.arraySize(); j++)
                    {
                        fList += new SysDictField(tablenum(TaxTrans_IN), dictFieldGroup.field(h), j).name();
                    }
                }
                else
                {
                    fList += dictFieldGroup.methodName(dictFieldGroup.field(h));
                }
            }
        }
    }
    return fList;
}

/// <summary>
/// Gets sales tax summary field id.
/// </summary>
/// <returns>
/// The sales tax summary field id.
/// </returns>
public static container salesTaxSummaryFieldIds()
{
    return [new SysDictField(tablenum(TaxTrans_IN),fieldnum(TaxTrans_IN, TransDate),1).name(),
            new SysDictField(tablenum(TaxTrans_IN),fieldnum(TaxTrans_IN, Voucher),1).name(),
            new SysDictField(tablenum(TaxTrans_IN),fieldnum(TaxTrans_IN, TaxCode),1).name(),
            new SysDictField(tablenum(TaxTrans_IN),fieldnum(TaxTrans_IN, SourceBaseAmountCur),1).name(),
            new SysDictField(tablenum(TaxTrans_IN),fieldnum(TaxTrans_IN, SourceTaxAmountCur),1).name()];
}

/// <summary>
/// Gets selected customs field id list.
/// </summary>
/// <returns>
/// The list of selected customs field id.
/// </returns>
public static container selectedCustomsFieldIdList()
{
    fieldName       fieldName;
    int             selectedNumber          = 1;
    container       summaryFieldIdList      = TaxTrans_IN::customsSelectedFieldIdList();
    container       selectedFieldIdList;

    while (selectedNumber <= conlen(summaryFieldIdList))
    {
        fieldName = conpeek(summaryFieldIdList, selectedNumber);
        selectedFieldIdList += fieldName;
        selectedNumber ++;
    }

    return selectedFieldIdList;
}

