/// <summary>
/// Checks if tax is settled for transaction.
/// </summary>
/// <param name="_sourceTableId">
/// The source table id.
/// </param>
/// <param name="_sourceRecId">
/// The source record id.
/// </param>
/// <returns>
/// If 'True', the tax is settled for transaction.
/// </returns>
public static boolean isTaxSettledForTransaction(TableId _sourceTableId, RecId _sourceRecId)
{
    TaxTrans        taxTrans;
    TaxTrans_IN     taxTransIN;

    select firstOnly TaxReportPeriod_IN from taxTransIN
        exists join taxTrans
            where taxTrans.RecId            == taxTransIN.RefRecId
                && taxTrans.SourceRecId     == _sourceRecId
                && taxTrans.SourceTableId   == _sourceTableId;

    return taxTransIN.TaxReportPeriod_IN != 0;
}
