public DimensionDefault dimensionDefault()
{
    return this.DeriveDim ? this.DefaultDimensionPosted : this.DefaultDimension;
}

public void initFromPBookVATProcessLogTransOper(PurchBookVATProcessLogTransOper_RU  _purchBookVATProcessLogTransOper)
{
    this.LedgerDimension                    = _purchBookVATProcessLogTransOper.LedgerDimension;
    this.TaxAmount                          = _purchBookVATProcessLogTransOper.TaxAmount;
    this.TransTxt                           = _purchBookVATProcessLogTransOper.TransTxt;
    this.PurchBookVATProcessParameters_RU   = _purchBookVATProcessLogTransOper.PurchBookVATProcessParameters_RU;
    this.Percent                            = _purchBookVATProcessLogTransOper.Percent;
    this.TransDate                          = _purchBookVATProcessLogTransOper.TransDate;
    this.Blocked                            = _purchBookVATProcessLogTransOper.Blocked;
    this.AmountLiableToVAT                  = _purchBookVATProcessLogTransOper.AmountLiableToVAT;
    this.OrigRecId                          = _purchBookVATProcessLogTransOper.RecId;
    this.DefaultDimension                   = _purchBookVATProcessLogTransOper.DefaultDimension;
    this.DeriveDim                          = _purchBookVATProcessLogTransOper.DeriveDim;
    this.DefaultDimensionPosted             = _purchBookVATProcessLogTransOper.DefaultDimensionPosted;
    this.CanceledRefRecId                   = _purchBookVATProcessLogTransOper.CanceledRefRecId;
    this.OffsetLedgerDimension              = _purchBookVATProcessLogTransOper.OffsetLedgerDimension;
    this.TaxCode                            = _purchBookVATProcessLogTransOper.TaxCode;
    this.FactureAmounts                     = _purchBookVATProcessLogTransOper.FacturedAmounts;
    this.InventTransId                      = _purchBookVATProcessLogTransOper.InventTransId;
    this.DocumentType                       = _purchBookVATProcessLogTransOper.DocumentType;
    this.rDeferralsTransRecId               = _purchBookVATProcessLogTransOper.rDeferralsTransRecId;
    this.rDeferralsClosedAmount             = _purchBookVATProcessLogTransOper.rDeferralsClosedAmount;
}

public boolean validateField(fieldId _fieldIdToCheck)
{
    boolean     ret;

    ret = super(_fieldIdToCheck);

    if (ret)
    {
        switch (_fieldIdToCheck)
        {
            case fieldNum(TmpPurchBookVATProcessLogTransOper_RU, LedgerDimension) :
                if (! this.LedgerDimension)
                {
                    ret = checkFailed(strFmt("@SYS26332", fieldPName(TmpPurchBookVATProcessLogTransOper_RU, LedgerDimension)));
                }
                break;

            case fieldNum(TmpPurchBookVATProcessLogTransOper_RU, OffsetLedgerDimension) :
                if (! this.OffsetLedgerDimension)
                {
                    ret = checkFailed(strFmt("@SYS26332", fieldPName(TmpPurchBookVATProcessLogTransOper_RU, OffsetLedgerDimension)));
                }
                break;
        }
    }

    return ret;
}

public static BookStructSmallVATAmounts_RU createLines(TmpPurchBookVATProcessLogTransOper_RU   _tmpProcessLogTransOper,
                                                              FactureId_RU                            _factureId,
                                                              TransDate                               _transDate,
                                                              PurchBookVATProcessParametersRecId_RU   _operationRecId,
                                                              DimensionDefault                        _defaultDimension,
                                                              TaxCode                                 _taxCode,
                                                              InventTransId                           _inventTransId,
                                                              SalesPurchBookTransType_RU              _transType,
                                                              BookStructSmallVATAmounts_RU            _factureAmounts,
                                                              BookStructSmallVATAmounts_RU            _settledAmounts,
                                                              BookStructSmallVATAmounts_RU            _usedAmounts,
                                                              BookStructSmallVATAmounts_RU            _blockedAmounts,
                                                              BookStructSmallVATAmounts_RU            _assetBlockedAmounts,
                                                              recId                                   _transRecId,
                                                              recId                                   _offsetRecId,
                                                              CompanyId                               _transCompany,
                                                              CompanyId                               _offsetCompany,
                                                              recId                                   _settlementRecId,
                                                              recId                                   _processLogTransRecId,
                                                              TransDate                               _lastBookClosingDate,
                                                              TransDate                               _settlementDate       = _transDate,
                                                              boolean                                 _isGroupBy            = false,
                                                              PurchBookVATDocumentType_RU             _documentType         = PurchBookVATDocumentType_RU::Unknown,
                                                              boolean                                 _useSettlementInfo    = true,
                                                              TransDate                               _fromDate             = dateNull(),
                                                              PurchBookProcessingType_RU              _processingType       = PurchBookProcessingType_RU::Standard,
                                                              container                               _deferralsOpenAmounts = conNull(),
                                                              NoYes                                   _vatTaxAgent          = NoYes::No)
{
    BookStructSmallVATAmounts_RU        remainAmounts  = new BookStructSmallVATAmounts_RU();

    PurchBookVATProcessLogTransOper_RU  processLogTransOper;
    PurchBookVATProcessLogTrans_RU      processLogTrans;
    PurchBookVATProcessLogTable_RU      processLogTable;
    PurchBookVATProcessParameters_RU    vatProcessParameters = _operationRecId                                          ?
                                                               PurchBookVATProcessParameters_RU::find(_operationRecId)  :
                                                               PurchBookVATProcessParameters_RU::defaultParms(_processingType);

    LedgerDimensionDefaultAccount       defaultAccount       /*68.x*/,
                                        offsetDefaultAccount /*19.x*/;
    PurchBookVATLedgerPosting_RU        ledgerPosting = PurchBookVATLedgerPosting_RU::ledgerPosting(_taxCode, vatProcessParameters.RecId);

    Percent                             totalPercent;

    boolean                             existsRDeferrals;
    Map                                 deferralsOpenTrans;
    recId                               settlementRecId;
    MapEnumerator                       meOpenTrans;
    RDeferralsTrans                     deferralsTrans;
    AmountCur                           curClosedSum;
    BookStructSmallVATAmounts_RU        curDisposalAmounts;
    TaxLedgerAccountGroup               taxLedgerAccountGroup;

    #define.NotNullRecId(1)

    void insertLogTransOper(BookStructSmallVATAmounts_RU _logTransOperAmounts, NoYes _blocked, boolean _updateDeferralsTransLinkSum = false)
    {
        _tmpProcessLogTransOper.clear();

        _tmpProcessLogTransOper.Blocked                          = _blocked;
        _tmpProcessLogTransOper.BlockedAuto                      = _blocked;
        _tmpProcessLogTransOper.PurchBookVATProcessParameters_RU = vatProcessParameters.RecId;
        _tmpProcessLogTransOper.TransDate                        = max(_transDate, _lastBookClosingDate + 1);
        _tmpProcessLogTransOper.DefaultDimension                 = _defaultDimension;
        _tmpProcessLogTransOper.DeriveDim                        = vatProcessParameters.DeriveDim;
        _tmpProcessLogTransOper.DefaultDimensionPosted           = vatProcessParameters.DeriveDim ? vatProcessParameters.DefaultDimension : _defaultDimension;
        _tmpProcessLogTransOper.TaxCode                          = _taxCode;
        _tmpProcessLogTransOper.InventTransId                    = _inventTransId;

        if (_updateDeferralsTransLinkSum && deferralsTrans)
        {
            _tmpProcessLogTransOper.Deferrals               = deferralsTrans.RecId ? NoYes::Yes : NoYes::No;
            _tmpProcessLogTransOper.rDeferralsClosedAmount  = curClosedSum;
            if (deferralsTrans.RecId != #NotNullRecId)
            {
                _tmpProcessLogTransOper.TransDate               = deferralsTrans.TransDate;
                _tmpProcessLogTransOper.rDeferralsTransRecId    = deferralsTrans.RecId;
            }
        }

        if (_transType != SalesPurchBookTransType_RU::PrepaymentStorno &&
           _transType != SalesPurchBookTransType_RU::TaxCorrection    &&
           _taxCode                                                   &&
           _logTransOperAmounts.taxAmountVAT())
        {
            if (_processingType == PurchBookProcessingType_RU::Standard)
            {
                if (! ledgerPosting.LedgerDimension || ! ledgerPosting.OffsetLedgerDimension)
                {
                    [defaultAccount, offsetDefaultAccount] = PurchBookVATProcessParameters_RU::calcLedgerDimensions(_taxCode,
                                                                                                                    _factureId,
                                                                                                                    _transType,
                                                                                                                    _transRecId,
                                                                                                                    _offsetRecId,
                                                                                                                    _transCompany,
                                                                                                                    _offsetCompany);
                }

                defaultAccount       = ledgerPosting.LedgerDimension       ? ledgerPosting.LedgerDimension       : defaultAccount;
                offsetDefaultAccount = ledgerPosting.OffsetLedgerDimension ? ledgerPosting.OffsetLedgerDimension : offsetDefaultAccount;

                if (defaultAccount && offsetDefaultAccount)
                {
                    _tmpProcessLogTransOper.TransTxt              = vatProcessParameters.Description;
                    _tmpProcessLogTransOper.LedgerDimension       = defaultAccount;
                    _tmpProcessLogTransOper.OffsetLedgerDimension = offsetDefaultAccount;
                }
            }
            else
            {
                [defaultAccount, offsetDefaultAccount] = SalesBookVATProcessParameters_RU::calcProcessLedgerAccounts(_taxCode,
                                                                                                                     _defaultDimension,
                                                                                                                     _inventTransId,
                                                                                                                     _factureId,
                                                                                                                     _transRecId,
                                                                                                                     SalesBookProcessingType_RU::VATRestoration,
                                                                                                                     SalesBookVATRestoringType_RU::Export,
                                                                                                                     _fromDate);

                _tmpProcessLogTransOper.TransTxt              = vatProcessParameters.Description;
                _tmpProcessLogTransOper.LedgerDimension       = offsetDefaultAccount;
                _tmpProcessLogTransOper.OffsetLedgerDimension = defaultAccount;
            }
        }
        else if (_transType == SalesPurchBookTransType_RU::TaxCorrection && _vatTaxAgent)
        {
            taxLedgerAccountGroup = TaxLedgerAccountGroup::find(TaxTable::find(_taxCode).TaxAccountGroup);

            _tmpProcessLogTransOper.LedgerDimension       = taxLedgerAccountGroup.TaxOutgoingLedgerDimension;
            _tmpProcessLogTransOper.OffsetLedgerDimension = taxLedgerAccountGroup.TaxIncomingLedgerDimension;
        }

        _tmpProcessLogTransOper.FactureAmounts      = _factureAmounts.pack();
        _tmpProcessLogTransOper.SettledAmounts      = _settledAmounts.pack();
        _tmpProcessLogTransOper.UsedAmounts         = _usedAmounts.pack();

        _tmpProcessLogTransOper.AmountLiableToVAT   = _logTransOperAmounts.amountLiableToVAT();
        _tmpProcessLogTransOper.TaxAmount           = _logTransOperAmounts.taxAmountVAT();
        _tmpProcessLogTransOper.Percent             = BookProcessVATEngine_RU::pct(_logTransOperAmounts.amountLiableToVAT(), _settledAmounts.amountLiableToVAT() - _usedAmounts.amountLiableToVAT());
        _tmpProcessLogTransOper.AllowDelete         = _tmpProcessLogTransOper.Percent  == 100 ? NoYes::No : _blocked;
        _tmpProcessLogTransOper.RefRecId            = _processLogTransRecId;
        _tmpProcessLogTransOper.DocumentType        = _documentType;

        _tmpProcessLogTransOper.insert();

        totalPercent += _tmpProcessLogTransOper.Percent;
    }

    if (_deferralsOpenAmounts)
    {
        existsRDeferrals    = conPeek(_deferralsOpenAmounts, 2);
        deferralsOpenTrans  = Map::create(conPeek(_deferralsOpenAmounts, 1));
        meOpenTrans         = deferralsOpenTrans.getEnumerator();
    }

    remainAmounts.addAmounts2(_settledAmounts);
    remainAmounts.subAmounts2(_usedAmounts);
    remainAmounts.subAmounts2(_blockedAmounts);

    if (_assetBlockedAmounts)
    {
        remainAmounts.subAmounts2(_assetBlockedAmounts);
    }

    if (meOpenTrans)
    {
        meOpenTrans.reset();
        while (meOpenTrans.moveNext())
        {
            curDisposalAmounts = new BookStructSmallVATAmounts_RU();
            curDisposalAmounts.unpack(conPeek(meOpenTrans.currentValue(), 2));

            remainAmounts.subAmounts2(curDisposalAmounts);
        }
    }

    if (remainAmounts.amountInclVAT())
    {
        if (existsRDeferrals)
        {
            deferralsTrans.RecId = #NotNullRecId;

            insertLogTransOper(remainAmounts, NoYes::Yes, true);
        }
        else
        {
            insertLogTransOper(remainAmounts, NoYes::No);
        }
    }

    if (_assetBlockedAmounts && _assetBlockedAmounts.amountInclVAT())
    {
        insertLogTransOper(_assetBlockedAmounts, NoYes::Yes);
    }

    if (meOpenTrans)
    {
        meOpenTrans.reset();
        while (meOpenTrans.moveNext())
        {
            deferralsTrans      = meOpenTrans.currentKey();

            curClosedSum        = conPeek(meOpenTrans.currentValue(), 1);
            curDisposalAmounts  = new BookStructSmallVATAmounts_RU();
            curDisposalAmounts.unpack(conPeek(meOpenTrans.currentValue(), 2));
            settlementRecId     = conPeek(meOpenTrans.currentValue(), 3);

            insertLogTransOper(curDisposalAmounts, NoYes::No, true);
        }
    }

    if (_blockedAmounts.amountInclVAT())
    {
        while select processLogTransOper
            where processLogTransOper.DefaultDimension  == _defaultDimension                   &&
                  processLogTransOper.TaxCode           == _taxCode                            &&
                  processLogTransOper.Blocked           == NoYes::Yes                          &&
                  processLogTransOper.BlockedAuto       == NoYes::No                           &&
                  processLogTransOper.CanceledRefRecId  == 0                                   &&
                  processLogTransOper.TransDate         >= _fromDate                           &&
                 (! _useSettlementInfo || processLogTransOper.DocumentType  == _documentType)  &&
                 (! _isGroupBy         || processLogTransOper.InventTransId == _inventTransId)
        exists join processLogTrans
            where processLogTrans.RecId                 == processLogTransOper.RefRecId        &&
                  processLogTrans.FactureId             == _factureId                          &&
                  processLogTrans.InvoiceRecIdRef       == _transRecId                         &&
                  processLogTrans.ProcessingType        == _processingType                     &&
                  processLogTrans.InvoiceCompany        == _transCompany                       &&
                 (! _useSettlementInfo || (processLogTrans.PaymentRecIdRef == _offsetRecId     &&
                                           processLogTrans.PaymentCompany  == _offsetCompany)) &&
                 (! _useSettlementInfo || processLogTrans.SettlementDate   == _settlementDate) &&
                 (! _useSettlementInfo || processLogTrans.SettlementRecId  == _settlementRecId)
        exists join processLogTable
            where processLogTransOper.LogTableRefRecId == processLogTable.RecId  &&
                  processLogTable.TransDate            >= _fromDate
        {
            _tmpProcessLogTransOper.clear();

            _tmpProcessLogTransOper.initFromPBookVATProcessLogTransOper(processLogTransOper);
            _tmpProcessLogTransOper.SettledAmounts = _settledAmounts.pack();
            _tmpProcessLogTransOper.FactureAmounts = _factureAmounts.pack();
            _tmpProcessLogTransOper.InventTransId  = _inventTransId;
            _tmpProcessLogTransOper.UsedAmounts    = _usedAmounts.pack();
            _tmpProcessLogTransOper.TransDate      = max(_tmpProcessLogTransOper.TransDate, _lastBookClosingDate + 1);
            _tmpProcessLogTransOper.Percent        = BookProcessVATEngine_RU::pct(_tmpProcessLogTransOper.AmountLiableToVAT, _settledAmounts.amountLiableToVAT()  - _usedAmounts.amountLiableToVAT());
            _tmpProcessLogTransOper.RefRecId       = _processLogTransRecId;
            _tmpProcessLogTransOper.insert();

            totalPercent += _tmpProcessLogTransOper.Percent;
        }
    }

    if (totalPercent > 100)
    {
        _tmpProcessLogTransOper.Percent -= totalPercent - 100;
        _tmpProcessLogTransOper.update();
    }

    return remainAmounts;
}
