public display FactureDate_External_RU factureDate_External()
{
    FactureJour_RU factureJour;

    if (this.TransType == SalesPurchBookTransType_RU::TaxCorrection)
    {
        select firstonly FactureDate_External from factureJour
            where  factureJour.FactureId == this.FactureId &&
                  (factureJour.Module    == FactureModule_RU::TaxCorrectionPurch ||
                   factureJour.Module    == FactureModule_RU::TaxCorrectionPurch_Cust);
    }
    else
    {
        select firstonly FactureDate_External from factureJour
            where factureJour.FactureId == this.FactureId &&
                  factureJour.Module    == this.factureModule();
    }

    return factureJour.FactureDate_External;
}

public FactureModule_RU factureModule()
{
    FactureModule_RU ret;

    switch (this.TransType)
    {
        case SalesPurchBookTransType_RU::AdvanceReport     :
            ret = FactureModule_RU::Empl;
            break;
        case SalesPurchBookTransType_RU::CreditNote        :
        case SalesPurchBookTransType_RU::Invoice           :
        case SalesPurchBookTransType_RU::SumDiff           :
        case SalesPurchBookTransType_RU::ExchAdj           :
        case SalesPurchBookTransType_RU::AdvanceAdjustment :
        case SalesPurchBookTransType_RU::Prepayment        :
            ret = FactureModule_RU::Vend;
            break;
        case SalesPurchBookTransType_RU::TaxCorrection     :
            ret = FactureJour_RU::findTaxCorrection(this.FactureId, FactureBook_RU::Purch).Module;
            break;
        case SalesPurchBookTransType_RU::PrepaymentStorno  :
            ret = FactureModule_RU::Cust;
            break;
        case SalesPurchBookTransType_RU::GTD               :
        case SalesPurchBookTransType_RU::CustomCorrection  :
            ret = FactureModule_RU::Vend;
            break;
    }

    return ret;
}

public FactureType_RU factureType()
{
    FactureType_RU  ret = FactureType_RU::All;

    switch (this.TransType)
    {
        case SalesPurchBookTransType_RU::AdvanceReport     :
            ret = FactureType_RU::AdvanceReport;
            break;
        case SalesPurchBookTransType_RU::CreditNote        :
        case SalesPurchBookTransType_RU::Invoice           :
            ret = FactureType_RU::Invoice;
            break;
        case SalesPurchBookTransType_RU::TaxCorrection     :
            ret = FactureType_RU::TaxCorrection;
            break;
        case SalesPurchBookTransType_RU::PrepaymentStorno  :
            ret = FactureType_RU::Prepayment;
            break;
        case SalesPurchBookTransType_RU::SumDiff           :
            ret = FactureJour_RU::find(this.FactureId, FactureModule_RU::Vend).FactureType;
            break;
        case SalesPurchBookTransType_RU::ExchAdj           :
            ret = FactureType_RU::ExchAdj;
            break;
        case SalesPurchBookTransType_RU::AdvanceAdjustment :
            ret = FactureType_RU::AdvanceAdjustment;
            break;
    }

    return ret;
}

public void initFromPBookVATProcessLogTrans(PurchBookVATProcessLogTrans_RU _purchBookVATProcessLogTrans)
{
    BookStructVATAmounts_RU usedAmounts = new BookStructVATAmounts_RU();

    this.FactureExternalId      = _purchBookVATProcessLogTrans.FactureExternalId;
    this.FactureDate            = _purchBookVATProcessLogTrans.FactureDate;
    this.PaymentDate            = _purchBookVATProcessLogTrans.PaymentDate;
    this.DeliveryDate           = _purchBookVATProcessLogTrans.DeliveryDate;
    this.AccountName            = _purchBookVATProcessLogTrans.AccountName;
    this.CountryGTD             = _purchBookVATProcessLogTrans.CountryGTD;

    this.UsedAmounts            = usedAmounts.pack();
    this.FactureAmounts         = _purchBookVATProcessLogTrans.FacturedAmounts;

    this.AccountNum             = _purchBookVATProcessLogTrans.AccountNum;
    this.FactureId              = _purchBookVATProcessLogTrans.FactureId;
    this.TransType              = _purchBookVATProcessLogTrans.TransType;
    this.InvoiceRecIdRef        = _purchBookVATProcessLogTrans.InvoiceRecIdRef;
    this.PaymentRecIdRef        = _purchBookVATProcessLogTrans.PaymentRecIdRef;
    this.RefTableId             = _purchBookVATProcessLogTrans.RefTableId;
    this.SettlementDate         = _purchBookVATProcessLogTrans.SettlementDate;
    this.SettlementRecId        = _purchBookVATProcessLogTrans.SettlementRecId;
    this.PurchBookTransDate     = max(_purchBookVATProcessLogTrans.FactureDate,
                                      max(_purchBookVATProcessLogTrans.SettlementDate,
                                          max(_purchBookVATProcessLogTrans.PaymentDate, _purchBookVATProcessLogTrans.DeliveryDate)));
    this.Reversed               = _purchBookVATProcessLogTrans.ReverseDate ? NoYes::Yes : NoYes::No;
    this.ReverseDate            = _purchBookVATProcessLogTrans.ReverseDate;
    this.ReverseSettlementRecId = _purchBookVATProcessLogTrans.ReverseSettlementRecId;

    this.ProcessingType         = _purchBookVATProcessLogTrans.ProcessingType;
    this.InvoiceCompany         = _purchBookVATProcessLogTrans.InvoiceCompany;
    this.PaymentCompany         = _purchBookVATProcessLogTrans.PaymentCompany;
    this.CorrectedPeriod        = _purchBookVATProcessLogTrans.CorrectedPeriod;
    this.CorrectedFactureId     = _purchBookVATProcessLogTrans.CorrectedFactureId;
}

