public static void createLine(TmpPurchBookVATProcessLogTrans_RU _purchBookVATProcessLogTrans,
                              FactureId_RU                      _factureId,
                              FactureExternalId_RU              _factureExternalId,
                              FactureDate_RU                    _factureDate,
                              TransDate                         _offsetTransDate,
                              TransDate                         _deliveryDate,
                              TransDate                         _settlementDate,
                              CustVendAC                        _accountNum,
                              Name                              _accountName,
                              Str1260                           _countryGTD,
                              SalesPurchBookTransType_RU        _transType,
                              RecId                             _transRecId,
                              RecId                             _offsetRecId,
                              RefTableId                        _refTableId,
                              CompanyId                         _transCompany,
                              CompanyId                         _offsetCompany,
                              RecId                             _settlementRecId,
                              RecId                             _reverseSettlementRecId = 0,
                              TransDate                         _reverseDate            = _settlementDate,
                              TransDate                         _maxAssetOpenDate       = dateNull(),
                              PurchBookProcessingType_RU        _processingType         = PurchBookProcessingType_RU::Standard,
                              TransDate                         _correctedPeriod        = dateNull(),
                              FactureId_RU                      _correctedFactureId     = '',
                              boolean                           _nullReverseDate        = false,
                              FactureReportDate_RU              _factureReportDate      = _factureDate)
{
    _purchBookVATProcessLogTrans.clear();

    _purchBookVATProcessLogTrans.FactureId          = _factureId;
    _purchBookVATProcessLogTrans.FactureExternalId  = _factureExternalId;
    _purchBookVATProcessLogTrans.FactureDate        = _factureDate;
    _purchBookVATProcessLogTrans.PaymentDate        = _offsetTransDate;
    _purchBookVATProcessLogTrans.DeliveryDate       = _deliveryDate;
    _purchBookVATProcessLogTrans.AccountNum         = _accountNum;
    _purchBookVATProcessLogTrans.AccountName        = _accountName;
    _purchBookVATProcessLogTrans.CountryGTD         = _countryGTD;
    _purchBookVATProcessLogTrans.InvoiceRecIdRef    = _transRecId;
    _purchBookVATProcessLogTrans.PaymentRecIdRef    = _offsetRecId;
    _purchBookVATProcessLogTrans.RefTableId         = _refTableId;
    _purchBookVATProcessLogTrans.InvoiceCompany     = _transCompany;
    _purchBookVATProcessLogTrans.PaymentCompany     = _offsetCompany;
    _purchBookVATProcessLogTrans.SettlementDate     = _settlementDate;
    _purchBookVATProcessLogTrans.SettlementRecId    = _settlementRecId;
    _purchBookVATProcessLogTrans.PurchBookTransDate = max(_maxAssetOpenDate,
                                                          max(_settlementDate,
                                                              max(_factureReportDate,
                                                                  max(_offsetTransDate,
                                                                      _deliveryDate))));
    _purchBookVATProcessLogTrans.TransType          = _transType;
    _purchBookVATProcessLogTrans.CorrectedPeriod    = _correctedPeriod;
    _purchBookVATProcessLogTrans.CorrectedFactureId = _correctedFactureId;
    _purchBookVATProcessLogTrans.ProcessingType     = _processingType;
    if (_reverseDate != _settlementDate)
    {
        _purchBookVATProcessLogTrans.Reversed               = NoYes::Yes;
        _purchBookVATProcessLogTrans.ReverseDate            = _reverseDate;
        _purchBookVATProcessLogTrans.ReverseSettlementRecId = _reverseSettlementRecId;
    }
    if (_nullReverseDate)
    {
        _purchBookVATProcessLogTrans.Reversed               = NoYes::No;
        _purchBookVATProcessLogTrans.ReverseDate            = dateNull();
        _purchBookVATProcessLogTrans.ReverseSettlementRecId = 0;
    }
    _purchBookVATProcessLogTrans.insert();
}

public static void updateLine(TmpPurchBookVATProcessLogTrans_RU     _purchBookVATProcessLogTrans,
                              BookStructVATAmounts_RU               _factureAmounts,
                              BookStructVATAmounts_RU               _settledAmounts,
                              BookStructVATAmounts_RU               _usedAmounts,
                              TransDate                             _fromDate,
                              TmpPurchBookVATProcessLogTransOper_RU _purchBookVATProcessLogTransOper = null)
{
    #VATTransitionPeriod_RU
    TmpPurchBookVATProcessLogTrans_RU   tmpLogTrans;
    SalesPurchBookTransType_RU          transType;
    VendInvoiceJour                     jour, jourOffset;
    BookStructVATAmounts_RU             amounts1,
                                        amounts2,
                                        amountsZero;
    RefRecId                            originalJourRecId,
                                        originalJourOffsetRecId;

    if (_settledAmounts.amountInclVAT() - _usedAmounts.amountInclVAT())
    {
        _purchBookVATProcessLogTrans.FactureAmounts     = _factureAmounts.pack();
        _purchBookVATProcessLogTrans.SettledAmounts     = _settledAmounts.pack();
        _purchBookVATProcessLogTrans.UsedAmounts        = _usedAmounts.pack();

        // during processing after transition period PurchBookTransDate should be 2008 year to correctly process of blocked amounts
        if (_fromDate > #TransitionEndDate)
        {
            _purchBookVATProcessLogTrans.PurchBookTransDate = max(_purchBookVATProcessLogTrans.PurchBookTransDate, #TransitionEndDate + 1);
        }

        _purchBookVATProcessLogTrans.update();

        if ((_purchBookVATProcessLogTrans.TransType == SalesPurchBookTransType_RU::Invoice     ||
             _purchBookVATProcessLogTrans.TransType == SalesPurchBookTransType_RU::CreditNote) &&
             _purchBookVATProcessLogTrans.SettlementRecId)
        {
            tmpLogTrans.setTmpData(_purchBookVATProcessLogTrans);
            transType = _purchBookVATProcessLogTrans.TransType == SalesPurchBookTransType_RU::Invoice ?
                        SalesPurchBookTransType_RU::CreditNote                                        :
                        SalesPurchBookTransType_RU::Invoice;

            select firstonly forupdate tmpLogTrans
                where tmpLogTrans.InvoiceRecIdRef   == _purchBookVATProcessLogTrans.PaymentRecIdRef
                   && tmpLogTrans.InvoiceCompany    == _purchBookVATProcessLogTrans.PaymentCompany
                   && tmpLogTrans.PaymentRecIdRef   == _purchBookVATProcessLogTrans.InvoiceRecIdRef
                   && tmpLogTrans.PaymentCompany    == _purchBookVATProcessLogTrans.InvoiceCompany
                   && tmpLogTrans.FactureExternalId == _purchBookVATProcessLogTrans.FactureExternalId
                   && tmpLogTrans.TransType         == transType
                   && tmpLogTrans.SettlementRecId;

            if (tmpLogTrans)
            {
                amountsZero = new BookStructVATAmounts_RU();
                amounts1    = BookStructVATAmounts_RU::create(tmpLogTrans.SettledAmounts);
                amounts2    = BookStructVATAmounts_RU::create(_purchBookVATProcessLogTrans.SettledAmounts);

                amounts1.addAmounts2(amounts2);

                if (amounts1.pack() == amountsZero.pack())
                {
                    jour        = VendTrans::findByCompany(tmpLogTrans.InvoiceCompany, tmpLogTrans.InvoiceRecIdRef).vendInvoiceJour_RU();
                    jourOffset  = VendTrans::findByCompany(tmpLogTrans.PaymentCompany, tmpLogTrans.PaymentRecIdRef).vendInvoiceJour_RU();

                    originalJourRecId       = jour.refOriginalInvoiceRecId_RU();
                    originalJourOffsetRecId = jourOffset.refOriginalInvoiceRecId_RU();

                    if ((jour.CorrectionType_RU                == FactureCorrectionType_RU::Revision   &&
                         (originalJourRecId                    == originalJourOffsetRecId              ||
                          originalJourRecId                    == jourOffset.RecId))                   ||
                        (jourOffset.CorrectionType_RU          == FactureCorrectionType_RU::Revision   &&
                         (originalJourOffsetRecId              == originalJourRecId                    ||
                          originalJourOffsetRecId              == jour.RecId)))
                    {
                        if (_purchBookVATProcessLogTransOper)
                        {
                            delete_from _purchBookVATProcessLogTransOper
                                where _purchBookVATProcessLogTransOper.RefRecId == tmpLogTrans.RecId;

                            delete_from _purchBookVATProcessLogTransOper
                                where _purchBookVATProcessLogTransOper.RefRecId == _purchBookVATProcessLogTrans.RecId;
                        }

                        tmpLogTrans.delete();
                        _purchBookVATProcessLogTrans.delete();
                    }
                }
            }
        }
    }
    else
    {
        _purchBookVATProcessLogTrans.delete();
    }
}
