public DimensionDefault dimensionDefault()
{
    return this.DeriveDim ? this.DefaultDimensionPosted : this.DefaultDimension;
}

public void initFromSBookVATProcessLogTransOper(SalesBookVATProcessLogTransOper_RU  _salesBookVATProcessLogTransOper)
{
    this.LedgerDimension                  = _salesBookVATProcessLogTransOper.LedgerDimension;
    this.TaxAmount                        = _salesBookVATProcessLogTransOper.TaxAmount;
    this.TransTxt                         = _salesBookVATProcessLogTransOper.TransTxt;
    this.SalesBookVATProcessParameters_RU = _salesBookVATProcessLogTransOper.SalesBookVATProcessParameters_RU;
    this.Percent                          = _salesBookVATProcessLogTransOper.Percent;
    this.TransDate                        = _salesBookVATProcessLogTransOper.TransDate;
    this.Blocked                          = _salesBookVATProcessLogTransOper.Blocked;
    this.AmountLiableToVAT                = _salesBookVATProcessLogTransOper.AmountLiableToVAT;
    this.OrigRecId                        = _salesBookVATProcessLogTransOper.RecId;
    this.DefaultDimension                 = _salesBookVATProcessLogTransOper.DefaultDimension;
    this.DeriveDim                        = _salesBookVATProcessLogTransOper.DeriveDim;
    this.DefaultDimensionPosted           = _salesBookVATProcessLogTransOper.DefaultDimensionPosted;
    this.CanceledRefRecId                 = _salesBookVATProcessLogTransOper.CanceledRefRecId;
    this.OffsetLedgerDimension            = _salesBookVATProcessLogTransOper.OffsetLedgerDimension;
    this.TaxCode                          = _salesBookVATProcessLogTransOper.TaxCode;
    this.FactureAmounts                   = _salesBookVATProcessLogTransOper.FactureAmounts;
    this.InventTransId                    = _salesBookVATProcessLogTransOper.InventTransId;
    this.RestoringType                    = _salesBookVATProcessLogTransOper.RestoringType;
}

public BookStructSmallVATAmounts_RU usedAmounts_InvoiceRecId(
                                          SalesPurchBookDataMap_RU      _salesPurchBookDataMap,
                                          recId                         _transRecId,
                                          CompanyId                     _transCompany,
                                          boolean                       _blocked,
                                          SalesBookVATRestoringType_RU  _restoringType = SalesBookVATRestoringType_RU::None)
{
    TmpSalesBookVATProcessLogTransOper_RU  processLogTransOper;
    TmpSalesBookVATProcessLogTrans_RU      processLogTrans;
    BookStructSmallVATAmounts_RU           amounts = new BookStructSmallVATAmounts_RU();

    processLogTransOper.setTmpData(this);
    processLogTrans.setTmpData(_salesPurchBookDataMap);

    select sum(AmountLiableToVAT), sum(TaxAmount) from processLogTransOper
        where processLogTransOper.Blocked  == _blocked &&
             (prmisDefault(_restoringType) || processLogTransOper.RestoringType == _restoringType)
    exists join processLogTrans
        where processLogTrans.InvoiceRecIdRef == _transRecId   &&
              processLogTrans.InvoiceCompany  == _transCompany &&
              processLogTransOper.RefRecId    == processLogTrans.RecId;

    amounts.addAmounts(processLogTransOper.AmountLiableToVAT + processLogTransOper.TaxAmount, processLogTransOper.AmountLiableToVAT, processLogTransOper.TaxAmount);

    return amounts;
}

public boolean validateField(fieldId _fieldIdToCheck)
{
    boolean     ret;

    ret = super(_fieldIdToCheck);

    if (ret)
    {
        switch (_fieldIdToCheck)
        {
            case fieldNum(TmpSalesBookVATProcessLogTransOper_RU, LedgerDimension) :
                if (! this.LedgerDimension)
                {
                    ret = checkFailed(strFmt("@SYS26332", fieldPName(TmpSalesBookVATProcessLogTransOper_RU, LedgerDimension)));
                }
                break;

            case fieldNum(TmpSalesBookVATProcessLogTransOper_RU, OffsetLedgerDimension) :
                if (! this.OffsetLedgerDimension)
                {
                    ret = checkFailed(strFmt("@SYS26332", fieldPName(TmpSalesBookVATProcessLogTransOper_RU, OffsetLedgerDimension)));
                }
                break;
        }
    }

    return ret;
}

public static BookStructSmallVATAmounts_RU createLines(TmpSalesBookVATProcessLogTransOper_RU   _tmpProcessLogTransOper,
                                                              FactureId_RU                            _factureId,
                                                              TransDate                               _transDate,
                                                              SalesBookVATProcessParametersRecId_RU   _operationRecId,
                                                              DimensionDefault                        _defaultDimension,
                                                              TaxCode                                 _taxCode,
                                                              InventTransId                           _inventTransId,
                                                              SalesPurchBookTransType_RU              _transType,
                                                              BookStructSmallVATAmounts_RU            _factureAmounts,
                                                              BookStructSmallVATAmounts_RU            _settledAmounts,
                                                              BookStructSmallVATAmounts_RU            _usedAmounts,
                                                              BookStructSmallVATAmounts_RU            _blockedAmounts,
                                                              SalesBookProcessingType_RU              _processingType,
                                                              recId                                   _transRecId,
                                                              recId                                   _offsetRecId,
                                                              CompanyId                               _transCompany,
                                                              CompanyId                               _offsetCompany,
                                                              recId                                   _settlementRecId,
                                                              recId                                   _processLogTransRecId,
                                                              TransDate                               _lastBookClosingDate,
                                                              TransDate                               _settlementDate = _transDate,
                                                              SalesBookVATRestoringType_RU            _restoringType  = SalesBookVATRestoringType_RU::None)
{
    BookStructSmallVATAmounts_RU        remainAmounts  = new BookStructSmallVATAmounts_RU();

    SalesBookVATProcessLogTransOper_RU  processLogTransOper;
    SalesBookVATProcessLogTrans_RU      processLogTrans;
    SalesBookVATProcessParameters_RU    vatProcessParameters = _operationRecId                                         ?
                                                               SalesBookVATProcessParameters_RU::find(_operationRecId) :
                                                               SalesBookVATProcessParameters_RU::defaultParms(_processingType, _restoringType);

    SalesBookVATLedgerPosting_RU        ledgerPosting = SalesBookVATLedgerPosting_RU::ledgerPosting(_taxCode, vatProcessParameters.RecId);
    LedgerDimensionDefaultAccount       defaultAccount;
    LedgerDimensionDefaultAccount       offsetDefaultAccount;
    Percent                             totalPercent;

    void insertLogTransOper(BookStructSmallVATAmounts_RU _logTransOperAmounts, NoYes _blocked)
    {
        _tmpProcessLogTransOper.clear();

        _tmpProcessLogTransOper.Blocked                          = _blocked;
        _tmpProcessLogTransOper.BlockedAuto                      = _blocked;
        _tmpProcessLogTransOper.SalesBookVATProcessParameters_RU = vatProcessParameters.RecId;
        _tmpProcessLogTransOper.TransDate                        = max(_transDate, _lastBookClosingDate + 1);
        _tmpProcessLogTransOper.DefaultDimension                 = _defaultDimension;
        _tmpProcessLogTransOper.DeriveDim                        = vatProcessParameters.DeriveDim;
        _tmpProcessLogTransOper.DefaultDimensionPosted           = vatProcessParameters.DeriveDim ? vatProcessParameters.DefaultDimension : _defaultDimension;
        _tmpProcessLogTransOper.TaxCode                          = _taxCode;
        _tmpProcessLogTransOper.InventTransId                    = _inventTransId;
        _tmpProcessLogTransOper.RestoringType                    = _restoringType;

        if (_taxCode &&
           ! (_processingType == SalesBookProcessingType_RU::OverdueConfirmation && _transType == SalesPurchBookTransType_RU::Invoice) &&
           ! (_processingType == SalesBookProcessingType_RU::VATZero || _processingType == SalesBookProcessingType_RU::VATZeroCorrection))
        {
            if (! ledgerPosting.LedgerDimension || ! ledgerPosting.OffsetLedgerDimension)
            {
                [defaultAccount, offsetDefaultAccount] = SalesBookVATProcessParameters_RU::calcLedgerDimensions(_taxCode,
                                                                                                                vatProcessParameters.RecId,
                                                                                                                _transType,
                                                                                                                _transRecId,
                                                                                                                _offsetRecId,
                                                                                                                _transCompany,
                                                                                                                _offsetCompany);
            }

            _tmpProcessLogTransOper.LedgerDimension       = ledgerPosting.LedgerDimension       ? ledgerPosting.LedgerDimension       : defaultAccount;
            _tmpProcessLogTransOper.OffsetLedgerDimension = ledgerPosting.OffsetLedgerDimension ? ledgerPosting.OffsetLedgerDimension : offsetDefaultAccount;
        }

        _tmpProcessLogTransOper.TransTxt       = vatProcessParameters.Description;
        _tmpProcessLogTransOper.FactureAmounts = _factureAmounts.pack();
        _tmpProcessLogTransOper.SettledAmounts = _settledAmounts.pack();
        _tmpProcessLogTransOper.UsedAmounts    = _usedAmounts.pack();

        _tmpProcessLogTransOper.AmountLiableToVAT = _logTransOperAmounts.amountLiableToVAT();
        _tmpProcessLogTransOper.TaxAmount         = _logTransOperAmounts.taxAmountVAT();
        _tmpProcessLogTransOper.Percent           = BookProcessVATEngine_RU::pct(_logTransOperAmounts.amountLiableToVAT(), _settledAmounts.amountLiableToVAT() - _usedAmounts.amountLiableToVAT());
        _tmpProcessLogTransOper.AllowDelete       = _tmpProcessLogTransOper.Percent  == 100 ? NoYes::No : _blocked;
        _tmpProcessLogTransOper.RefRecId          = _processLogTransRecId;

        _tmpProcessLogTransOper.insert();

        totalPercent += _tmpProcessLogTransOper.Percent;
    }

    remainAmounts.addAmounts2(_settledAmounts);
    remainAmounts.subAmounts2(_usedAmounts);
    remainAmounts.subAmounts2(_blockedAmounts);

    if (remainAmounts.amountInclVAT())
    {
        insertLogTransOper(remainAmounts, NoYes::No);
    }
    if (_blockedAmounts.amountInclVAT())
    {
        while select processLogTransOper
            where processLogTransOper.DefaultDimension == _defaultDimension &&
                  processLogTransOper.TaxCode          == _taxCode          &&
                  processLogTransOper.Blocked          == NoYes::Yes        &&
                  processLogTransOper.BlockedAuto      == NoYes::No         &&
                  processLogTransOper.CanceledRefRecId == 0                 &&
                  processLogTransOper.InventTransId    == _inventTransId    &&
                  processLogTransOper.RestoringType    == _restoringType
        exists join processLogTrans
            where processLogTrans.RecId           == processLogTransOper.RefRecId &&
                  processLogTrans.FactureId       == _factureId            &&
                  processLogTrans.InvoiceRecIdRef == _transRecId           &&
                  processLogTrans.InvoiceCompany  == _transCompany         &&
                  processLogTrans.PaymentRecIdRef == _offsetRecId          &&
                  processLogTrans.PaymentCompany  == _offsetCompany        &&
                  processLogTrans.SettlementDate  == _settlementDate       &&
                  processLogTrans.SettlementRecId == _settlementRecId
        {
            _tmpProcessLogTransOper.clear();

            _tmpProcessLogTransOper.initFromSBookVATProcessLogTransOper(processLogTransOper);
            _tmpProcessLogTransOper.SettledAmounts = _settledAmounts.pack();
            _tmpProcessLogTransOper.FactureAmounts = _factureAmounts.pack();
            _tmpProcessLogTransOper.InventTransId  = _inventTransId;
            _tmpProcessLogTransOper.UsedAmounts    = _usedAmounts.pack();
            _tmpProcessLogTransOper.TransDate      = max(_tmpProcessLogTransOper.TransDate, _lastBookClosingDate + 1);
            _tmpProcessLogTransOper.Percent        = BookProcessVATEngine_RU::pct(_tmpProcessLogTransOper.AmountLiableToVAT, _settledAmounts.amountLiableToVAT()  - _usedAmounts.amountLiableToVAT());
            _tmpProcessLogTransOper.RefRecId       = _processLogTransRecId;
            _tmpProcessLogTransOper.insert();

            totalPercent += _tmpProcessLogTransOper.Percent;
        }
    }

    if (totalPercent > 100)
    {
        _tmpProcessLogTransOper.Percent -= totalPercent - 100;
        _tmpProcessLogTransOper.update();
    }

    return remainAmounts;
}
