/// <summary>
/// Display the address information.
/// </summary>
/// <param name="_transTaxInformation">
/// The transaction tax information.
/// </param>
/// <returns>
/// The instance of the <c>LogisticsAddressing</c> class.
/// </returns>
display LogisticsAddressing displayAddress(TransTaxInformation _transTaxInformation)
{
    LogisticsPostalAddress  logisticsPostalAddress;

    logisticsPostalAddress = LogisticsLocationEntity::location2PostalAddress(_transTaxInformation.CompanyLocation, DateTimeUtil::getSystemDateTime(), true);

    return logisticsPostalAddress.Address;
}

/// <summary>
/// Display the type label for transaction tax information.
/// </summary>
/// <param name="_transTaxInformation">
/// The transaction tax information.
/// </param>
/// <returns>
/// The label.
/// </returns>
display str displayTransTaxInformationTypeLabel(TransTaxInformation _transTaxInformation)
{
    DictEnum    dictEnum;
    str         label;

    if (_transTaxInformation.Type != TransTaxInformationType_IN::Normal)
    {
        dictEnum = new DictEnum(Enumnum(TransTaxInformationType_IN));
        label = dictEnum.value2Label(_transTaxInformation.Type);
    }

    return label;
}

/// <summary>
/// Initializes the default values.
/// </summary>
public void initValue()
{
    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        this.ServiceTaxIsRecoverable = NoYes::Yes;
    }
}

