/// <summary>
/// Dispaly the vednor tax information tax id.
/// </summary>
/// <param name = "_transTaxInformation">the <c>TransTaxInformation</c> record.</param>
/// <returns>The vednor tax information tax id.</returns>
display TaxRegistrationNumber dispalyVendorTaxId(TransTaxInformation _transTaxInformation)
{
    TaxInformation_IN taxInformation = TaxInformation_IN::find(_transTaxInformation.VendorTaxInformation);
    TaxRegistration taxRegistration = TaxRegistration::find(taxInformation.TaxID);
    return TaxRegistration.RegistrationNumber;
}

