/// <summary>
/// Display the customer deemed export.
/// </summary>
/// <returns>
/// Deemed export is yes when TaxGSTEPZCode_IN is BTP/EHTP/EOU/STP or Registration number's Type = UID.
/// </returns>
public display TaxGSTIsDeemedExport_IN displayCustomerDeemedExport(TransTaxInformation _transTaxInformation)
{
    LogisticsPostalAddress  logisticsPostalAddress = LogisticsLocationEntity::location2PostalAddress(_transTaxInformation.CustomerLocation, DateTimeUtil::getSystemDateTime(), true);
    LogisticsLocationExt    logisticsLocationExt = LogisticsLocationExt::find(logisticsPostalAddress.Location);
    NoYes                   ret = logisticsLocationExt.isDeemedExportType();

    if (!ret)
    {
        TaxInformation_IN         taxInformation_IN = TaxInformation_IN::find(this.CustomerTaxInformation);
        TaxRegistrationNumbers_IN taxRegistrationNumbers_IN = TaxRegistrationNumbers_IN::find(taxInformation_IN.GSTIN);

        ret = taxRegistrationNumbers_IN.Type == GSTType_IN::UID;
    }

    return ret;
}

/// <summary>
/// Display the customer SEZ export.
/// </summary>
/// <returns>
/// SEZ is yes when TaxGSTEPZCode_IN is SEZ.
/// </returns>
public display TaxGSTIsSEZParty_IN displayCustomerSEZ(TransTaxInformation _transTaxInformation)
{
    LogisticsPostalAddress  logisticsPostalAddress = LogisticsLocationEntity::location2PostalAddress(_transTaxInformation.CustomerLocation, DateTimeUtil::getSystemDateTime(), true);
    LogisticsLocationExt    logisticsLocationExt = LogisticsLocationExt::find(logisticsPostalAddress.Location);

    return logisticsLocationExt.TaxGSTEPZCode_IN == TaxGSTEPZCode_IN::SEZ;
}

