public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    switch(_fieldIdToCheck)
    {
        case fieldNum(TransTaxInformation, HSNCodeTable) :
        case fieldNum(TransTaxInformation, ServiceAccountingCodeTable) :
            if (this.HSNCodeTable && this.ServiceAccountingCodeTable)
                {
                    ret = ret && checkFailed(strFmt("@TaxGST:HSNAndSACCodeCheck"));
                }
        break;
    }

    return ret;
}

    public void modifiedField(FieldId _fieldId)
    {
        #isoCountryRegionCodes
        TransTaxInformation transTaxInformationLoc;
        TransTaxInformationType_IN transTaxInformationType;
        TransitDocumentTransTaxInfoRelation_IN transitDocumentTransTaxInfoRelation;
        TransitDocumentTransTaxInfoRelation_IN transitDocumentTransTaxInfoRelationLoc;

        super(_fieldId);

        switch(_fieldId)
        {
            case fieldNum(TransTaxInformation, Exempt) :
            case fieldNum(TransTaxInformation, InclTax) :
            case fieldNum(TransTaxInformation, TaxRateType) :
            // <GIN>
			case fieldNum(TransTaxInformation, NonGST) :
            case fieldNum(TransTaxInformation, HSNCodeTable) :
            case fieldNum(TransTaxInformation, ServiceAccountingCodeTable) :
            case fieldNum(TransTaxInformation, SalesTaxFormTypes) :
            // </GIN>
                if (this.type != TransTaxInformationType_IN::Normal)
                {
                    transTaxInformationType = this.type == TransTaxInformationType_IN::InventTransferTo ? TransTaxInformationType_IN::InventTransferFrom : TransTaxInformationType_IN::InventTransferTo;

                    select firstonly TransactionTableId, TransactionRecId from transitDocumentTransTaxInfoRelation
                        where transitDocumentTransTaxInfoRelation.TransTaxInformationRecId == this.RecId;

                    transTaxInformationLoc = TransTaxInformationHelper::newHelper().getTransTaxInformation(transitDocumentTransTaxInfoRelation.TransactionTableId, transitDocumentTransTaxInfoRelation.TransactionRecId, transTaxInformationType);

                    if (transTaxInformationLoc)
                    {
                        ttsbegin;
                        transTaxInformationLoc.selectForUpdate(true);
                        transTaxInformationLoc.Exempt = this.Exempt;
                        transTaxInformationLoc.InclTax = this.InclTax;
                        transTaxInformationLoc.TaxRateType = this.TaxRateType;
                        // <GIN>
                        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
                        {
                            transTaxInformationLoc.HSNCodeTable = this.HSNCodeTable;
                            transTaxInformationLoc.ServiceAccountingCodeTable = this.ServiceAccountingCodeTable;
                            transTaxInformationLoc.SalesTaxFormTypes = this.SalesTaxFormTypes;
                            transTaxInformationLoc.NonGST = this.NonGST;
                        }
                        // </GIN>
                        transTaxInformationLoc.update();
                        ttscommit;

                        if(this.isFormDataSource())
                        {
                            this.dataSource().refresh();
                            this.dataSource().research();
                        }
                    }
                }
                break;
        }
    }

