/// <summary>
/// Gets field name of the company registration number for tax type specified.
/// </summary>
/// <param name = "_taxType">The tax type.</param>
/// <returns>The field name of the company registration number.</returns>
internal static FieldName getCompanyRegNumberFieldNameByTaxType(TaxType_IN _taxType)
{
    FieldName companyRegistrationNumberFieldName;

    switch (_taxType)
    {
        case TaxType_IN::VAT:
            companyRegistrationNumberFieldName = fieldId2PName(tableNum(TransTaxInformation), fieldNum(TransTaxInformation, VATTIN));
            break;
        case TaxType_IN::SalesTax:
            companyRegistrationNumberFieldName = fieldId2PName(tableNum(TransTaxInformation), fieldNum(TransTaxInformation, SalesTaxRegistrationNumber));
            break;
        case TaxType_IN::Excise:
            companyRegistrationNumberFieldName = fieldId2PName(tableNum(TransTaxInformation), fieldNum(TransTaxInformation, ExciseECCRegistrationNumber));
            break;
        case TaxType_IN::ServiceTax:
            companyRegistrationNumberFieldName = fieldId2PName(tableNum(TransTaxInformation), fieldNum(TransTaxInformation, ServiceTaxRegistrationNumber));
            break;
        case TaxType_IN::Customs:
            companyRegistrationNumberFieldName = fieldId2PName(tableNum(TransTaxInformation), fieldNum(TransTaxInformation, CustomsIECRegistrationNumber));
            break;
        case TaxType_IN::GST:
            companyRegistrationNumberFieldName = fieldId2PName(tableNum(TransTaxInformation), fieldNum(TransTaxInformation, GSTIN));
            break;
        default:
            companyRegistrationNumberFieldName = '';
    }

    return companyRegistrationNumberFieldName;
}

public void update()
{
    TransTaxInformation origTransTaxInfo = this.orig();

    ttsbegin;
    this.updateTaxInformationForMarkup();
    super();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && this.HeadingTableId == tableNum(TaxEngineLedgerJournalTransHeader)
        && (origTransTaxInfo.CustomerLocation != this.CustomerLocation
            || origTransTaxInfo.VendorLocation != this.VendorLocation))
    {
        LedgerJournalTransTaxExtensionIN::updateWhenTransTaxInfoCustVendLocationModified(this);
    }
    ttscommit;
}

private void updateTaxInformationForMarkup()
{
    TransitDocumentTransTaxInfoRelation_IN trans = this.getSourceTransactionOfTaxInformation();
    if (this.isMarkupTaxInformationToBeUpdated(trans.TransactionTableId, trans.TransactionRecId))
    {
        SetEnumerator markupTransSetEnumerator = MarkupTrans::getMarkupRecIdsBySource(trans.TransactionTableId, trans.TransactionRecId);
        while (markupTransSetEnumerator.moveNext())
        {
            RecId markupTransRecId = markupTransSetEnumerator.current();
            TransTaxInformation markupTaxInformation = TransTaxInformationHelper::findOrCreateTransTaxInformation(tableNum(MarkupTrans), markupTransRecId, true);
            if (markupTaxInformation)
            {
                markupTaxInformation.CompanyLocation = this.CompanyLocation;
                markupTaxInformation.update();
            }
        }
    }
}

private boolean isMarkupTaxInformationToBeUpdated(TableId _tableId, RecId _recId)
{
    return (this.CompanyLocation != this.orig().CompanyLocation)
        && (_tableId == tableNum(PurchLine)
            || _tableId == tableNum(SalesLine)
            || _tableId == tableNum(SalesQuotationLine)
            || _tableId == tableNum(CustInvoiceLine)
            || _tableId == tableNum(VendInvoiceInfoLine))
        && MarkupTrans::exist(_tableId, _recId);
}

internal TransitDocumentTransTaxInfoRelation_IN getSourceTransactionOfTaxInformation()
{
    TransitDocumentTransTaxInfoRelation_IN transitDocumentTransTaxInfoRelation;
    select firstonly TransactionTableId, TransactionRecId from transitDocumentTransTaxInfoRelation
        where transitDocumentTransTaxInfoRelation.TransTaxInformationRecId == this.RecId;
    return transitDocumentTransTaxInfoRelation;
}
