/// <summary>
/// Adjusts the <c>VatDueDate_W</c> field.
/// </summary>
/// <param name="_vatDueDate_W">
/// The <c>VatDueDate_W</c>.
/// </param>
public void adjustVATDueDate_W(VatDueDate_W _vatDueDate_W = dateNull())
{
    #ISOCountryRegionCodes

    VendInvoiceInfoTable vendInvoiceInfoTable = this.VendInvoiceInfoTable();

    if (! prmisDefault(_vatDueDate_W))
    {
        this.VatDueDate_W = _vatDueDate_W;
        TaxUncommitted::deleteForDocumentHeader(vendInvoiceInfoTable.TableId, vendInvoiceInfoTable.RecId);
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]) && PlTaxDueTable::find(vendInvoiceInfoTable.vendInvoiceInfoTable_W().TaxPeriodPaymentCode_PL).VatDateIsPaymentDate)
    {
        this.VatDueDate_W = vendInvoiceInfoTable.FixedDueDate;
    }

    if (TaxSpecificExchangeRateHelper::isTaxSpecificExchRateEnabledByBusinessProcess(TaxIntegrationBusinessProcess::Purchase))
    {
        TaxSpecificExchangeRate::updateOrDeleteByVATDueDate(vendInvoiceInfoTable.TableId, vendInvoiceInfoTable.RecId, vendInvoiceInfoTable.vendInvoiceInfoTable_W().VatDueDate_W);
    }
    else if (TaxParameters::isBankExchRateEnabled_W())
    {
        SalesTaxExchangeRateHelper::maintainSalesTaxTransactionExchangeRate(vendInvoiceInfoTable, vendInvoiceInfoTable.CurrencyCode, TransactionType::Purchase, vendInvoiceInfoTable.vendInvoiceInfoTable_W().VatDueDate_W);
    }
}

/// <summary>
/// Edit method for the Fiscal Document Series column
/// </summary>
/// <param name="_set">
/// Indicates wether the current value is being set or not
/// </param>
/// <param name="_fiscalDocumentSeries">
/// The current Fiscal Document Series value
/// </param>
/// <returns>
/// The fiscal document series depending on the issuer of the Vend Invoice
/// </returns>
/// <remarks>
/// This method was created because the series field required some intelligence when the Fiscal Establishment is in SCAN contingency
/// </remarks>
edit public FiscalDocumentSeries_BR editFiscalDocumentSeries_BR(boolean _set, FiscalDocumentSeries_BR _fiscalDocumentSeries)
{
    if (_set)
    {
        this.FiscalDocumentSeries_BR = _fiscalDocumentSeries;
    }

    return this.getFiscalDocumentSeries_BR();
}

/// <summary>
///     Controls if data source fields have to be enabled or not
/// </summary>

public void enableDataSourceFields_BR()
{
    FormDataObject  formDataObject;
    VendInvoiceInfoTable vendInvoiceInfoTable;
    boolean isCancelDocument;

    if (FormDataUtil::isFormDataSource(this))
    {
        vendInvoiceInfoTable = this.vendInvoiceInfoTable();

        formDataObject = FormDataUtil::getFormDataSource(this).object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentType_BR));
        formDataObject.allowEdit(vendInvoiceInfoTable.IsTheFiscalDocumentIssuer_BR());

        isCancelDocument = vendInvoiceInfoTable.isCancelDocument_BR();
        formDataObject = FormDataUtil::getFormDataSource(this).object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentSeries_BR));
        formDataObject.allowEdit(!isCancelDocument);

        formDataObject = FormDataUtil::getFormDataSource(this).object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentSpecie_BR));
        formDataObject.allowEdit(!isCancelDocument);

        formDataObject = FormDataUtil::getFormDataSource(this).object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentModel_BR));
        formDataObject.allowEdit(!isCancelDocument);
    }
}

/// <summary>
/// Gets the correct series for the fiscal document.
/// </summary>
/// <returns>
/// The fiscal document series for the Vendor Invoice which is the same of the Fiscal Document, since it's persisted upon posting it
/// </returns>
/// <remarks>
/// Series information depends on the type of action that user is performing:
/// - if reversing an original fiscal documentl docu/// - issuing a new fiscal document ( depending on the contingency mode of issuing fiscal establishment)
/// - if there is relation to another fiscal document then it depends fiscal establishment contingecy scenario
/// </remarks>
public FiscalDocumentSeries_BR getFiscalDocumentSeries_BR()
{
    if (this.vendInvoiceInfoTable().IsTheFiscalDocumentIssuer_BR())
    {
        return FiscalDocumentType_BR::getFiscalDocumentSeries(this.FiscalDocumentType_BR);
    }

    if (this.vendInvoiceInfoTable().purchTable().isReversingFiscalDocument_BR())
    {
        return this.vendInvoiceInfoTable().purchTable().purchTable_BR().findRelatedFiscalDocByInvoiceRefRecId().FiscalDocumentSeries;
    }

    return this.FiscalDocumentSeries_BR;
}

