{
    super();

    if (BrazilParameters::IsEnabled())
    {
        this.TypeOfCTe_BR = TypeOfCTe_BR::Blank;
    }
}

public void insert()
{
    if (this.VendInvoiceInfoTable)
    {
        super();
    }
}

public void modifiedField(FieldId _fieldId, VendInvoiceInfoTable _vendInvoiceInfoTable = this.SysExtensionSerializerExtensionMap::getBaseTable())
{
    boolean unapproveVariances;
    Object  formDataSource;
    FiscalDocumentType_BR   fiscalDocumentType;

    VendInvoiceInfoTable            vendInvoiceInfoTable = _vendInvoiceInfoTable as VendInvoiceInfoTable;

    super(_fieldId);

    vendInvoiceInfoTable.packVendInvoiceInfoTable_W(this);

    switch (_fieldId)
    {
        case fieldNum(VendInvoiceInfoTable_W, EmplAccount_RU):
            vendInvoiceInfoTable.PostingProfile = VendParameters::find().PostingProfile;

            break;

        case fieldNum(VendInvoiceInfoTable_W, VatDueDate_W):

            vendInvoiceInfoTable.type().defaultField(fieldNum(VendInvoiceInfoTable, CashDiscDate));
            vendInvoiceInfoTable.type().defaultField(fieldNum(VendInvoiceInfoTable, FixedDueDate));

            if (!vendInvoiceInfoTable.FixedExchRate && PurchParameters::find().DateOfExchRate_W == DateOfExchRate_W::DateOfVATRegister)
            {
                vendInvoiceInfoTable.initFromExchRates();
                unapproveVariances = true;
            }

            this.adjustVATDueDate_W(this.VatDueDate_W);

            break;

        case fieldNum(VendInvoiceInfoTable_W, FiscalDocumentType_BR):
                fiscalDocumentType              = FiscalDocumentType_BR::findRecId(this.FiscalDocumentType_BR);
                this.FiscalDocumentModel_BR     = fiscalDocumentType.FiscalDocumentModel;
                this.FiscalDocumentSpecie_BR    = fiscalDocumentType.FiscalDocumentSpecie;
                VendInvoiceInfoTable.setDatesByFiscalDocumentType_BR();
                break;

        case fieldNum(VendInvoiceInfoTable_W, FiscalDocumentModel_BR):
            if (!vendInvoiceInfoTable.hasToIssueFiscalDocument_BR())
            {
                vendInvoiceInfoTable.Num = '';
                this.AccessKey_BR = '';
                this.FiscalDocumentSeries_BR = '';
            }
            break;

        case fieldNum(VendInvoiceInfoTable_W, AccessKey_BR):
            if (!vendInvoiceInfoTable.hasToIssueFiscalDocument_BR())
            {
                this.AccessKey_BR = strKeep(this.AccessKey_BR, '0123456789');
                vendInvoiceInfoTable.Num = EFDocAccessKeyBase_BR::getInvoiceNumber(this.AccessKey_BR);
                this.FiscalDocumentSeries_BR = EFDocAccessKeyBase_BR::getInvoiceSeries(this.AccessKey_BR);
            }
            break;

        case fieldNum(VendInvoiceInfoTable_W, InvoiceSeries_MX):
            if (this.InvoiceSeries_MX != '')
            {
                this.CFDIUUID_MX = '';
            }
            break;

        case fieldNum(VendInvoiceInfoTable_W, CFDIUUID_MX):
            if (this.CFDIUUID_MX != '')
            {
                this.InvoiceSeries_MX = '';
            }
            break;
    }

    vendInvoiceInfoTable.packVendInvoiceInfoTable_W(this);

    if (unapproveVariances && FormDataUtil::isFormDataSource(this))
    {
        if (formDataSourceHasMethod(FormDataUtil::getFormDataSource(vendInvoiceInfoTable), identifierStr(unApproveVariances)))
        {
            formDataSource = FormDataUtil::getFormDataSource(this);
            formDataSource.unApproveVariances(unapproveVariances);
        }
    }
}

/// <summary>
/// Determines whether the given vend invoice must have an access key mandatory.
/// </summary>
/// <returns>
/// True if access key is mandatory.
/// </returns>
public boolean mustHaveAccessKey()
{
    VendInvoiceInfoTable vendInvoiceInfoTable = this.SysExtensionSerializerExtensionMap::getBaseTable();

    if (!vendInvoiceInfoTable.hasToIssueFiscalDocument_BR()
            && FiscalDocModel_BR::find(this.FiscalDocumentModel_BR).IsForNFeFederal)
    {
        return true;
    }

    return false;
}

public void update()
{
    if (this.VendInvoiceInfoTable)
    {
        VendInvoiceInfoTable_W orig = this.orig();

        super();

        if (this.hasTaxChanged(orig))
        {
            VendInvoiceInfoTable vendInvoiceInfoTable = this.vendInvoiceInfoTable();
            TaxUncommitted::deleteForDocumentHeader(vendInvoiceInfoTable.TableId, vendInvoiceInfoTable.RecId);
        }
    }
}

/// <summary>
/// Determines whether the tax information has changed.
/// </summary>
/// <param name="_originalVendInvoiceInfoTable_W">
/// The original vendor invoice table w record.
/// </param>
/// <returns>
/// true if the tax information has changed; otherwise, false.
/// </returns>
public boolean hasTaxChanged(VendInvoiceInfoTable_W _originalVendInvoiceInfoTable_W = this.orig())
{
    #ISOCountryRegionCodes
    boolean taxChanged = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL])
        && this.TaxPeriodPaymentCode_PL != _originalVendInvoiceInfoTable_W.TaxPeriodPaymentCode_PL;

    taxChanged = taxChanged
        || (this.hasTaxChangedForTaxIntegration(_originalVendInvoiceInfoTable_W));

    return taxChanged;
}

/// <summary>
/// Determines whether the tax information has changed for tax integration of tax calculation service.
/// </summary>
/// <param name = "_originalVendInvoiceInfoTable_W">
/// The original vendor invoice table w record.
/// </param>
/// <returns>
/// true if the tax information has changed; otherwise, false.
/// </returns>
private boolean hasTaxChangedForTaxIntegration(VendInvoiceInfoTable_W _originalVendInvoiceInfoTable_W)
{
    return (Tax::isTaxIntegrationEnabledforBusinessProcess(TaxIntegrationBusinessProcess::Purchase)
        && (TaxIntegrationUtils::isTaxIntegrationEnabledForTaxSolutionProvider()
            && this.VatDueDate_W != _originalVendInvoiceInfoTable_W.VatDueDate_W));
}

/// <summary>
/// Determines whether the given field is valid or not
/// </summary>
/// <param name="_fieldIdToCheck">
/// The field ID of the field to validate
/// </param>
/// <returns>
/// True if the field is valid, otherwise false
/// </returns>
public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;
    VendInvoiceInfoTable_W vendInvoiceInfoTable_W_orig;
    VendInvoiceInfoTable            vendInvoiceInfoTable = this.SysExtensionSerializerExtensionMap::getBaseTable();

    // <GBR>
    boolean createInventTrans(RecId _salesPurchOperationType_BR)
    {
        if (!_salesPurchOperationType_BR)
        {
            return true;
        }

        return SalesPurchOperationType_BR::find(_salesPurchOperationType_BR).CreateInventTrans;
    }
    // </GBR>
    ret = super(_fieldIdToCheck);

    vendInvoiceInfoTable.packVendInvoiceInfoTable_W(this);

    switch (_fieldIdToCheck)
    {
        // <GBR>
        case fieldNum(VendInvoiceInfoTable_W, SalesPurchOperationType_BR):
            vendInvoiceInfoTable_W_orig = this.orig();
            if (vendInvoiceInfoTable_W_orig.SalesPurchOperationType_BR && this.VendInvoiceInfoTable().existLine())
            {
                if (!createInventTrans(vendInvoiceInfoTable_W_orig.SalesPurchOperationType_BR)
                    && createInventTrans(vendInvoiceInfoTable_W_orig.SalesPurchOperationType_BR) != createInventTrans(this.SalesPurchOperationType_BR))
                {
                    ret = checkFailed("@GLS63949");
                }
            }
            break;
        case fieldNum(VendInvoiceInfoTable_W, AccessKey_BR):
            if (!vendInvoiceInfoTable.hasToIssueFiscalDocument_BR()
                && FiscalDocModel_BR::find(this.FiscalDocumentModel_BR).IsForNFeFederal)
            {
                if (!EFDocAccessKeyBase_BR::validate(this.AccessKey_BR))
                {
                    ret = checkFailed("@SYS4110579");
                }
            }
            break;
        // </GBR>

        case fieldNum(LedgerJournalTrans, InvoiceSeries_MX):
            if (strKeep(this.InvoiceSeries_MX, 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz') != this.InvoiceSeries_MX)
            {
                ret = checkFailed(strFmt("@Mexico:ELedgerAccounting_InvoiceSeriesValidation", this.InvoiceSeries_MX));
            }
            break;
    }

    return ret;
}

