public boolean validateWrite()
{
    boolean                 ret;
    VendInvoiceInfoTable    vendInvoiceInfoTable = this.SysExtensionSerializerExtensionMap::getBaseTable();

    ret = super();

    // <GBR>
    vendInvoiceInfoTable.packVendInvoiceInfoTable_W(this);

    if (!vendInvoiceInfoTable.IsTheFiscalDocumentIssuer_BR()
        && FiscalDocModel_BR::find(this.FiscalDocumentModel_BR).IsForNFeFederal
        && !this.AccessKey_BR)
    {
        ret = checkFailed("@SYS4110579");
    }

    if (VendInvoiceInfoTable_W::isAccessKeyAlreadyUsed_BR(this.AccessKey_BR, vendInvoiceInfoTable.TableRefId))
    {
        warning("@SYS4010097");
    }
    // </GBR>

    return ret;
}

/// <summary>
///    Determines whether the access key provided is being used by another vendor invoice. Two different vendor invoices should not have the same access key.
/// </summary>
/// <param name="_accessKey">
///    The access key to be searched.
/// </param>
/// <param name="_tableRefId">
///    The tableRefId of the vendor invoice.
/// </param>
/// <returns>
///    true if the specified access key is already used by some different vendor invoice; otherwise, false.
/// </returns>
public static boolean isAccessKeyAlreadyUsed_BR(EFDocAccessKey_BR _accessKey, TradeLineRefId _tableRefId)
{
    VendInvoiceInfoTable_W vendInvoiceInfoTable_W;
    VendInvoiceInfoTable   vendInvoiceInfoTable;

    if (_accessKey != '')
    {
        select firstonly RecId from vendInvoiceInfoTable_W
                exists join vendInvoiceInfoTable
                    where vendInvoiceInfoTable_W.VendInvoiceInfoTable == vendInvoiceInfoTable.RecId
                        && vendInvoiceInfoTable_W.AccessKey_BR == _accessKey
                        && vendInvoiceInfoTable.TableRefId != _tableRefId;
    }

    return vendInvoiceInfoTable_W != null;
}

