/// </summary>
/// <returns>
/// true if <C>VendInvoiceInfoTable_W</C> table extension is enabled; otherwise, false.
/// </returns>
public static boolean isExtensionEnabled()
{
    #ISOCountryRegionCodes

    return SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCZ, #isoEE, #isoHU, #isoLT, #isoLV, #isoPL, #isoBR, #isoIN, #isoJP, #isoRU, #isoMY, #isoUS, #isoGB])
        || VatDueDateFeature::isEnabled();
}
