/// <summary>
/// Initializes the record based on a <c>PurchTable</c> record.
/// </summary>
/// <param name="_purchTable">
/// A <c>PurchTable</c> record to initialize based on.
/// </param>
void  initFromPurchTable(PurchTable _purchTable)
{
    #ISOCountryRegionCodes
    PurchTable_RU       purchTable_RU;

    VendPackingSlipJour_Intrastat vendPackingSlipJour_Intrastat;

    if (SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(VendPackingSlipJour_Intrastat)))
    {
        vendPackingSlipJour_Intrastat = this.vendPackingSlipJour_Intrastat();
    }

    this.PurchaseType           = _purchTable.PurchaseType;
    this.PurchId                = _purchTable.PurchId;
    this.OrderAccount           = _purchTable.OrderAccount;
    this.InvoiceAccount         = _purchTable.InvoiceAccount;
    this.NumberSequenceGroup    = _purchTable.NumberSequenceGroup;

    this.DeliveryName           = _purchTable.DeliveryName;
    this.DeliveryPostalAddress  = _purchTable.DeliveryPostalAddress;
    this.CountryRegionId        = LogisticsPostalAddress::findRecId(this.DeliveryPostalAddress).CountryRegionId;

    this.ItemBuyerGroupId       = _purchTable.ItemBuyerGroupId;
    this.LanguageId             = _purchTable.LanguageId;
    this.DeliveryType           = _purchTable.DeliveryType;
    this.DlvTerm                = _purchTable.DlvTerm;
    this.DlvMode                = _purchTable.DlvMode;
    this.DefaultDimension       = _purchTable.DefaultDimension;
    this.ReturnItemNum          = _purchTable.ReturnItemNum;
    this.FreightSlipType        = _purchTable.FreightSlipType;

    if (IntrastatCountryRegionParameters::isInEUByIsoCode())
    {
        TransportationDocument::copy(_purchTable, this);
    }

    // M4/M7 implementation
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        purchTable_RU = _purchTable.purchTable_RU();

        this.CurrencyCode_W         = _purchTable.CurrencyCode;

        this.ConsigneeAccount_RU    = purchTable_RU.ConsigneeAccount_RU;
        this.ConsignorAccount_RU    = purchTable_RU.ConsignorAccount_RU;
    }

    if (IntrastatParameters::isLegalEntityInEEU())
    {
        this.CurrencyCode_W         = _purchTable.CurrencyCode;
    }

    this.InterCompanyCompanyId  = _purchTable.InterCompanyCompanyId;
    this.InterCompanySalesId    = _purchTable.InterCompanySalesId;

    this.ReqAttention           = _purchTable.ReqAttention;
    this.Requester              = _purchTable.Requester;

    if (FinTagConfiguration::isFinTagConfigurationSet(_purchTable.DataAreaId) && PurchaseOrderFinTagFeature::isEnabled())
    {
        this.FinTag = _purchTable.FinTag;
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
    {
        this.IntrastatFulfillmentDate_HU = _purchTable.IntrastatFulfillmentDate_HU;
    }

    if (SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(VendPackingSlipJour_Intrastat)))
    {
        vendPackingSlipJour_Intrastat.SpecialMovement_CZ = _purchTable.purchTable_Intrastat().SpecialMovement_CZ;
        this.packVendPackingSlipJour_Intrastat(vendPackingSlipJour_Intrastat);
    }
}

