/// <summary>
/// Indicates whether a packing slip is canceled.
/// </summary>
/// <returns>
/// true if the packing slip is canceled; otherwise false.
/// </returns>
public boolean isCanceled()
{
    return (select firstonly RecId from vendPackingSlipTrans
                where vendPackingSlipTrans.VendPackingSlipJour == this.RecId
                   && vendPackingSlipTrans.Qty != 0).RecId == 0;
}

/// <summary>
/// Indicates whether a packing slip has been fully invoiced.
/// </summary>
/// <returns>
/// true if the packing slip has been fully invoiced; otherwise, false.
/// </returns>
public boolean isFullyInvoiced()
{
    VendPackingSlipTrans vendPackingSlipTrans;

    select firstonly RecId from vendPackingSlipTrans
        where  vendPackingSlipTrans.VendPackingSlipJour == this.RecId
            && vendPackingSlipTrans.FullyMatched == NoYes::No;

    return vendPackingSlipTrans.RecId == 0;
}

/// <summary>
/// Indicates whether a packing slip has been invoice matched.
/// </summary>
/// <returns>
/// true if a packing slip has been invoice matched; otherwise, false.
/// </returns>
public boolean isInvoiceMatched()
{
    VendInvoicePackingSlipQuantityMatch vendInvoicePackingSlipQuantityMatch;
    VendPackingSlipTrans vendPackingSlipTrans;

    select firstonly RecId from vendInvoicePackingSlipQuantityMatch
    exists join vendPackingSlipTrans
        where  vendPackingSlipTrans.SourceDocumentLine == vendInvoicePackingSlipQuantityMatch.PackingSlipSourceDocumentLine
            && vendPackingSlipTrans.VendPackingSlipJour == this.RecId;

    return vendInvoicePackingSlipQuantityMatch.RecId != 0;
}

public boolean isProforma()
{
    return !this.PackingSlipId;
}

