static boolean exist(PurchId _purchId)
{
    return _purchId &&(select firstonly RecId from vendPackingSlipJour
            where vendPackingSlipJour.PurchId     == _purchId
           ).RecId != 0;
}

/// <summary>
/// Finds a <c>VendPackingSlipJour</c> record in the <c>SourceDocumentHeader</c> table.
/// </summary>
/// <param name="_sourceDocumentHeader">
/// The <c>SourceDocumentHeader</c> record ID.
/// </param>
/// <returns>
/// The <c>VendPackingSlipJour</c> record.
/// </returns>
public static VendPackingSlipJour findBySourceDocumentHeader(
    RefRecId    _sourceDocumentHeader)
{
    VendPackingSlipJour    vendPackingSlipJour;

    select firstonly vendPackingSlipJour
        where vendPackingSlipJour.SourceDocumentHeader == _sourceDocumentHeader;

    return vendPackingSlipJour;
}

static VendPackingSlipJour findFromPurchId(PurchId _purchId,  boolean _forUpdate = false)
{
    VendPackingSlipJour vendPackingSlipJour;

    vendPackingSlipJour.selectForUpdate(_forUpdate);

    select firstonly vendPackingSlipJour
        where vendPackingSlipJour.PurchId == _purchId;

    return vendPackingSlipJour;
}

static VendPackingSlipJour findRecId(RecId  _recId,
                                     boolean  _forUpdate = false)
{
    VendPackingSlipJour vendPackingSlipJour;

    vendPackingSlipJour.selectForUpdate(_forUpdate);

    select vendPackingSlipJour
        where vendPackingSlipJour.RecId == _recId;

    return vendPackingSlipJour;
}

/// <summary>
/// This method is used to create a lookup for the Packing slip field on the PurchParmTable.VendInvoieInfoTable form.
/// </summary>
/// <param name="_lookupCtrl">
/// The countrol for which the lookup is being created.
/// </param>
/// <param name="_query">
/// The Query to be applied while creating the lookup.
/// </param>
public static void lookupPackingSlipId_W(Object _lookupCtrl,
                                                Query  _query)
{
    SysTableLookup lookup = SysTableLookup::newParameters(tableNum(VendPackingSlipJour),
                                                          _lookupCtrl);

    lookup.addLookupfield(fieldNum(VendPackingSlipJour, PackingSlipId));
    lookup.addLookupfield(fieldNum(VendPackingSlipJour, PurchId));
    lookup.addLookupfield(fieldNum(VendPackingSlipJour, DeliveryDate));
    lookup.parmQuery(_query);
    lookup.performFormLookup();
}

/// <summary>
/// This method is used to create a Query that will be used to perform a lookup on Packing slip field on the PurchParmTable/VendInvoiceInfoTable form.
/// </summary>
/// <param name="_parmId">
/// ParmId of the unposted transaction.
/// </param>
/// <param name="_purchId">
/// PurchId of the unposted transaction.
/// </param>
/// <param name="_tradeLineRefId">
/// TableRefId of the unposted transaction.
/// </param>
/// <param name="_useSummaryRange">
/// Boolean value indicating whether to use summary range or not.
/// </param>
/// <returns>
/// A query that is used to filter the lookup.
/// </returns>
public static Query queryPackingSlipIdForUpdate_W(ParmId         _parmId,
                                                  PurchId        _purchId,
                                                  TradeLineRefId _tradeLineRefId,
                                                  boolean        _useSummaryRange = true)
{
    Query                query = new Query();
    QueryBuildDataSource qBd = query.addDataSource(tableNum(VendPackingSlipJour));
    PurchParmSubTable    purchParmSubTable;
    Range                range = _purchId;

    qBd = qBd.addDataSource(tableNum(VendPackingSlipPurchLink));
    qBd.relations(true);
    qBd.joinMode(JoinMode::ExistsJoin);

    if (_useSummaryRange)
    {
        while select OrigPurchId from purchParmSubTable
            where purchParmSubTable.TableRefId  == _tradeLineRefId &&
                  purchParmSubTable.ParmId      == _parmId         &&
                  purchParmSubTable.OrigPurchId != _purchId
        {
            range = queryRangeConcat(range, purchParmSubTable.OrigPurchId);
        }
    }

    qBd.addRange(fieldNum(VendPackingSlipPurchLink, OrigPurchId)).value(range);

    return query;
}

/// <summary>
/// This method is used to provide a query to be used to perform a lookup on Packing slip field on the PurchParmTable/VendInvoiceInfoTable form while posting a packing slip.
/// The lookup provides a list of all packing slips that are being updated with the current packing slip whose PackingSlipStatus_LT field is not set to Broken or Cancelled.
/// </summary>
/// <returns>
/// A query to be used for Packing slip lookup.
/// </returns>
public static Query queryPackingSlipStorno_LT()
{
    Query                query = new Query();
    QueryBuildDataSource qBd   = query.addDataSource(tableNum(VendPackingSlipJour));

    qBd.addRange(fieldNum(VendPackingSlipJour, PackingSlipNumberingCode_LT)).value(SysQuery::valueNotEmptyString());

    return query;
}

/// <summary>
///    Determines whether any unposted invoices exist for the <c>InventTrans</c> record.
/// </summary>
/// <param name="_inventTrans">
///    The <c>InventTrans</c> record that is used to find the invoice.
/// </param>
/// <returns>
///    true if an <c>InventTrans</c> record exists; otherwise, false.
/// </returns>
static boolean unpostedInvoicesExist(InventTrans _inventTrans)
{
    VendPackingSlipVersion  vendPackingSlipVersion;
    VendPackingSlipJour     vendPackingSlipJour;
    VendPackingSlipTrans    vendPackingSlipTrans;
    VendInvoiceInfoTable    parmTable;
    VendInvoiceInfoLine     parmLine;
    VendInvoiceInfoSubLine  parmSubLine;
    InventTransOrigin       inventTransOrigin = _inventTrans.inventTransOrigin();

    // See if any unposted invoice lines exist for this inventTrans that are connected to a packing slip
    while select RecId from parmLine
        where parmLine.InventTransId == inventTransOrigin.InventTransId
    join JournalRefTableId, JournalRefRecId from parmSubLine
        where parmSubLine.LineRefRecId    == parmLine.RecId
           && parmSubLine.JournalRefRecId != 0
    exists join parmTable
        where parmTable.ParmId     == parmLine.ParmId
           && parmTable.TableRefId == parmLine.TableRefId
           && (parmTable.ParmJobStatus == ParmJobStatus::Waiting ||
               parmTable.ParmJobStatus == ParmJobStatus::ContainErrors)
    {
        // Now see if they are connected to the packing slip that created this inventTrans
        select firstonly RecId from vendPackingSlipTrans
            where vendPackingSlipTrans.TableId == parmSubLine.JournalRefTableId
               && vendPackingSlipTrans.RecId   == parmSubLine.JournalRefRecId
        exists join vendPackingSlipJour
            where vendPackingSlipJour.RecId        == vendPackingSlipTrans.VendPackingSlipJour
               && vendPackingSlipJour.DeliveryDate == _inventTrans.DatePhysical
        exists join vendPackingSlipVersion
            where vendPackingSlipVersion.VendPackingSlipJour == vendPackingSlipJour.RecId
               && vendPackingSlipVersion.CostLedgerVoucher   == _inventTrans.VoucherPhysical;

        if (vendPackingSlipTrans.RecId != 0)
        {
            return true;
        }
    }

    return false;
}

/// <summary>
/// Indicates whether a pending vendor invoice exists.
/// </summary>
/// <returns>
/// true if the pending vendor invoice exists; otherwise false.
/// </returns>
public boolean existsPendingVendorInvoice()
{
    VendPackingSlipTrans vendPackingSlipTrans;
    VendInvoiceInfoSubLine vendInvoiceInfoSubLine;
    VendInvoiceInfoLine vendInvoiceInfoLine;
    VendInvoiceInfoTable vendInvoiceInfoTable;
    boolean ret;

    select firstOnly RecId from vendPackingSlipTrans
        where vendPackingSlipTrans.VendPackingSlipJour == this.RecId
    exists join vendInvoiceInfoSubLine
        where vendInvoiceInfoSubLine.JournalRefTableId == vendPackingSlipTrans.TableId
        && vendInvoiceInfoSubLine.JournalRefRecId == vendPackingSlipTrans.RecId
    join vendInvoiceInfoLine
        where vendInvoiceInfoLine.RecId == vendInvoiceInfoSubLine.LineRefRecId
    join vendInvoiceInfoTable
        where vendInvoiceInfoTable.ParmId == vendInvoiceInfoLine.ParmId
            && vendInvoiceInfoTable.TableRefId == vendInvoiceInfoLine.TableRefId
            && vendInvoiceInfoTable.ParmJobStatus != ParmJobStatus::Executed;

    if (vendPackingSlipTrans)
    {
        ret = true;
    }

    return ret;
}

/// <summary>
/// Finds a quality order that is related to the product reciept journal and that is still open.
/// </summary>
/// <returns>
/// An <c>InventQualityOrderTable</c> record; otherwise, an empty record.
/// </returns>
public InventQualityOrderTable openQualityOrder()
{
    InventQualityOrderTable inventQualityOrderTable;
    PurchLine               purchLine;
    VendPackingSlipTrans    vendPackingSlipTrans;

    select firstOnly *
        from inventQualityOrderTable
            where inventQualityOrderTable.ReferenceType == InventTestReferenceType::Purch
               && inventQualityOrderTable.OrderStatus   == InventTestOrderStatus::Open
        exists join purchLine
            where purchLine.PurchId       == inventQualityOrderTable.InventRefId
               && purchLine.InventTransId == inventQualityOrderTable.InventRefTransId
               && !purchLine.IsDeleted
        exists join vendPackingSlipTrans
            where vendPackingSlipTrans.InventTransId       == purchLine.InventTransId
               && vendPackingSlipTrans.VendPackingSlipJour == this.RecId;

    return inventQualityOrderTable;
}

