/// <summary>
/// Indicates whether a packing slip has related item requirements.
/// </summary>
/// <returns>
/// true if the packing slip has related item requirements; otherwise, false.
/// </returns>
public boolean hasItemReq()
{
    PurchTable purchTable;
    SalesTable salesTable;
    SalesLine salesLine;
    PurchLine purchLine;

    select firstonly RecId from salesTable 
        where salesTable.ProjId != '' 
            && salesTable.SalesType == SalesType::ItemReq
        exists join purchTable
            where purchTable.PurchId == this.PurchId
                && purchTable.ProjId == salesTable.ProjId
        exists join purchLine
            where purchLine.PurchId == purchTable.PurchId
        exists join salesLine 
            where salesLine.SalesId == salesTable.SalesId
                && salesLine.InventRefTransId == purchLine.InventTransId
                && salesLine.InventTransId == purchLine.InventRefTransId;

    return salesTable.RecId;
}
