/// <summary>
/// Gets or sets the status of the automatic invoice header matching setting for the current company.
/// </summary>
/// <param name="_set">
/// Specifies whether the field is updated; optional.
/// </param>
/// <param name="_automaticHeaderMatching">
/// Specifies the current status of the automatic invoice header matching; optional.
/// </param>
/// <returns>
/// The <c>NoYes::Yes</c> enumeration value if the <c>UnknownNoYes</c> enumeration type of automatic invoice header matching is
/// enabled; otherwise, the <c>NoYes::No</c> enumeration value.
/// </returns>
/// <remarks>
/// This method should always be called instead of directly accessing the field with the same name. If
/// the field has not been set, the status is <c>UnknownNoYes::None</c>. Then the status will be updated to <c>UnknownNoYes::No</c>.
/// </remarks>
public edit NoYes editAutomaticHeaderMatching(boolean _set = false, NoYes _automaticHeaderMatching = NoYes::No)
{
    NoYes automaticHeaderMatching;

    if (_set)
    {
        if (_automaticHeaderMatching == NoYes::Yes)
        {
            this.AutomaticHeaderMatching = UnknownNoYes::Yes;
        }
        else
        {
            this.AutomaticHeaderMatching = UnknownNoYes::No;
        }
    }
    else
    {
        if (!this.editUseInvoiceMatching())
        {
            automaticHeaderMatching = NoYes::No;
        }
        else
        {
            if (this.AutomaticHeaderMatching == UnknownNoYes::Unknown)
            {
                //Set the field on the table because if a value hasn't been previously set, the feature should be turned off for performance reasons.
                this.AutomaticHeaderMatching = UnknownNoYes::No;
                automaticHeaderMatching = NoYes::No;
            }
            else if (this.AutomaticHeaderMatching == UnknownNoYes::Yes)
            {
                automaticHeaderMatching = NoYes::Yes;
            }
            else
            {
                automaticHeaderMatching = NoYes::No;
            }
        }
    }

    return automaticHeaderMatching;
}

/// <summary>
/// Gets or sets the status of the <c>UseInvoiceMatching</c> enumeration type of invoice matching for the current
/// company.
/// </summary>
/// <param name="_set">
/// Specifies whether the field is updated; optional.
/// </param>
/// <param name="_useInvoiceMatching">
/// Specifies the current status of the invoice matching; optional.
/// </param>
/// <returns>
/// The <c>NoYes::Yes</c> enumeration value if the <c>UseInvoiceMatching</c> enumeration type of invoice matching is
/// enabled; otherwise, the <c>NoYes::No</c> enumeration value.
/// </returns>
/// <remarks>
/// This method should always be called instead of directly accessing the field with the same name.If
/// the field has not been set, the status is <c>UnknownNoYes::None</c>. Then the status will be
/// updated to <c>UnknownNoYes::Yes</c>
/// </remarks>
public edit NoYes editUseInvoiceMatching(boolean _set = false, NoYes _useInvoiceMatching = NoYes::Yes)
{
    NoYes useInvoiceMatching;

    if (_set)
    {
        Debug::assert(isConfigurationkeyEnabled(configurationKeyNum(PurchInvoiceMatching)));

        // Set the field as requested
        if (_useInvoiceMatching == NoYes::Yes)
        {
            this.UseInvoiceMatching = UnknownNoYes::Yes;
        }
        else
        {
            this.UseInvoiceMatching = UnknownNoYes::No;
        }
    }
    else
    {
        if (this.UseInvoiceMatching == UnknownNoYes::Unknown)
        {
            if (isConfigurationkeyEnabled(configurationKeyNum(PurchInvoiceMatching)))
            {
                // The default setting is enabled, change the None to Yes and return Yes
                this.UseInvoiceMatching = UnknownNoYes::Yes;
                useInvoiceMatching = NoYes::Yes;
            }
            else
            {
                // Always return No if the configuration key is not enabled
                useInvoiceMatching = NoYes::No;
            }
        }
        else if (this.UseInvoiceMatching == UnknownNoYes::Yes)
        {
            useInvoiceMatching = NoYes::Yes;
        }
        else
        {
            useInvoiceMatching = NoYes::No;
        }
    }

    return useInvoiceMatching;
}

