/// <summary>
/// Validates the value of the <c>PurchAgreementAccountStructure_PSN</c> field in the
/// <c>VendParameters</c> table.
/// </summary>
/// <returns>
/// true if the validation is successful; otherwise, false .
/// </returns>
public boolean isPurchAgreementAccountStructureValid()
{
    DimensionHierarchy dimensionHierarchy;
    boolean isValid;

    if (PublicSectorUtils::isFrenchRegulatoryEnabled())
    {
        select firstonly RecId from dimensionHierarchy
            where dimensionHierarchy.RecId == this.PurchAgreementAccountStructure_PSN
                && dimensionHierarchy.StructureType == DimensionHierarchyType::AccountStructure
                && dimensionHierarchy.IsDraft == NoYes::No
                && dimensionHierarchy.IsSystemGenerated == NoYes::No
                && dimensionHierarchy.DraftName == ''
                && dimensionHierarchy.DraftDescription == '';

        isValid = dimensionHierarchy.RecId != 0;
    }

    return isValid;
}

/// <summary>
/// Validates the value of the <c>PurchAgreementFinancialDimension_PSN</c> field in the
/// <c>VendParameters</c> table.
/// </summary>
/// <returns>
/// true if the validation is successful; otherwise, false .
/// </returns>
public boolean isPurchAgreementFinancialDimensionValid()
{
    DimensionHierarchy dimensionHierarchy;
    DimensionAttribute dimensionAttribute;
    DimensionHierarchyLevel dimensionHierarchyLevel;
    DimensionRule dimensionRule;
    DimensionRuleAppliedHierarchy dimensionRuleAppliedHierarchy;
    boolean isValid = true;

    if (PublicSectorUtils::isFrenchRegulatoryEnabled())
    {
        select firstonly RecId from dimensionAttribute
            where dimensionAttribute.RecId == this.PurchAgreementFinancialDimension_PSN
                && dimensionAttribute.RecId != DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount)
            exists join dimensionHierarchyLevel
                where dimensionHierarchyLevel.DimensionAttribute == dimensionAttribute.RecId
            exists join dimensionHierarchy
                where dimensionHierarchy.RecId == dimensionHierarchyLevel.DimensionHierarchy
                    && dimensionHierarchy.RecId == this.PurchAgreementAccountStructure_PSN;

        isValid = dimensionAttribute.RecId != 0 && this.PurchAgreementAccountStructure_PSN != 0;

        if (!isValid)
        {
            select firstonly RecId from dimensionAttribute
                where dimensionAttribute.RecId == this.PurchAgreementFinancialDimension_PSN
                    && dimensionAttribute.RecId != DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount)
                exists join dimensionHierarchyLevel
                    where dimensionHierarchyLevel.DimensionAttribute == dimensionAttribute.RecId
                exists join dimensionRuleAppliedHierarchy
                    where dimensionRuleAppliedHierarchy.DimensionHierarchy == dimensionHierarchyLevel.DimensionHierarchy
                exists join dimensionRule
                    where dimensionRule.RecId == dimensionRuleAppliedHierarchy.DimensionRule
                exists join dimensionHierarchy
                    where dimensionHierarchy.RecId == dimensionRule.AccountStructure
                        && dimensionHierarchy.RecId == this.PurchAgreementAccountStructure_PSN;

            isValid = dimensionAttribute.RecId != 0 && this.PurchAgreementAccountStructure_PSN != 0;
        }
    }

    return isValid;
}

/// <summary>
///    Performs a specific task based on the field modified.
/// </summary>
/// <param name="_fieldId">
///    The modified field ID.
/// </param>
public void modifiedField(fieldId _fieldId)
{
    VendTable vendTable;

    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(VendParameters, MaxMSTDiff):
            if (this.MaxMSTOverUnder < this.MaxMSTDiff)
            {
                this.MaxMSTOverUnder = this.MaxMSTDiff;
            }
        case fieldNum(VendParameters, w9Validation):
            if (this.w9Validation == NoYes::Yes)
            {
                update_recordset vendTable
                    setting w9Included = NoYes::Yes;
            }
            break;
        // <GTH>
        case fieldNum(VendParameters, ValidateTaxInvoiceDate_TH):
            if (this.ValidateTaxInvoiceDate_TH == NoYes::No)
            {
                this.AllowedTaxInvoicePostingPeriod_TH = 0;
            }
            break;
        // </GTH>

        // When "Automatically submit imported invoices to workflow" is not selected,
        // then clear all the "Vendor invoice workflow" fast tab fields values.
        case fieldNum(VendParameters, AutomationWorkflowSubmission):
            if (this.AutomationWorkflowSubmission == NoYes::No)
            {
                this.AutomationTotalsReqForWorkflowSubmission = NoYes::No;
                this.AutomationMatchingPriorToWorkflow = NoYes::No;
            }
            break;

        // When "Match product receipts to invoice lines" is selected,
        // then select "Automatically match product receipts to invoice lines" and
        // "Validate only when the matched product receipt quantity is equal to the invoice quantity" options
        // in "Match product receipts automatically" fast tab.
        case fieldNum(VendParameters, AutomationMatchingPriorToWorkflow):
            if (this.AutomationMatchingPriorToWorkflow == NoYes::Yes)
            {
                this.AutomationProductReceiptMatching = NoYes::Yes;
            }
            break;
    }
}

void update()
{
    PurchMatchingPolicyManager::rolldownCompanyMatchingPolicy(this);

    super();

    flush VendParameters;

    if (this.EnableElectronicPaymentNumber == NoYes::Yes)
    {
        NumberSequenceDatatype numberSequenceDatatype = NumberSequenceDatatype::findByDatatypeId(extendedTypeNum(VendorElectronicPaymentReference));

        if (!numberSequenceDatatype)
        {
            NumberSeqModuleVendor numberSeqModuleVendor = new NumberSeqModuleVendor();
            numberSeqModuleVendor.load();
        }
    }
}

