/// <summary>
/// Determines whether the specified field is valid.
/// </summary>
/// <param name="_fieldId">
/// The field ID of the field to validate.
/// </param>
/// <returns>
/// true if the field is valid; otherwise, false.
/// </returns>
boolean validateField(FieldId _fieldId)
{
    // <GEERU>
    #ISOCountryRegionCodes
    boolean countryRegion_RU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    // </GEERU>

    RecId originalAccountStructure;
    RecId originalFinancialDimension;

    boolean ok;
    LedgerJournalName ledgerJournalName;

    ok = super(_fieldId);

    if (ok)
    {
        switch (_fieldId)
        {
            case fieldNum(VendParameters, ClearingLedgerDimension):
                ok = MainAccount::checkTransactionAccount(this.ClearingLedgerDimension);
                break;
            case fieldNum(VendParameters, VendInvoiceInternalDefaultGroup):
                if (this.VendInvoiceDefaultGroupType == VendInvoiceDefaultGroupType::Custom && this.VendInvoiceInternalDefaultGroup == '' && this.VendInvoiceGroupingEnable == NoYes::Yes)
                {
                    ok = checkFailed(strFmt("@SYS84753", "@SYS130646"));
                }
                break;
            case fieldNum(VendParameters, PurchAgreementAccountStructure_PSN):
                if (PublicSectorUtils::isFrenchRegulatoryEnabled())
                {
                    originalAccountStructure = this.orig().PurchAgreementAccountStructure_PSN;
                    if (this.PurchAgreementAccountStructure_PSN != 0 && this.PurchAgreementAccountStructure_PSN != originalAccountStructure && !this.isPurchAgreementAccountStructureValid())
                    {
                        this.PurchAgreementAccountStructure_PSN = originalAccountStructure;
                        ok = false;
                    }
                    else
                    {
                        this.PurchAgreementFinancialDimension_PSN = 0;
                    }
                }
                break;

            case fieldNum(VendParameters, PurchAgreementFinancialDimension_PSN):
                if (PublicSectorUtils::isFrenchRegulatoryEnabled())
                {
                    originalFinancialDimension = this.orig().PurchAgreementFinancialDimension_PSN;
                    if (this.PurchAgreementFinancialDimension_PSN != 0 && this.PurchAgreementFinancialDimension_PSN != originalFinancialDimension && !this.isPurchAgreementFinancialDimensionValid())
                    {
                        this.PurchAgreementFinancialDimension_PSN = originalFinancialDimension;
                        ok = checkFailed("@SPS2450");
                    }
                }
                break;

            // <GEERU>
            case fieldNum(VendParameters, VatProcessGroupBy_RU):
                ok = VendParameters::canVatProcessGroupByBeModified_RU(this.VatProcessGroupBy_RU);
                break;

            case fieldNum(VendParameters, UseDocumentDate):
                if (countryRegion_RU && this.UseDocumentDate && !this.UsePurchConsumpAccount_RU)
                {
                    this.UseDocumentDate = false;
                    ok = checkFailed("@SYS4082462");
                }
                break;

            case fieldNum(VendParameters, UsePurchConsumpAccount_RU):
                if (this.UseDocumentDate && !this.UsePurchConsumpAccount_RU)
                {
                    this.UsePurchConsumpAccount_RU = false;
                    ok = checkFailed("@SYS4082463");
                }
                break;
            // </GEERU>
            case fieldNum(VendParameters, mcrBrokerDifferentialJournal):
                ledgerJournalName = LedgerJournalName::find(this.mcrBrokerDifferentialJournal);
                if (ledgerJournalName.JournalType != LedgerJournalType::Daily)
                {
                    ok = checkFailed(strFmt("@SYS22977", enum2str(LedgerJournalType::Daily)));
                }
                break;

            case fieldNum(VendParameters, mcrBrokerExpenseJournal):
                ledgerJournalName = LedgerJournalName::find(this.mcrBrokerExpenseJournal);
                if (ledgerJournalName.JournalType != LedgerJournalType::Daily)
                {
                    ok = checkFailed(strFmt("@SYS22977", enum2str(LedgerJournalType::Daily)));
                }
                break;
            case fieldNum(VendParameters, PostChargeAccount):
                if (this.PostChargeAccount == NoYes::Yes
                    && this.PostPackingSlip == NoYes::No)
                {
                    warning("@SCM:PostToChargeParameterWarning");
                }
                break;

            case fieldNum(VendParameters, AutomationProductReceiptMatching):
                // When "Match product receipts to invoice lines" is selected but
                // "Automatically match product receipt to invoice lines" is not selected,
                // then select the "Automatically match product receipt to invoice lines" and throw a message.
                if (this.AutomationProductReceiptMatching == NoYes::No
                    && this.AutomationMatchingPriorToWorkflow == NoYes::Yes)
                {
                    ok = checkFailed("@AccountsPayable:AutomationProductReceiptMatchingProcessInfo");
                }
                break;
            case fieldNum(VendParameters, AutomationProductReceiptMatchingMaxAttempts):
                // When "Maximum allowed attempts" is set to zero and
                // "Automatically match product receipt to invoice lines" is selected, then throw a message.
                if (this.AutomationProductReceiptMatchingMaxAttempts == 0
                    && this.AutomationProductReceiptMatching == NoYes::Yes)
                {
                    ok = checkFailed("@AccountsPayable:AutomationMatchingMaxAttemptsZeroValueWarning");
                }
                break;
            case fieldNum(VendParameters, AutomationWorkflowSubmission):
                // Info message when user selects automatic workflow submission but there are no active workflow setup for the company in setup page for vendor invoice automation feature.
                if (this.AutomationWorkflowSubmission == NoYes::Yes && !VendParameters::validateActiveVendInvoiceWorkflowPerCompany())
                {
                    ok = checkFailed("@AccountsPayable:AutomationWorkflowSubmitMissingActiveWorkflowSetup");
                }
                break;
        }
    }

    return ok;
}

