public boolean validateWrite()
{
    boolean ret = super();

    if (!this.VendInvoiceGroupingEnable && !VendParameters::validateVendInvoiceGroupsCanBeDisabled())
    {
        ret = checkFailed("@SYS130660");
    }

    if (this.AllowedTaxInvoicePostingPeriod_TH < 0)
    {
        ret = checkFailed(strFmt("@WAX4605", fieldPName(VendParameters, AllowedTaxInvoicePostingPeriod_TH)));
    }

    if (this.SettlementWriteOffEnabled && !this.SettlementWriteOffJournalName)
    {
        ret = checkFailed(strFmt("@SYS26332", "@SYS97804"));
    }

    if (FeatureStateProvider::isFeatureEnabled(CustVendExchRateTypeEnhancementFeature::instance())
        && this.ExchAdjRateSource == CustVendExchAdjRateSource::Specific
        && (this.AccountingCurrencyExchangeRateType == 0 || this.ReportingCurrencyExchangeRateType == 0))
    {
        ret = checkFailed("@CashManagement:CustVendExchRateMissingExchangeRateType");
    }

    return ret;
}

public static boolean canVatProcessGroupByBeModified_RU(NoYes _groupBy)
{
    boolean ret = true;
    PurchBookVATProcessLogTransOper_RU processLogTransOper;
    PurchBookVATProcessLogTrans_RU processLogTrans;
    FactureJour_RU factureJour;
    VendSettlement vendSettlement;
    VendTrans vendTrans;
    TransDate lastClosingDate = PurchBookTable_RU::getLastClosedBook().ClosingDate;

    if (_groupBy == NoYes::Yes)
    {
        select firstonly CustVendInvoiceAccount, FactureExternalId from factureJour
            where factureJour.Module == FactureModule_RU::Vend
        exists join processLogTrans
            where processLogTrans.TransType == SalesPurchBookTransType_RU::Invoice &&
                  processLogTrans.FactureId == factureJour.FactureId
        exists join processLogTransOper
            where processLogTransOper.RefRecId == processLogTrans.RecId &&
                  processLogTransOper.BlockedAuto == NoYes::No &&
                  processLogTransOper.TransDate > lastClosingDate &&
                  processLogTransOper.CanceledRefRecId == 0;

        if (factureJour.RecId)
        {
            ret = checkFailed(strFmt("@GLS110618", factureJour.CustVendInvoiceAccount,factureJour.FactureExternalId));
        }

        if (ret)
        {
            select firstonly CustVendInvoiceAccount, FactureExternalId from factureJour
                where factureJour.Module == FactureModule_RU::Vend
            exists join processLogTrans
                where processLogTrans.TransType == SalesPurchBookTransType_RU::Invoice &&
                      processLogTrans.FactureId == factureJour.FactureId
            exists join vendSettlement
                where vendSettlement.CanBeReversed == NoYes::Yes &&
                      vendSettlement.RecId == processLogTrans.SettlementRecId
            exists join processLogTransOper
                where processLogTransOper.Blocked == NoYes::Yes &&
                      processLogTransOper.BlockedAuto == NoYes::No &&
                      processLogTransOper.RefRecId == processLogTrans.RecId &&
                      processLogTransOper.CanceledRefRecId == 0;

            if (factureJour.RecId)
            {
                ret = checkFailed(strFmt("@GLS110617", factureJour.CustVendInvoiceAccount, factureJour.FactureExternalId));
            }
        }

        if (ret)
        {
            select firstonly AccountNum, Invoice from vendTrans
                where ! vendTrans.Closed && vendTrans.Invoice
            exists join vendSettlement
               where vendSettlement.TransRecId == vendTrans.RecId &&
                     vendSettlement.CanBeReversed == NoYes::Yes;

            if (vendTrans.RecId)
            {
                ret = checkFailed(strFmt("@GLS110619", vendTrans.AccountNum, vendTrans.Invoice));
            }
        }

        if (ret)
        {
            select firstonly AccountNum, Invoice from vendTrans
                where vendTrans.Closed && vendTrans.Invoice
            exists join vendSettlement
               where vendSettlement.TransRecId == vendTrans.RecId &&
                     vendSettlement.TransDate > lastClosingDate &&
                     vendSettlement.CanBeReversed == NoYes::Yes;

            if (vendTrans.RecId)
            {
                ret = checkFailed(strFmt("@GLS110620", vendTrans.AccountNum, vendTrans.Invoice));
            }
        }
    }
    else
    {
        if (RDeferralsParameters::find().vatRefundingMethod == RDeferralsVATRefundingMethod::Proportional || RDeferralsGroup::existVatRefundingMethodProportial() || RDeferralsTable::existVatRefundingMethodProportial())
        {
            ret = checkFailed("@GLS114361");
        }
    }

    return ret;
}

